import React, { useEffect, useRef, Suspense } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';

// Core components (loaded synchronously for critical path)
import { AuthProvider } from './contexts/AuthContext';
import ProtectedRoute from './components/ProtectedRoute';
import { PWAInstall } from './components/PWAInstall';
import ErrorBoundary from './components/ui/ErrorBoundary';
import { suppressThirdPartyErrors } from './components/ui/ThirdPartyErrorBoundary';

// Loading components
import { PageLoadingSpinner } from './components/ui/PageLoadingSpinner';

// Professional services
import { analyticsService } from './services/analyticsService';
import { clarityService } from './services/clarityService';
import { tallyService } from './services/tallyService';
import { config, isDevelopment, validateEnvironment } from './config/environment';
import { logger } from './utils/logger';
import { performanceService } from './services/performance';

// =============================================================================
// LAZY LOADED PAGES - CODE SPLITTING
// =============================================================================

// Lazy load all pages for optimal bundle splitting
const HomePage = React.lazy(() => 
  performanceService.measureFunction('load_HomePage', () => 
    import('./pages/HomePage').then(module => {
      logger.debug('HomePage lazy loaded', {}, 'CODE_SPLITTING');
      return module;
    })
  )
);

const GeneratorPage = React.lazy(() => 
  performanceService.measureFunction('load_GeneratorPage', () => 
    import('./pages/GeneratorPage').then(module => {
      logger.debug('GeneratorPage lazy loaded', {}, 'CODE_SPLITTING');
      return module;
    })
  )
);

const LoginPage = React.lazy(() => 
  performanceService.measureFunction('load_LoginPage', () => 
    import('./pages/LoginPage').then(module => {
      logger.debug('LoginPage lazy loaded', {}, 'CODE_SPLITTING');
      return module;
    })
  )
);

const SignupPage = React.lazy(() => 
  performanceService.measureFunction('load_SignupPage', () => 
    import('./pages/SignupPage').then(module => {
      logger.debug('SignupPage lazy loaded', {}, 'CODE_SPLITTING');
      return module;
    })
  )
);

const SimpleUserDashboard = React.lazy(() => 
  performanceService.measureFunction('load_SimpleUserDashboard', () => 
    import('./pages/SimpleUserDashboard').then(module => {
      logger.debug('SimpleUserDashboard lazy loaded', {}, 'CODE_SPLITTING');
      return module;
    })
  )
);

// =============================================================================
// PRELOADING STRATEGY
// =============================================================================

const preloadPages = () => {
  // Preload critical pages after initial load
  const preloadPromises = [
    import('./pages/GeneratorPage'),
    import('./pages/LoginPage'),
  ];

  // Preload user dashboard if authenticated
  if (localStorage.getItem('firebase:auth:user')) {
    preloadPromises.push(import('./pages/SimpleUserDashboard'));
  }

  Promise.all(preloadPromises).then(() => {
    logger.debug('Critical pages preloaded', {}, 'CODE_SPLITTING');
  }).catch(error => {
    logger.warn('Page preloading failed', { error }, 'CODE_SPLITTING');
  });
};

// =============================================================================
// MAIN APP COMPONENT
// =============================================================================

import './App.css';

const App: React.FC = () => {
  const initialized = useRef(false);

  useEffect(() => {
    // Prevent double initialization in React Strict Mode
    if (initialized.current) {
      logger.debug('App already initialized, skipping...', {}, 'APP');
      return;
    }

    initialized.current = true;

    // üõ°Ô∏è THIRD-PARTY ERROR SUPPRESSION
    // Initialize global error suppression for scripts like Microsoft Clarity
    const cleanupErrorSuppressor = suppressThirdPartyErrors();
    logger.debug('Third-party error suppression activated', {
      patterns: ['clarity.ms', 'Cannot read properties of undefined', 's05cslzjy5'],
      status: 'active'
    }, 'APP');

    // Record app initialization performance
    const initStartTime = performance.now();

    // Validate environment first
    const validation = validateEnvironment();
    if (!validation.valid) {
      logger.warn('Environment validation warnings detected', {
        errors: validation.errors,
        environment: config.environment
      }, 'APP');
    }

    logger.info('App initialization started', {
      environment: config.environment,
      version: config.version,
      debugMode: config.debugMode,
      timestamp: new Date().toISOString()
    }, 'APP');

    const initializeServices = async () => {
      try {
        // Initialize core services in parallel
        const servicePromises = [
          analyticsService.initialize().catch(err => ({ service: 'analytics', error: err })),
          clarityService.initialize().catch(err => ({ service: 'clarity', error: err })),
          tallyService.initialize().catch(err => ({ service: 'tally', error: err })),
        ];

        const results = await Promise.allSettled(servicePromises);

        // Process results
        const serviceStatus: Record<string, boolean> = {};
        const serviceErrors: string[] = [];

        results.forEach((result, index) => {
          const serviceName = ['analytics', 'clarity', 'tally'][index];

          if (result.status === 'fulfilled') {
            const value = result.value;
            if (value && typeof value === 'object' && 'error' in value) {
              serviceStatus[serviceName] = false;
              serviceErrors.push(`${serviceName}: ${value.error}`);
            } else {
              serviceStatus[serviceName] = true;
            }
          } else {
            serviceStatus[serviceName] = false;
            serviceErrors.push(`${serviceName}: ${result.reason}`);
          }
        });

        // Record initialization performance
        const initDuration = performance.now() - initStartTime;
        performanceService.recordMetric('app_initialization', initDuration, 'ms', 'loading', {
          servicesInitialized: Object.keys(serviceStatus).length,
          successfulServices: Object.values(serviceStatus).filter(Boolean).length,
        });

        // Log initialization results
        logger.info('Services initialization completed', {
          status: serviceStatus,
          successCount: Object.values(serviceStatus).filter(Boolean).length,
          totalCount: Object.keys(serviceStatus).length,
          errors: serviceErrors.length > 0 ? serviceErrors : undefined,
          duration: `${initDuration.toFixed(2)}ms`
        }, 'APP');

        // Preload pages after successful initialization
        setTimeout(preloadPages, 1000);

        // Expose debug services ONLY in development
        if (isDevelopment()) {
          logger.security('Exposing debug services for development', {
            environment: config.environment,
            services: Object.keys(serviceStatus)
          }, 'APP');

          (window as any).debugServices = {
            analytics: analyticsService,
            clarity: clarityService,
            tally: tallyService,
            performance: performanceService,
            config,

            // Utility functions
            getStatus: () => serviceStatus,
            getConfig: () => ({
              environment: config.environment,
              version: config.version,
              debugMode: config.debugMode
            }),
            validateEnv: () => validateEnvironment(),

            // Code splitting utilities
            preloadAllPages: () => {
              const allPages = [
                import('./pages/HomePage'),
                import('./pages/GeneratorPage'),
                import('./pages/LoginPage'),
                import('./pages/SignupPage'),
                import('./pages/SimpleUserDashboard'),
              ];
              return Promise.all(allPages);
            },

            // Testing utilities
            testServices: async () => {
              logger.info('Testing all services...', {}, 'DEBUG');
              const tests = await Promise.allSettled([
                analyticsService.trackEvent?.('debug_test', { source: 'debug_services' }),
                clarityService.trackEvent?.('debug_test'),
                tallyService.trackEvent?.('debug_test')
              ]);

              const testResults = tests.map((test, i) => ({
                service: ['analytics', 'clarity', 'tally'][i],
                success: test.status === 'fulfilled'
              }));

              logger.info('Service tests completed', { results: testResults }, 'DEBUG');
              return testResults;
            }
          };

          logger.debug('Debug services exposed globally', {
            services: Object.keys(serviceStatus),
            utilities: ['getStatus()', 'getConfig()', 'validateEnv()', 'testServices()', 'preloadAllPages()']
          }, 'APP');
        } else {
          logger.info('Production mode: Debug services not exposed', {
            environment: config.environment
          }, 'APP');
        }

      } catch (error) {
        logger.error('Critical error during service initialization', { 
          error: error instanceof Error ? error.message : 'Unknown error',
          stack: error instanceof Error ? error.stack : undefined,
          environment: config.environment
        }, 'APP');

        // In production, we might want to send this to external monitoring
        if (!isDevelopment()) {
          // This would integrate with external error tracking
          console.error('CRITICAL: Service initialization failed in production', error);
        }
      }
    };

    // Run initialization
    initializeServices();

    // Cleanup function
    return () => {
      logger.debug('App cleanup initiated', {}, 'APP');
      initialized.current = false;

      // üõ°Ô∏è Cleanup error suppression
      cleanupErrorSuppressor();
      logger.debug('Third-party error suppression cleaned up', {}, 'APP');

      // üõ°Ô∏è Cleanup error suppression

      // üõ°Ô∏è Cleanup error suppression

      // Clean up debug services in development
      if (isDevelopment() && (window as any).debugServices) {
        delete (window as any).debugServices;
        logger.debug('Debug services cleaned up', {}, 'APP');
      }
    };
  }, []); // Empty dependency array for one-time initialization
  return (
    <ErrorBoundary>
      <Router future={{ v7_startTransition: true, v7_relativeSplatPath: true }}>
        <AuthProvider>
          <div className="min-h-screen bg-background text-foreground">
            <ErrorBoundary isolateErrors>
              <Suspense fallback={<PageLoadingSpinner message="Carregando aplica√ß√£o..." />}>
                <Routes>
                  <Route 
                    path="/" 
                    element={<HomePage />} 
                  />
                  <Route 
                    path="/generator" 
                    element={
                      <ProtectedRoute>
                        <GeneratorPage />
                      </ProtectedRoute>
                    } 
                  />
                  <Route 
                    path="/login" 
                    element={<LoginPage />} 
                  />
                  <Route 
                    path="/signup" 
                    element={<SignupPage />} 
                  />
                  <Route 
                    path="/dashboard" 
                    element={
                      <ProtectedRoute>
                        <SimpleUserDashboard />
                      </ProtectedRoute>
                    } 
                  />
                </Routes>
              </Suspense>
            </ErrorBoundary>

            <PWAInstall variant="banner" />

            <Toaster 
              position="top-right"
              toastOptions={{
                className: 'dark:bg-card dark:text-card-foreground',
                duration: 4000,
              }}
            />
          </div>
        </AuthProvider>
      </Router>
    </ErrorBoundary>
  );
};

// Wrapper for additional setup
const AppWrapper: React.FC = () => {
  return <App />;
};

export default AppWrapper;

import React from 'react';
import { render, screen, waitFor } from '@testing-library/react';
import { renderHook } from '@testing-library/react';
import { describe, it, expect, jest, beforeEach, afterEach } from '@testing-library/jest-dom';
import { AuthProvider, useAuth } from './AuthContext';
import { onAuthStateChanged } from 'firebase/auth';
import type { User } from 'firebase/auth';

// jest.MockedFunction do Firebase Auth
jest.mock('firebase/auth', () => ({
  onAuthStateChanged: jest.fn(),
}));

// jest.MockedFunction do firebaseConfig
jest.mock('../firebaseConfig', () => ({
  auth: {},
}));

const mockOnAuthStateChanged = onAuthStateChanged as jest.MockedFunction;

// jest.MockedFunction user do Firebase
const mockUser: Partial<User> = {
  uid: 'test-uid-123',
  email: 'test@example.com',
  displayName: 'Test User',
  emailVerified: true,
};

describe('AuthContext', () => {
  let unsubscribejest.MockedFunction: ReturnType<typeof jest.fn>;

  beforeEach(() => {
    jest.clearAlljest.MockedFunctions();
    unsubscribejest.MockedFunction = jest.fn();
    mockOnAuthStateChanged.mockReturnValue(unsubscribejest.MockedFunction);
  });

  afterEach(() => {
    jest.restoreAlljest.MockedFunctions();
  });

  describe('AuthProvider', () => {
    it('inicializa com loading true', () => {
      mockOnAuthStateChanged.mockImplementation((auth, callback) => {
        // N√£o chama callback imediatamente para manter loading
        return unsubscribejest.MockedFunction;
      });

      render(
        <AuthProvider>
          <div data-testid="child">Child content</div>
        </AuthProvider>
      );

      // Filho n√£o deve ser renderizado enquanto loading
      expect(screen.queryByTestId('child')).not.toBeInTheDocument();
    });

    it('renderiza filhos ap√≥s carregar (sem usu√°rio)', async () => {
      mockOnAuthStateChanged.mockImplementation((auth, callback) => {
        // Simular Firebase retornando null (n√£o logado)
        setTimeout(() => callback(null), 0);
        return unsubscribejest.MockedFunction;
      });

      render(
        <AuthProvider>
          <div data-testid="child">Child content</div>
        </AuthProvider>
      );

      await waitFor(() => {
        expect(screen.getByTestId('child')).toBeInTheDocument();
      });
    });

    it('renderiza filhos ap√≥s carregar (com usu√°rio)', async () => {
      mockOnAuthStateChanged.mockImplementation((auth, callback) => {
        // Simular Firebase retornando usu√°rio logado
        setTimeout(() => callback(mockUser), 0);
        return unsubscribejest.MockedFunction;
      });

      render(
        <AuthProvider>
          <div data-testid="child">Child content</div>
        </AuthProvider>
      );

      await waitFor(() => {
        expect(screen.getByTestId('child')).toBeInTheDocument();
      });
    });

    it('registra listener no Firebase Auth', () => {
      render(
        <AuthProvider>
          <div>Test</div>
        </AuthProvider>
      );

      expect(mockOnAuthStateChanged).toHaveBeenCalledWith(
        {}, // mock auth object
        expect.any(Function)
      );
    });

    it('limpa listener ao desmontar', () => {
      const { unmount } = render(
        <AuthProvider>
          <div>Test</div>
        </AuthProvider>
      );

      unmount();

      expect(unsubscribejest.MockedFunction).toHaveBeenCalled();
    });

    it('atualiza estado quando usu√°rio faz login', async () => {
      let authCallback: ((user: User | null) => void) | null = null;

      mockOnAuthStateChanged.mockImplementation((auth, callback) => {
        authCallback = callback;
        return unsubscribejest.MockedFunction;
      });

      const TestComponent = () => {
        const { currentUser, loading } = useAuth();
        return (
          <div>
            <div data-testid="loading">{loading.toString()}</div>
            <div data-testid="user-email">{currentUser?.email || 'No user'}</div>
          </div>
        );
      };

      render(
        <AuthProvider>
          <TestComponent />
        </AuthProvider>
      );

      // Simular mudan√ßa de estado: usu√°rio faz login
      if (authCallback) {
        authCallback(mockUser as User);
      }

      await waitFor(() => {
        expect(screen.getByTestId('loading')).toHaveTextContent('false');
        expect(screen.getByTestId('user-email')).toHaveTextContent('test@example.com');
      });
    });

    it('atualiza estado quando usu√°rio faz logout', async () => {
      let authCallback: ((user: User | null) => void) | null = null;

      mockOnAuthStateChanged.mockImplementation((auth, callback) => {
        authCallback = callback;
        // Iniciar com usu√°rio logado
        setTimeout(() => callback(mockUser), 0);
        return unsubscribejest.MockedFunction;
      });

      const TestComponent = () => {
        const { currentUser, loading } = useAuth();
        return (
          <div>
            <div data-testid="loading">{loading.toString()}</div>
            <div data-testid="user-email">{currentUser?.email || 'No user'}</div>
          </div>
        );
      };

      render(
        <AuthProvider>
          <TestComponent />
        </AuthProvider>
      );

      // Aguardar login inicial
      await waitFor(() => {
        expect(screen.getByTestId('user-email')).toHaveTextContent('test@example.com');
      });

      // Simular logout
      if (authCallback) {
        authCallback(null);
      }

      await waitFor(() => {
        expect(screen.getByTestId('user-email')).toHaveTextContent('No user');
      });
    });
  });

  describe('useAuth hook', () => {
    const wrapper = ({ children }: { children: React.ReactNode }) => (
      <AuthProvider>{children}</AuthProvider>
    );

    it('retorna valores iniciais corretos', async () => {
      mockOnAuthStateChanged.mockImplementation((auth, callback) => {
        setTimeout(() => callback(null), 0);
        return unsubscribejest.MockedFunction;
      });

      const { result } = renderHook(() => useAuth(), { wrapper });

      // Estado inicial
      expect(result.current.loading).toBe(true);
      expect(result.current.currentUser).toBeNull();

      // Ap√≥s Firebase responder
      await waitFor(() => {
        expect(result.current.loading).toBe(false);
      });
    });

    it('retorna usu√°rio quando logado', async () => {
      mockOnAuthStateChanged.mockImplementation((auth, callback) => {
        setTimeout(() => callback(mockUser), 0);
        return unsubscribejest.MockedFunction;
      });

      const { result } = renderHook(() => useAuth(), { wrapper });

      await waitFor(() => {
        expect(result.current.loading).toBe(false);
        expect(result.current.currentUser).toEqual(mockUser);
      });
    });

    it('retorna null quando n√£o logado', async () => {
      mockOnAuthStateChanged.mockImplementation((auth, callback) => {
        setTimeout(() => callback(null), 0);
        return unsubscribejest.MockedFunction;
      });

      const { result } = renderHook(() => useAuth(), { wrapper });

      await waitFor(() => {
        expect(result.current.loading).toBe(false);
        expect(result.current.currentUser).toBeNull();
      });
    });

    it('atualiza quando estado de auth muda', async () => {
      let authCallback: ((user: User | null) => void) | null = null;

      mockOnAuthStateChanged.mockImplementation((auth, callback) => {
        authCallback = callback;
        // Iniciar sem usu√°rio
        setTimeout(() => callback(null), 0);
        return unsubscribejest.MockedFunction;
      });

      const { result } = renderHook(() => useAuth(), { wrapper });

      // Estado inicial: sem usu√°rio
      await waitFor(() => {
        expect(result.current.currentUser).toBeNull();
      });

      // Simular login
      if (authCallback) {
        authCallback(mockUser as User);
      }

      await waitFor(() => {
        expect(result.current.currentUser).toEqual(mockUser);
      });

      // Simular logout
      if (authCallback) {
        authCallback(null);
      }

      await waitFor(() => {
        expect(result.current.currentUser).toBeNull();
      });
    });

    it('trava erro quando usado fora do AuthProvider', () => {
      // jest.MockedFunction console.error para n√£o poluir output dos testes
      const consoleSpy = jest.spyOn(console, 'error').mockImplementation(() => {});

      expect(() => {
        renderHook(() => useAuth());
      }).toThrow();

      consoleSpy.mockRestore();
    });
  });

  describe('Context Value', () => {
    it('fornece valores corretos para componentes filhos', async () => {
      mockOnAuthStateChanged.mockImplementation((auth, callback) => {
        setTimeout(() => callback(mockUser), 0);
        return unsubscribejest.MockedFunction;
      });

      const TestComponent = () => {
        const auth = useAuth();
        return (
          <div>
            <div data-testid="loading">{auth.loading.toString()}</div>
            <div data-testid="user-uid">{auth.currentUser?.uid || 'No UID'}</div>
            <div data-testid="user-email">{auth.currentUser?.email || 'No Email'}</div>
            <div data-testid="user-name">{auth.currentUser?.displayName || 'No Name'}</div>
            <div data-testid="email-verified">{auth.currentUser?.emailVerified?.toString() || 'false'}</div>
          </div>
        );
      };

      render(
        <AuthProvider>
          <TestComponent />
        </AuthProvider>
      );

      await waitFor(() => {
        expect(screen.getByTestId('loading')).toHaveTextContent('false');
        expect(screen.getByTestId('user-uid')).toHaveTextContent('test-uid-123');
        expect(screen.getByTestId('user-email')).toHaveTextContent('test@example.com');
        expect(screen.getByTestId('user-name')).toHaveTextContent('Test User');
        expect(screen.getByTestId('email-verified')).toHaveTextContent('true');
      });
    });

    it('permite m√∫ltiplos componentes acessarem o mesmo contexto', async () => {
      mockOnAuthStateChanged.mockImplementation((auth, callback) => {
        setTimeout(() => callback(mockUser), 0);
        return unsubscribejest.MockedFunction;
      });

      const Component1 = () => {
        const { currentUser } = useAuth();
        return <div data-testid="comp1">{currentUser?.email || 'No user'}</div>;
      };

      const Component2 = () => {
        const { currentUser } = useAuth();
        return <div data-testid="comp2">{currentUser?.uid || 'No UID'}</div>;
      };

      render(
        <AuthProvider>
          <Component1 />
          <Component2 />
        </AuthProvider>
      );

      await waitFor(() => {
        expect(screen.getByTestId('comp1')).toHaveTextContent('test@example.com');
        expect(screen.getByTestId('comp2')).toHaveTextContent('test-uid-123');
      });
    });
  });

  describe('Firebase Integration', () => {
    it('lida com mudan√ßas r√°pidas de estado', async () => {
      let authCallback: ((user: User | null) => void) | null = null;

      mockOnAuthStateChanged.mockImplementation((auth, callback) => {
        authCallback = callback;
        return unsubscribejest.MockedFunction;
      });

      const { result } = renderHook(() => useAuth(), { 
        wrapper: ({ children }) => <AuthProvider>{children}</AuthProvider> 
      });

      // M√∫ltiplas mudan√ßas r√°pidas
      if (authCallback) {
        authCallback(mockUser as User);
        authCallback(null);
        authCallback(mockUser as User);
      }

      await waitFor(() => {
        expect(result.current.currentUser).toEqual(mockUser);
        expect(result.current.loading).toBe(false);
      });
    });

    it('mant√©m refer√™ncia est√°vel do callback', () => {
      const { rerender } = render(
        <AuthProvider>
          <div>Test</div>
        </AuthProvider>
      );

      const firstCall = mockOnAuthStateChanged.mock.calls[0];

      rerender(
        <AuthProvider>
          <div>Test Updated</div>
        </AuthProvider>
      );

      // N√£o deve registrar novo listener em re-render
      expect(mockOnAuthStateChanged).toHaveBeenCalledTimes(1);
      expect(mockOnAuthStateChanged.mock.calls[0]).toBe(firstCall);
    });
  });
});

import React from 'react';
import { render, screen, waitFor } from '@testing-library/react';
import { renderHook } from '@testing-library/react';
