      // Estado inicial: sem usu√°rio
      await waitFor(() => {
        expect(result.current.currentUser).toBeNull();
      });

      // Simular login
      if (authCallback) {
        authCallback(mockUser as User);
      }

      await waitFor(() => {
        expect(result.current.currentUser).toEqual(mockUser);
      });

      // Simular logout
      if (authCallback) {
        authCallback(null);
      }

      await waitFor(() => {
        expect(result.current.currentUser).toBeNull();
      });
