describe('PWAFeedback', () => {
  beforeEach(() => {
    jest.clearAlljest.MockedFunctions();
    localStoragejest.MockedFunction.getItem.mockReturnValue(null);
  });

  it('renderiza corretamente', () => {
    render(<PWAFeedback />);

    // Pode ter diferentes estados, ent√£o verifico elementos comuns
    expect(document.body).toBeInTheDocument();
  });

  it('n√£o mostra feedback se j√° foi dispensado', () => {
    localStoragejest.MockedFunction.getItem.mockReturnValue('dismissed');

    const { container } = render(<PWAFeedback />);
    // Se foi dispensado, n√£o deve mostrar conte√∫do principal
    expect(container.firstChild).toBeNull();
  });

  it('mostra notifica√ß√£o de sucesso', () => {
    render(<PWAFeedback message="Roteiro salvo com sucesso!" type="success" />);

