      <PWAFeedback>
        <div data-testid="custom-content">Conte√∫do customizado</div>
      </PWAFeedback>
    );

    expect(screen.getByTestId('custom-content')).toBeInTheDocument();
  });

  it('trata clique em bot√£o de a√ß√£o', () => {
    const onAction = jest.fn();
    render(
      <PWAFeedback 
        message="A√ß√£o necess√°ria" 
        action={{ label: 'Confirmar', onClick: onAction }}
      />
    );

    const actionButton = screen.queryByText('Confirmar');
    if (actionButton) {
      fireEvent.click(actionButton);
      expect(onAction).toHaveBeenCalled();
    }
  });

  it('gerencia m√∫ltiplas notifica√ß√µes', () => {
    render(
      <div>
        <PWAFeedback message="Primeira notifica√ß√£o" id="first" />
        <PWAFeedback message="Segunda notifica√ß√£o" id="second" />
      </div>
    );

    expect(screen.getByText('Primeira notifica√ß√£o')).toBeInTheDocument();
    expect(screen.getByText('Segunda notifica√ß√£o')).toBeInTheDocument();
  });

  it('suporta posicionamento customizado', () => {
    const { container } = render(
      <PWAFeedback message="Teste posi√ß√£o" position="top-right" />
    );

    // Verifica se o container tem classes ou estilos de posicionamento
    expect(container.firstChild).toBeInTheDocument();
  });
});

