// jest.MockedFunction do componente Toast
const jest.MockedFunctionToast = ({ 
  type = 'info', 
  message, 
  duration = 5000, 
  onClose 
}: {
  type?: 'success' | 'error' | 'warning' | 'info';
  message: string;
  duration?: number;
  onClose?: () => void;
}) => {
  const icons = {
    success: '‚úÖ',
    error: '‚ùå', 
    warning: '‚ö†Ô∏è',
    info: '‚ÑπÔ∏è'
  };

  const colors = {
    success: 'bg-green-500',
    error: 'bg-red-500',
    warning: 'bg-yellow-500', 
    info: 'bg-blue-500'
  };

  // Simular auto-dismiss
  React.useEffect(() => {
    if (duration > 0) {
      const timer = setTimeout(() => {
        onClose?.();
      }, duration);
      return () => clearTimeout(timer);
    }
  }, [duration, onClose]);

  return (
    <div 
      data-testid="toast"
      className={`toast ${colors[type]}`}
      role="alert"
      aria-live="polite"
    >
      <span data-testid="toast-icon">{icons[type]}</span>
      <span data-testid="toast-message">{message}</span>
      <button 
        data-testid="toast-close"
        onClick={onClose}
        aria-label="Fechar notifica√ß√£o"
      >
        √ó
      </button>
      <div 
        data-testid="progress-bar"
        className="progress-bar"
        style={{
          animation: `progress ${duration}ms linear`
        }}
      />
    </div>
  );
};

// jest.MockedFunction React para useEffect
const React = {
  useEffect: jest.fn((callback, deps) => {
    // Simular execu√ß√£o imediata para testes
    if (deps && deps.length > 0) {
      const cleanup = callback();
      return cleanup;
    }
  })
};

