interface VitalMetricProps {
  name: string;
  value?: number;
  unit: string;
  threshold: number;
  description: string;
}

const VitalMetric: React.FC<VitalMetricProps> = ({ name, value, unit, threshold, description }) => {
  const getStatus = () => {
    if (!value) return 'unknown';
    return value <= threshold ? 'good' : 'warning';
  };

  const getStatusIcon = () => {
    const status = getStatus();
    switch (status) {
      case 'good':
        return <CheckCircle className="w-4 h-4 text-green-500" />;
      case 'warning':
        return <AlertTriangle className="w-4 h-4 text-yellow-500" />;
      default:
        return <AlertTriangle className="w-4 h-4 text-gray-400" />;
    }
  };

  return (
    <div className="p-3 border rounded-lg">
      <div className="flex items-center justify-between mb-1">
        <span className="text-sm font-medium">{name}</span>
        {getStatusIcon()}
      </div>
      <div className="text-lg font-bold">
        {value ? `${value.toFixed(name === 'CLS' ? 3 : 0)}${unit}` : 'N/A'}
      </div>
      <div className="text-xs text-muted-foreground">{description}</div>
    </div>
  );
};

interface MetricRowProps {
  label: string;
  value: string;
  status: 'good' | 'warning' | 'error';
}

const MetricRow: React.FC<MetricRowProps> = ({ label, value, status }) => {
  const getStatusColor = () => {
    switch (status) {
      case 'good':
        return 'text-green-600';
      case 'warning':
        return 'text-yellow-600';
      case 'error':
        return 'text-red-600';
      default:
        return 'text-gray-600';
    }
  };

  const getStatusIcon = () => {
    switch (status) {
      case 'good':
        return <CheckCircle className="w-4 h-4 text-green-500" />;
      case 'warning':
        return <AlertTriangle className="w-4 h-4 text-yellow-500" />;
      case 'error':
        return <AlertTriangle className="w-4 h-4 text-red-500" />;
    }
  };

  return (
    <div className="flex items-center justify-between">
      <span className="text-sm text-muted-foreground">{label}</span>
      <div className="flex items-center space-x-2">
        <span className={`text-sm font-medium ${getStatusColor()}`}>{value}</span>
        {getStatusIcon()}
      </div>
    </div>
  );
};

