import { Button } from '../ui/Button';
import { Input } from '../ui/Input';
import { Badge } from '../ui/Badge';
import { 
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '../ui/Select';

import { 
  FilterOptions, 
  PlatformType, 
  FormatType, 
  ProjectStatus,
  SortOption,
  Tag,
  Folder as FolderType
} from '../../types/enhanced';

import { tagService } from '../../services/tagService';
import { createLogger } from '../../utils/logger';

const logger = createLogger('DashboardFilters');

interface DashboardFiltersProps {
  filters: FilterOptions;
  onFiltersChange: (filters: FilterOptions) => void;
  totalProjects: number;
  filteredCount: number;
  isLoading?: boolean;
  userTags?: Tag[];
  userFolders?: FolderType[];
}

export const DashboardFilters: React.FC<DashboardFiltersProps> = ({
  filters,
  onFiltersChange,
  totalProjects,
  filteredCount,
  isLoading = false,
  userTags = [],
  userFolders = []
}) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedDateRange, setSelectedDateRange] = useState<string>('all');

  // Platform options
  const platformOptions: { value: PlatformType; label: string; icon: string }[] = [
    { value: 'youtube', label: 'YouTube', icon: 'üì∫' },
    { value: 'instagram', label: 'Instagram', icon: 'üì∏' },
    { value: 'tiktok', label: 'TikTok', icon: 'üéµ' },
    { value: 'facebook', label: 'Facebook', icon: 'üìò' },
    { value: 'twitter', label: 'Twitter', icon: 'üê¶' },
    { value: 'linkedin', label: 'LinkedIn', icon: 'üíº' }
  ];

  // Format options  
  const formatOptions: { value: FormatType; label: string }[] = [
    { value: 'short', label: 'Short (at√© 60s)' },
    { value: 'reel', label: 'Reel (at√© 90s)' },
    { value: 'story', label: 'Story (at√© 15s)' },
    { value: 'post', label: 'Post' },
    { value: 'video', label: 'V√≠deo Longo' },
    { value: 'carousel', label: 'Carrossel' }
  ];

  // Status options
  const statusOptions: { value: ProjectStatus; label: string; color: string }[] = [
    { value: 'draft', label: 'Rascunho', color: 'yellow' },
    { value: 'completed', label: 'Conclu√≠do', color: 'green' },
    { value: 'published', label: 'Publicado', color: 'blue' }
  ];

  // Sort options
  const sortOptions: { value: SortOption; label: string }[] = [
    { value: 'date', label: 'Data de Cria√ß√£o' },
    { value: 'title', label: 'T√≠tulo' },
    { value: 'platform', label: 'Plataforma' },
    { value: 'wordCount', label: 'Palavras' },
    { value: 'editCount', label: 'Edi√ß√µes' },
    { value: 'viewCount', label: 'Visualiza√ß√µes' }
  ];

  // Date range presets
  const dateRangeOptions = [
    { value: 'all', label: 'Todos os per√≠odos' },
    { value: 'today', label: 'Hoje' },
    { value: 'week', label: '√öltima semana' },
    { value: 'month', label: '√öltimo m√™s' },
    { value: 'quarter', label: '√öltimos 3 meses' },
    { value: 'year', label: '√öltimo ano' },
    { value: 'custom', label: 'Per√≠odo personalizado' }
  ];

  // Calculate active filters count
  const activeFiltersCount = useMemo(() => {
    let count = 0;
    if (filters.platforms?.length) count++;
    if (filters.formats?.length) count++;
    if (filters.status?.length) count++;
    if (filters.tags?.length) count++;
    if (filters.folderId) count++;
    if (filters.isFavorite !== undefined) count++;
    if (filters.dateRange) count++;
    return count;
  }, [filters]);

  // Handle platform selection
  const handlePlatformToggle = (platform: PlatformType) => {
    const currentPlatforms = filters.platforms || [];
    const newPlatforms = currentPlatforms.includes(platform)
      ? currentPlatforms.filter(p => p !== platform)
      : [...currentPlatforms, platform];

    onFiltersChange({
      ...filters,
      platforms: newPlatforms.length > 0 ? newPlatforms : undefined
    });
  };

  // Handle format selection
  const handleFormatToggle = (format: FormatType) => {
    const currentFormats = filters.formats || [];
    const newFormats = currentFormats.includes(format)
      ? currentFormats.filter(f => f !== format)
      : [...currentFormats, format];

    onFiltersChange({
      ...filters,
      formats: newFormats.length > 0 ? newFormats : undefined
    });
  };

  // Handle status selection
  const handleStatusToggle = (status: ProjectStatus) => {
    const currentStatuses = filters.status || [];
    const newStatuses = currentStatuses.includes(status)
      ? currentStatuses.filter(s => s !== status)
      : [...currentStatuses, status];

    onFiltersChange({
      ...filters,
      status: newStatuses.length > 0 ? newStatuses : undefined
    });
  };

  // Handle tag selection
  const handleTagToggle = (tagId: string) => {
    const currentTags = filters.tags || [];
    const newTags = currentTags.includes(tagId)
      ? currentTags.filter(t => t !== tagId)
      : [...currentTags, tagId];

    onFiltersChange({
      ...filters,
      tags: newTags.length > 0 ? newTags : undefined
    });
  };

  // Handle date range selection
  const handleDateRangeChange = (range: string) => {
    setSelectedDateRange(range);

    const now = new Date();
    let start: Date | undefined;
    let end: Date | undefined;

    switch (range) {
      case 'today':
        start = new Date(now.getFullYear(), now.getMonth(), now.getDate());
        end = new Date(now.getFullYear(), now.getMonth(), now.getDate() + 1);
        break;
      case 'week':
        start = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
        end = now;
        break;
      case 'month':
        start = new Date(now.getFullYear(), now.getMonth() - 1, now.getDate());
        end = now;
        break;
      case 'quarter':
        start = new Date(now.getFullYear(), now.getMonth() - 3, now.getDate());
        end = now;
        break;
      case 'year':
        start = new Date(now.getFullYear() - 1, now.getMonth(), now.getDate());
        end = now;
        break;
      case 'all':
      default:
        start = undefined;
        end = undefined;
        break;
    }

    onFiltersChange({
      ...filters,
      dateRange: start && end ? { start, end } : undefined
    });
  };

  // Handle sort change
  const handleSortChange = (sortBy: SortOption) => {
    onFiltersChange({
      ...filters,
      sortBy
    });
  };

  // Handle sort order toggle
  const handleSortOrderToggle = () => {
    onFiltersChange({
      ...filters,
      sortOrder: filters.sortOrder === 'asc' ? 'desc' : 'asc'
    });
  };

  // Clear all filters
  const clearAllFilters = () => {
    onFiltersChange({
      sortBy: 'date',
      sortOrder: 'desc'
    });
    setSelectedDateRange('all');
    setSearchQuery('');
  };

  // Clear specific filter
  const clearFilter = (filterType: string) => {
    const newFilters = { ...filters };

    switch (filterType) {
      case 'platforms':
        delete newFilters.platforms;
        break;
      case 'formats':
        delete newFilters.formats;
        break;
      case 'status':
        delete newFilters.status;
        break;
      case 'tags':
        delete newFilters.tags;
        break;
      case 'folder':
        delete newFilters.folderId;
        break;
      case 'favorites':
        delete newFilters.isFavorite;
        break;
      case 'dateRange':
        delete newFilters.dateRange;
        setSelectedDateRange('all');
        break;
    }

    onFiltersChange(newFilters);
  };

  return (
    <div className="bg-white border border-gray-200 rounded-lg shadow-sm">
      {/* Filter Header */}
      <div className="p-4 border-b border-gray-200">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-3">
            <Filter size={20} className="text-gray-600" />
            <h3 className="font-semibold text-gray-900">Filtros</h3>
            {activeFiltersCount > 0 && (
              <Badge variant="secondary" className="text-xs">
                {activeFiltersCount} ativo{activeFiltersCount > 1 ? 's' : ''}
              </Badge>
            )}
          </div>

          <div className="flex items-center gap-2">
            <span className="text-sm text-gray-600">
              {isLoading ? 'Carregando...' : `${filteredCount} de ${totalProjects} projetos`}
            </span>

            <Button
              variant="ghost"
              size="sm"
              onClick={() => setIsExpanded(!isExpanded)}
              className="text-gray-600"
            >
              <ChevronDown 
                size={16} 
                className={`transition-transform ${isExpanded ? 'rotate-180' : ''}`}
              />
            </Button>
          </div>
        </div>

        {/* Quick Actions */}
        <div className="flex items-center gap-2 mt-3">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => onFiltersChange({
              ...filters,
              isFavorite: filters.isFavorite ? undefined : true
            })}
            className={filters.isFavorite ? 'bg-yellow-50 text-yellow-700' : ''}
          >
            <Star size={14} className={filters.isFavorite ? 'fill-current' : ''} />
            Favoritos
          </Button>

          {activeFiltersCount > 0 && (
            <Button
              variant="ghost"
              size="sm"
              onClick={clearAllFilters}
              className="text-red-600 hover:text-red-700"
            >
              <X size={14} />
              Limpar Filtros
            </Button>
          )}
        </div>
      </div>

      {/* Expanded Filters */}
      {isExpanded && (
        <div className="p-4 space-y-6">
          {/* Search */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Buscar
            </label>
            <div className="relative">
              <Search size={16} className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
              <Input
                type="text"
                placeholder="Buscar por t√≠tulo, conte√∫do ou tags..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10"
              />
            </div>
          </div>

          {/* Platforms */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Plataformas
            </label>
            <div className="flex flex-wrap gap-2">
              {platformOptions.map((platform) => (
                <Button
                  key={platform.value}
                  variant={filters.platforms?.includes(platform.value) ? "default" : "outline"}
                  size="sm"
                  onClick={() => handlePlatformToggle(platform.value)}
                  className="text-xs"
                >
                  <span className="mr-1">{platform.icon}</span>
                  {platform.label}
                </Button>
              ))}
            </div>
          </div>

          {/* Formats */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Formatos
            </label>
            <div className="flex flex-wrap gap-2">
              {formatOptions.map((format) => (
                <Button
                  key={format.value}
                  variant={filters.formats?.includes(format.value) ? "default" : "outline"}
                  size="sm"
                  onClick={() => handleFormatToggle(format.value)}
                  className="text-xs"
                >
                  {format.label}
                </Button>
              ))}
            </div>
          </div>

          {/* Status */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Status
            </label>
            <div className="flex flex-wrap gap-2">
              {statusOptions.map((status) => (
                <Button
                  key={status.value}
                  variant={filters.status?.includes(status.value) ? "default" : "outline"}
                  size="sm"
                  onClick={() => handleStatusToggle(status.value)}
                  className="text-xs"
                >
                  <div className={`w-2 h-2 rounded-full mr-2 bg-${status.color}-400`} />
                  {status.label}
                </Button>
              ))}
            </div>
          </div>

          {/* Tags */}
          {userTags.length > 0 && (
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Tags
              </label>
              <div className="flex flex-wrap gap-2 max-h-32 overflow-y-auto">
                {userTags.map((tag) => (
                  <Button
                    key={tag.id}
                    variant={filters.tags?.includes(tag.id) ? "default" : "outline"}
                    size="sm"
                    onClick={() => handleTagToggle(tag.id)}
                    className="text-xs"
                  >
                    <div 
                      className="w-2 h-2 rounded-full mr-2"
                      style={{ backgroundColor: tag.color }}
                    />
                    {tag.name}
                  </Button>
                ))}
              </div>
            </div>
          )}

          {/* Date Range */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              <Clock size={14} className="inline mr-1" />
              Per√≠odo
            </label>
            <Select value={selectedDateRange} onValueChange={handleDateRangeChange}>
              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                {dateRangeOptions.map((option) => (
                  <SelectItem key={option.value} value={option.value}>
                    {option.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Sorting */}
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Ordenar por
              </label>
              <Select value={filters.sortBy} onValueChange={handleSortChange}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {sortOptions.map((option) => (
                    <SelectItem key={option.value} value={option.value}>
                      {option.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Ordem
              </label>
              <Button
                variant="outline"
                onClick={handleSortOrderToggle}
                className="w-full justify-start"
              >
                {filters.sortOrder === 'asc' ? '‚Üë Crescente' : '‚Üì Decrescente'}
              </Button>
            </div>
          </div>
        </div>
      )}

      {/* Active Filters Display */}
      {activeFiltersCount > 0 && (
        <div className="px-4 py-3 bg-gray-50 border-t border-gray-200">
          <div className="flex flex-wrap gap-2">
            {filters.platforms && (
              <Badge 
                variant="secondary" 
                className="cursor-pointer"
                onClick={() => clearFilter('platforms')}
              >
                Plataformas ({filters.platforms.length})
                <X size={12} className="ml-1" />
              </Badge>
            )}

            {filters.formats && (
              <Badge 
                variant="secondary"
                className="cursor-pointer"
                onClick={() => clearFilter('formats')}
              >
                Formatos ({filters.formats.length})
                <X size={12} className="ml-1" />
              </Badge>
            )}

            {filters.status && (
              <Badge 
                variant="secondary"
                className="cursor-pointer"
                onClick={() => clearFilter('status')}
              >
                Status ({filters.status.length})
                <X size={12} className="ml-1" />
              </Badge>
            )}

            {filters.tags && (
              <Badge 
                variant="secondary"
                className="cursor-pointer"
                onClick={() => clearFilter('tags')}
              >
                Tags ({filters.tags.length})
                <X size={12} className="ml-1" />
              </Badge>
            )}

            {filters.isFavorite && (
              <Badge 
                variant="secondary"
                className="cursor-pointer"
                onClick={() => clearFilter('favorites')}
              >
                <Star size={12} className="mr-1 fill-current" />
                Favoritos
                <X size={12} className="ml-1" />
              </Badge>
            )}

            {filters.dateRange && (
              <Badge 
                variant="secondary"
                className="cursor-pointer"
                onClick={() => clearFilter('dateRange')}
              >
                <Clock size={12} className="mr-1" />
                Per√≠odo
                <X size={12} className="ml-1" />
              </Badge>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default DashboardFilters;

import React, { useState, useEffect } from 'react';
import { 
  TrendingUp, 
  TrendingDown, 
  Clock, 
  Target, 
  Zap, 
  Users, 
  Eye,
  Heart,
  Share2,
  Download,
  BarChart3,
  Activity,
  Award,
  Star,
  ChevronRight,
  RefreshCw, AlertCircle,
  Calendar,
  Filter
} from 'lucide-react';
import { Card } from '../ui/Card';
import { Badge } from '../ui/Badge';
import { Button } from '../ui/Button';
import { ProgressRing, MultiProgressRing } from '../ui/ProgressRing';
import { LoadingSpinner } from '../ui/LoadingSpinner';
import { AdvancedAnalyticsService } from '../../services/advancedAnalyticsService';
import type { 
  ProductivityMetrics, 
  UserAnalytics, 
  PerformanceInsight,
  CollaborationMetrics,
  ContentQualityMetrics
} from '../../types';

interface DashboardStatsProps {
  userId: string;
  timeRange: '7d' | '30d' | '90d' | '1y';
  onTimeRangeChange: (range: '7d' | '30d' | '90d' | '1y') => void;
}

export const DashboardStats: React.FC<DashboardStatsProps> = ({
  userId,
  timeRange,
  onTimeRangeChange
}) => {
  // Estados
  const [isLoading, setIsLoading] = useState(true);
  const [isRefreshing, setIsRefreshing] = useState(false);
  const [metrics, setMetrics] = useState<ProductivityMetrics | null>(null);
  const [analytics, setAnalytics] = useState<UserAnalytics | null>(null);
  const [insights, setInsights] = useState<PerformanceInsight[]>([]);
  const [collaborationData, setCollaborationData] = useState<CollaborationMetrics | null>(null);
  const [qualityData, setQualityData] = useState<ContentQualityMetrics | null>(null);
  const [selectedView, setSelectedView] = useState<'overview' | 'detailed' | 'comparison'>('overview');
  const [animatedValues, setAnimatedValues] = useState<Record<string, number>>({});

  // Carregar dados iniciais
  useEffect(() => {
    loadDashboardData();
  }, [userId, timeRange]);

  // Anima√ß√£o de valores
  useEffect(() => {
    if (metrics) {
      const targets = {
        productivity: metrics.overallProductivity,
        efficiency: metrics.efficiencyScore,
        quality: qualityData?.overallQuality || 0,
        collaboration: collaborationData?.collaborationScore || 0
      };

      Object.entries(targets).forEach(([key, target]) => {
        let current = 0;
        const increment = target / 30; // Anima√ß√£o em 30 frames

        const animate = () => {
          current = Math.min(current + increment, target);
          setAnimatedValues(prev => ({ ...prev, [key]: current }));

          if (current < target) {
            requestAnimationFrame(animate);
          }
        };

        requestAnimationFrame(animate);
      });
    }
  }, [metrics, qualityData, collaborationData]);

  const loadDashboardData = async () => {
    try {
      setIsLoading(true);

      const [
        productivityData,
        analyticsData,
        insightsData,
        collaborationMetrics,
        qualityMetrics
      ] = await Promise.all([
        AdvancedAnalyticsService.getProductivityMetrics(userId, timeRange),
        AdvancedAnalyticsService.getUserAnalytics(userId),
        AdvancedAnalyticsService.generateInsights(userId, timeRange),
        AdvancedAnalyticsService.getCollaborationMetrics(userId, timeRange),
        AdvancedAnalyticsService.getContentQualityMetrics(userId, timeRange)
      ]);

      setMetrics(productivityData);
      setAnalytics(analyticsData);
      setInsights(insightsData);
      setCollaborationData(collaborationMetrics);
      setQualityData(qualityMetrics);
    } catch (error) {
      console.error('Erro ao carregar dados do dashboard:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleRefresh = async () => {
    setIsRefreshing(true);
    await loadDashboardData();
    setIsRefreshing(false);
  };

  const formatValue = (value: number, type: 'number' | 'percentage' | 'time' | 'score' = 'number') => {
    if (type === 'percentage') return `${Math.round(value)}%`;
    if (type === 'time') return `${Math.round(value)}min`;
    if (type === 'score') return `${value.toFixed(1)}/10`;
    return value.toLocaleString();
  };

  const getTrendIcon = (trend: number) => {
    if (trend > 0) return <TrendingUp className="w-4 h-4 text-green-500" />;
    if (trend < 0) return <TrendingDown className="w-4 h-4 text-red-500" />;
    return <Activity className="w-4 h-4 text-gray-500" />;
  };

  const getTrendColor = (trend: number) => {
    if (trend > 0) return 'text-green-600 dark:text-green-400';
    if (trend < 0) return 'text-red-600 dark:text-red-400';
    return 'text-gray-600 dark:text-gray-400';
  };

  const getPerformanceLevel = (score: number) => {
    if (score >= 90) return { label: 'Excepcional', color: 'green', icon: Award };
    if (score >= 80) return { label: 'Excelente', color: 'blue', icon: Star };
    if (score >= 70) return { label: 'Bom', color: 'yellow', icon: Target };
    if (score >= 60) return { label: 'Regular', color: 'orange', icon: Activity };
    return { label: 'Precisa Melhorar', color: 'red', icon: TrendingDown };
  };

  if (isLoading) {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {[...Array(8)].map((_, i) => (
          <Card key={i} className="p-6">
            <div className="animate-pulse">
              <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-3/4 mb-2"></div>
              <div className="h-8 bg-gray-200 dark:bg-gray-700 rounded w-1/2 mb-4"></div>
              <div className="h-2 bg-gray-200 dark:bg-gray-700 rounded w-full"></div>
            </div>
          </Card>
        ))}
      </div>
    );
  }

  if (!metrics || !analytics) {
    return (
      <Card className="p-8 text-center">
        <p className="text-gray-600 dark:text-gray-400">
          Erro ao carregar dados do dashboard. Tente novamente.
        </p>
        <Button onClick={handleRefresh} className="mt-4">
          <RefreshCw className="w-4 h-4 mr-2" />
          Tentar Novamente
        </Button>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header com controles */}
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div>
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white">
            Dashboard Anal√≠tico
          </h2>
          <p className="text-gray-600 dark:text-gray-400">
            Insights em tempo real sobre sua produtividade
          </p>
        </div>

        <div className="flex items-center gap-3">
          {/* Seletor de per√≠odo */}
          <div className="flex items-center gap-1 p-1 bg-gray-100 dark:bg-gray-800 rounded-lg">
            {[
              { value: '7d', label: '7d' },
              { value: '30d', label: '30d' },
              { value: '90d', label: '90d' },
              { value: '1y', label: '1a' }
            ].map(period => (
              <button
                key={period.value}
                onClick={() => onTimeRangeChange(period.value as any)}
                className={`px-3 py-1.5 text-sm font-medium rounded-md transition-colors ${
                  timeRange === period.value
                    ? 'bg-white dark:bg-gray-700 text-gray-900 dark:text-white shadow-sm'
                    : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white'
                }`}
              >
                {period.label}
              </button>
            ))}
          </div>

          {/* Seletor de visualiza√ß√£o */}
          <div className="flex items-center gap-1 p-1 bg-gray-100 dark:bg-gray-800 rounded-lg">
            {[
              { value: 'overview', label: 'Vis√£o Geral', icon: BarChart3 },
              { value: 'detailed', label: 'Detalhado', icon: Activity },
              { value: 'comparison', label: 'Compara√ß√£o', icon: TrendingUp }
            ].map(view => (
              <button
