import React, { useState, useEffect } from 'react';
import { 
  Save, 
  Bookmark, 
  History, 
  Settings, 
  Plus, 
  Trash2, 
  Edit3, 
  Check, 
  X,
  Clock,
  Star,
  MoreVertical
} from 'lucide-react';
import { Button } from '../ui/Button';
import { Input } from '../ui/Input';
import { Badge } from '../ui/Badge';
import { 
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from '../ui/Dialog';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from '../ui/DropdownMenu';
import { Textarea } from '../ui/Textarea';
import { Switch } from '../ui/Switch';

import type { FilterOptions } from '../../types/enhanced';
import { FilterPersistenceService, SavedFilter } from '../../services/filterPersistenceService';
import { createLogger } from '../../utils/logger';

const logger = createLogger('FilterPresets');

interface FilterPresetsProps {
  currentFilters: FilterOptions;
  onApplyFilters: (filters: FilterOptions) => void;
  userId: string;
}

export const FilterPresets: React.FC<FilterPresetsProps> = ({
  currentFilters,
  onApplyFilters,
  userId
}) => {
  const [presets, setPresets] = useState<SavedFilter[]>([]);
  const [filterHistory, setFilterHistory] = useState<Array<{
    filters: FilterOptions;
    timestamp: string;
    description: string;
  }>>([]);
  const [preferences, setPreferences] = useState(FilterPersistenceService.loadFilterPreferences());
  const [isLoading, setIsLoading] = useState(false);

  // Estados para modais
  const [showSaveDialog, setShowSaveDialog] = useState(false);
  const [showHistoryDialog, setShowHistoryDialog] = useState(false);
  const [showPreferencesDialog, setShowPreferencesDialog] = useState(false);
  const [editingPreset, setEditingPreset] = useState<SavedFilter | null>(null);

  // Estados para formul√°rios
  const [presetName, setPresetName] = useState('');
  const [presetDescription, setPresetDescription] = useState('');

  // Carregar dados iniciais
  useEffect(() => {
    loadPresets();
    loadFilterHistory();
  }, [userId]);

  // Auto-salvar filtros quando habilitado
  useEffect(() => {
    if (preferences.autoSave) {
      FilterPersistenceService.saveCurrentFilters(currentFilters);
    }
  }, [currentFilters, preferences.autoSave]);

  const loadPresets = async () => {
    try {
      setIsLoading(true);
      const userPresets = await FilterPersistenceService.loadFilterPresets(userId);
      setPresets(userPresets);
    } catch (error) {
      logger.error('Erro ao carregar presets', error);
    } finally {
      setIsLoading(false);
    }
  };

  const loadFilterHistory = () => {
    const history = FilterPersistenceService.getFilterHistory();
    setFilterHistory(history);
  };

  const handleSavePreset = async () => {
    if (!presetName.trim()) return;

    try {
      setIsLoading(true);

      if (editingPreset) {
        await FilterPersistenceService.updateFilterPreset(editingPreset.id, {
          name: presetName,
          description: presetDescription,
          filters: currentFilters
        });
      } else {
        await FilterPersistenceService.saveFilterPreset(
          userId,
          presetName,
          currentFilters,
          presetDescription
        );
      }

      await loadPresets();
      setShowSaveDialog(false);
      setEditingPreset(null);
      setPresetName('');
      setPresetDescription('');
    } catch (error) {
      logger.error('Erro ao salvar preset', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleDeletePreset = async (presetId: string) => {
    try {
      setIsLoading(true);
      await FilterPersistenceService.deleteFilterPreset(presetId);
      await loadPresets();
    } catch (error) {
      logger.error('Erro ao deletar preset', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleApplyPreset = async (preset: SavedFilter) => {
    try {
      await FilterPersistenceService.incrementPresetUsage(preset.id);
      onApplyFilters(preset.filters);
      await loadPresets(); // Recarregar para atualizar contadores
    } catch (error) {
      logger.error('Erro ao aplicar preset', error);
    }
  };

  const handleApplyFromHistory = (filters: FilterOptions) => {
    onApplyFilters(filters);
    setShowHistoryDialog(false);
  };

  const handleEditPreset = (preset: SavedFilter) => {
    setEditingPreset(preset);
    setPresetName(preset.name);
    setPresetDescription(preset.description || '');
    setShowSaveDialog(true);
  };

  const handleSavePreferences = () => {
    FilterPersistenceService.saveFilterPreferences(preferences);
    setShowPreferencesDialog(false);
  };

  const formatTimestamp = (timestamp: string) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diffMs = now.getTime() - date.getTime();
    const diffMins = Math.floor(diffMs / (1000 * 60));
    const diffHours = Math.floor(diffMs / (1000 * 60 * 60));
    const diffDays = Math.floor(diffMs / (1000 * 60 * 60 * 24));

    if (diffMins < 60) {
      return `${diffMins}min atr√°s`;
    } else if (diffHours < 24) {
      return `${diffHours}h atr√°s`;
    } else if (diffDays < 30) {
      return `${diffDays}d atr√°s`;
    } else {
      return date.toLocaleDateString('pt-BR');
    }
  };

  return (
    <div className="flex items-center gap-2">
      {/* Presets Salvos */}
      {presets.length > 0 && (
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <Button variant="outline" size="sm">
              <Bookmark size={14} className="mr-1" />
              Presets ({presets.length})
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent className="w-64 max-h-80 overflow-y-auto">
            {presets.map((preset) => (
              <div key={preset.id} className="flex items-center justify-between p-2 hover:bg-gray-50">
                <div 
                  className="flex-1 cursor-pointer"
                  onClick={() => handleApplyPreset(preset)}
                >
                  <div className="font-medium text-sm">{preset.name}</div>
                  {preset.description && (
                    <div className="text-xs text-gray-500 truncate">
                      {preset.description}
                    </div>
                  )}
                  <div className="flex items-center gap-2 mt-1">
                    <Badge variant="outline" className="text-xs">
                      {preset.usageCount} uso{preset.usageCount !== 1 ? 's' : ''}
                    </Badge>
                    <span className="text-xs text-gray-400">
                      {formatTimestamp(preset.updatedAt.toISOString())}
                    </span>
                  </div>
                </div>

                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button variant="ghost" size="sm">
                      <MoreVertical size={12} />
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent>
                    <DropdownMenuItem onClick={() => handleEditPreset(preset)}>
                      <Edit3 size={12} className="mr-2" />
                      Editar
                    </DropdownMenuItem>
                    <DropdownMenuItem 
                      onClick={() => handleDeletePreset(preset.id)}
                      className="text-red-600"
                    >
                      <Trash2 size={12} className="mr-2" />
                      Excluir
                    </DropdownMenuItem>
                  </DropdownMenuContent>
                </DropdownMenu>
              </div>
            ))}
          </DropdownMenuContent>
        </DropdownMenu>
      )}

      {/* Salvar Preset Atual */}
      <Dialog open={showSaveDialog} onOpenChange={setShowSaveDialog}>
        <DialogTrigger asChild>
          <Button variant="outline" size="sm">
            <Save size={14} className="mr-1" />
            Salvar
          </Button>
        </DialogTrigger>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>
              {editingPreset ? 'Editar Preset' : 'Salvar Filtros Como Preset'}
            </DialogTitle>
          </DialogHeader>

          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium mb-1">Nome do Preset</label>
              <Input
                value={presetName}
                onChange={(e) => setPresetName(e.target.value)}
                placeholder="Ex: Conte√∫do Instagram Recente"
                maxLength={50}
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-1">Descri√ß√£o (opcional)</label>
              <Textarea
                value={presetDescription}
                onChange={(e) => setPresetDescription(e.target.value)}
                placeholder="Descreva quando usar este preset..."
                maxLength={200}
                rows={3}
              />
            </div>

            <div className="flex justify-end gap-2">
              <Button 
                variant="outline" 
                onClick={() => {
                  setShowSaveDialog(false);
                  setEditingPreset(null);
                  setPresetName('');
                  setPresetDescription('');
                }}
              >
                <X size={14} className="mr-1" />
                Cancelar
              </Button>
              <Button 
                onClick={handleSavePreset}
                disabled={!presetName.trim() || isLoading}
              >
                <Check size={14} className="mr-1" />
                {editingPreset ? 'Atualizar' : 'Salvar'}
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>

      {/* Hist√≥rico de Filtros */}
      {preferences.showFilterHistory && filterHistory.length > 0 && (
        <Dialog open={showHistoryDialog} onOpenChange={setShowHistoryDialog}>
          <DialogTrigger asChild>
            <Button variant="outline" size="sm">
              <History size={14} className="mr-1" />
              Hist√≥rico
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Hist√≥rico de Filtros</DialogTitle>
            </DialogHeader>

            <div className="max-h-96 overflow-y-auto space-y-2">
              {filterHistory.map((item, index) => (
                <div
                  key={`${item.timestamp}-${index}`}
                  className="flex items-center justify-between p-3 border border-gray-200 rounded-lg hover:bg-gray-50 cursor-pointer"
                  onClick={() => handleApplyFromHistory(item.filters)}
                >
                  <div className="flex-1">
                    <div className="font-medium text-sm">{item.description}</div>
                    <div className="text-xs text-gray-500 flex items-center gap-1">
                      <Clock size={10} />
                      {formatTimestamp(item.timestamp)}
                    </div>
                  </div>
                  <Button variant="ghost" size="sm">
                    Aplicar
                  </Button>
                </div>
              ))}
            </div>
          </DialogContent>
        </Dialog>
      )}

      {/* Prefer√™ncias */}
      <Dialog open={showPreferencesDialog} onOpenChange={setShowPreferencesDialog}>
        <DialogTrigger asChild>
          <Button variant="outline" size="sm">
            <Settings size={14} className="mr-1" />
            Prefer√™ncias
          </Button>
        </DialogTrigger>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Prefer√™ncias de Filtros</DialogTitle>
          </DialogHeader>

          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <div>
                <div className="font-medium">Salvar automaticamente</div>
                <div className="text-sm text-gray-500">
                  Salva filtros automaticamente no navegador
                </div>
              </div>
              <Switch
                checked={preferences.autoSave}
                onCheckedChange={(checked) => 
                  setPreferences(prev => ({ ...prev, autoSave: checked }))
                }
              />
            </div>

            <div className="flex items-center justify-between">
              <div>
                <div className="font-medium">Lembrar estado expandido</div>
                <div className="text-sm text-gray-500">
                  Mant√©m filtros expandidos ao recarregar
                </div>
              </div>
              <Switch
                checked={preferences.rememberExpanded}
                onCheckedChange={(checked) => 
                  setPreferences(prev => ({ ...prev, rememberExpanded: checked }))
                }
              />
            </div>

            <div className="flex items-center justify-between">
              <div>
                <div className="font-medium">Mostrar hist√≥rico</div>
                <div className="text-sm text-gray-500">
                  Exibe bot√£o do hist√≥rico de filtros
                </div>
              </div>
              <Switch
                checked={preferences.showFilterHistory}
                onCheckedChange={(checked) => 
                  setPreferences(prev => ({ ...prev, showFilterHistory: checked }))
                }
              />
            </div>

            <div className="border-t pt-4">
              <div className="font-medium mb-2">Ordena√ß√£o padr√£o</div>
              <div className="grid grid-cols-2 gap-2">
                <select
                  value={preferences.defaultSort.sortBy}
                  onChange={(e) => setPreferences(prev => ({
                    ...prev,
                    defaultSort: { ...prev.defaultSort, sortBy: e.target.value }
                  }))}
                  className="p-2 border border-gray-300 rounded"
                >
                  <option value="date">Data</option>
                  <option value="title">T√≠tulo</option>
                  <option value="platform">Plataforma</option>
                  <option value="wordCount">Palavras</option>
                </select>

                <select
                  value={preferences.defaultSort.sortOrder}
                  onChange={(e) => setPreferences(prev => ({
                    ...prev,
                    defaultSort: { ...prev.defaultSort, sortOrder: e.target.value as 'asc' | 'desc' }
                  }))}
                  className="p-2 border border-gray-300 rounded"
                >
                  <option value="desc">Decrescente</option>
                  <option value="asc">Crescente</option>
                </select>
              </div>
            </div>

            <div className="flex justify-end gap-2 pt-4 border-t">
              <Button 
                variant="outline" 
                onClick={() => setShowPreferencesDialog(false)}
              >
                Cancelar
              </Button>
              <Button onClick={handleSavePreferences}>
                <Check size={14} className="mr-1" />
                Salvar
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>

      {/* Indicador de carregamento */}
      {isLoading && (
        <div className="text-xs text-gray-500 flex items-center gap-1">
          <div className="animate-spin rounded-full h-3 w-3 border-2 border-gray-300 border-t-blue-600"></div>
          Processando...
        </div>
      )}
    </div>
  );
};

import React, { useState } from 'react';
import { 
  MoreVertical, 
  Star, 
  StarOff, 
  Edit, 
  Copy, 
  Share, 
  Download, 
  FolderOpen, 
  Trash2, 
  Clock, 
  FileText, 
  Calendar,
  Eye,
  ExternalLink
} from 'lucide-react';
import { Button } from '../ui/Button';
import { Badge } from '../ui/Badge';
import { Card } from '../ui/Card';
import { Separator } from '../ui/Separator';
import type { EnhancedProject } from '../../types';
import { cn } from '../../lib/utils';

interface ProjectCardProps {
  project: EnhancedProject;
  view: 'grid' | 'list';
  onAction: (action: string, project: EnhancedProject) => void;
  isSelected?: boolean;
  allowSelection?: boolean;
  showActions?: boolean;
  className?: string;
}

const ProjectCard: React.FC<ProjectCardProps> = ({
  project,
  view,
  onAction,
  isSelected = false,
  allowSelection = false,
  showActions = true,
  className = ''
}) => {
  const [showMenu, setShowMenu] = useState(false);
  const [isHovered, setIsHovered] = useState(false);

  const formatDate = (timestamp: any) => {
    if (!timestamp) return 'Data desconhecida';
    const date = timestamp.toDate ? timestamp.toDate() : new Date(timestamp);
    return new Intl.DateTimeFormat('pt-BR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    }).format(date);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed': return 'bg-green-100 text-green-800 border-green-200';
      case 'published': return 'bg-blue-100 text-blue-800 border-blue-200';
      case 'draft': 
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getStatusLabel = (status: string) => {
    switch (status) {
      case 'completed': return 'Conclu√≠do';
      case 'published': return 'Publicado';
      case 'draft': 
      default: return 'Rascunho';
    }
  };

  const getPlatformEmoji = (platform: string) => {
    const platformEmojis: Record<string, string> = {
      'YouTube': 'üì∫',
      'Instagram': 'üì∑',
      'TikTok': 'üéµ',
      'Facebook': 'üë•',
      'LinkedIn': 'üíº',
      'Twitter': 'üê¶',
    };
    return platformEmojis[platform] || 'üì±';
  };

  const truncateContent = (content: string, maxLength: number = 150) => {
    if (!content || content.length <= maxLength) return content;
    return content.substring(0, maxLength).trim() + '...';
  };

  const handleActionClick = (action: string, e?: React.MouseEvent) => {
    if (e) {
      e.stopPropagation();
    }
    setShowMenu(false);
    onAction(action, project);
  };

  const handleCardClick = () => {
    if (allowSelection) {
      onAction('select', project);
    } else {
      onAction('view', project);
    }
  };

  const cardClasses = cn(
    "group relative transition-all duration-200 cursor-pointer",
    "hover:shadow-lg hover:shadow-primary/10 hover:-translate-y-1",
    "border border-border/50 bg-background/60 backdrop-blur-sm",
    view === 'grid' ? "p-4 rounded-lg" : "p-3 flex items-center gap-4 rounded-md",
    isSelected && "ring-2 ring-primary ring-offset-2",
    "hover:border-primary/30",
    className
  );

  // Renderiza√ß√£o em Grid
  if (view === 'grid') {
    return (
      <Card 
        className={cardClasses}
        onClick={handleCardClick}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
      >
        {/* Header do Card */}
        <div className="flex items-start justify-between mb-3">
          <div className="flex-1 min-w-0">
            <div className="flex items-center gap-2 mb-1">
              <span className="text-lg">{getPlatformEmoji(project.formData.platform)}</span>
              {project.isFavorite && (
                <Star className="h-4 w-4 text-yellow-500 fill-current" />
              )}
              {project.isShared && (
                <ExternalLink className="h-3 w-3 text-blue-500" />
              )}
            </div>
            <h3 className="font-semibold text-foreground line-clamp-2 leading-tight">
              {project.title || project.formData.subject}
            </h3>
          </div>

          {/* Menu de A√ß√µes */}
          {showActions && (
            <div className="relative ml-2">
              <Button
                variant="ghost"
                size="icon"
                className={cn(
                  "h-8 w-8 opacity-0 group-hover:opacity-100 transition-opacity",
                  showMenu && "opacity-100"
                )}
                onClick={(e) => {
                  e.stopPropagation();
                  setShowMenu(!showMenu);
                }}
              >
                <MoreVertical className="h-4 w-4" />
              </Button>

              {showMenu && (
                <div className="absolute right-0 top-8 bg-background rounded-md shadow-lg border border-border z-20 min-w-[180px]">
                  <div className="py-1">
                    <ActionButton 
                      icon={Edit} 
                      onClick={(e) => handleActionClick('edit', e)}
                    >
                      Editar
                    </ActionButton>
                    <ActionButton 
                      icon={Eye} 
                      onClick={(e) => handleActionClick('view', e)}
                    >
                      Visualizar
                    </ActionButton>
                    <ActionButton 
                      icon={Copy} 
                      onClick={(e) => handleActionClick('duplicate', e)}
                    >
                      Duplicar
                    </ActionButton>
                    <ActionButton 
                      icon={Share} 
                      onClick={(e) => handleActionClick('share', e)}
                    >
                      Compartilhar
                    </ActionButton>
                    <ActionButton 
                      icon={Download} 
                      onClick={(e) => handleActionClick('export', e)}
                    >
                      Exportar
                    </ActionButton>
                    <ActionButton 
                      icon={project.isFavorite ? StarOff : Star} 
                      onClick={(e) => handleActionClick('toggleFavorite', e)}
                    >
                      {project.isFavorite ? 'Desfavoritar' : 'Favoritar'}
                    </ActionButton>
                    <ActionButton 
                      icon={FolderOpen} 
                      onClick={(e) => handleActionClick('move', e)}
                    >
                      Mover para pasta
                    </ActionButton>
                    <Separator className="my-1" />
                    <ActionButton 
                      icon={Trash2} 
                      onClick={(e) => handleActionClick('delete', e)}
                      className="text-destructive hover:text-destructive hover:bg-destructive/10"
                    >
                      Excluir
                    </ActionButton>
                  </div>
                </div>
              )}
            </div>
          )}
        </div>

        {/* Pr√©via do Conte√∫do */}
        {project.content && (
          <div className="mb-3">
            <p className="text-sm text-muted-foreground line-clamp-3 leading-relaxed">
              {truncateContent(project.content)}
            </p>
          </div>
        )}

        {/* Tags */}
        {project.tags && project.tags.length > 0 && (
          <div className="flex flex-wrap gap-1 mb-3">
            {project.tags.slice(0, 3).map(tag => (
              <Badge key={tag} variant="outline" className="text-xs">
                {tag}
              </Badge>
            ))}
            {project.tags.length > 3 && (
              <Badge variant="outline" className="text-xs">
                +{project.tags.length - 3}
              </Badge>
            )}
          </div>
        )}

        {/* M√©tricas */}
        <div className="flex flex-wrap gap-2 mb-3">
          <Badge variant="outline" className="text-xs flex items-center gap-1">
            <Clock className="h-3 w-3" />
            {project.formData.duration}s
          </Badge>
          <Badge variant="secondary" className="text-xs">
            {project.formData.platform}
          </Badge>
          <Badge 
            className={cn("text-xs border", getStatusColor(project.status || 'draft'))}
          >
            {getStatusLabel(project.status || 'draft')}
          </Badge>
          {project.wordCount && (
            <Badge variant="outline" className="text-xs flex items-center gap-1">
              <FileText className="h-3 w-3" />
              {project.wordCount} palavras
            </Badge>
          )}
        </div>

        {/* Footer */}
        <div className="flex items-center justify-between text-xs text-muted-foreground pt-2 border-t border-border/50">
          <div className="flex items-center gap-1">
            <Calendar className="h-3 w-3" />
            <span>{formatDate(project.updatedAt || project.createdAt)}</span>
          </div>
          <div className="flex items-center gap-3">
            {project.viewCount > 0 && (
              <span className="flex items-center gap-1">
                <Eye className="h-3 w-3" />
                {project.viewCount}
              </span>
            )}
            {project.editCount > 0 && (
              <span className="flex items-center gap-1">
                <Edit className="h-3 w-3" />
                {project.editCount}
              </span>
            )}
          </div>
        </div>
      </Card>
    );
  }

  // Renderiza√ß√£o em Lista
  return (
    <Card 
      className={cardClasses}
      onClick={handleCardClick}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Checkbox de sele√ß√£o */}
      {allowSelection && (
        <input
          type="checkbox"
          checked={isSelected}
          onChange={() => onAction('select', project)}
          className="mr-3 rounded"
          onClick={(e) => e.stopPropagation()}
        />
      )}

      {/* √çcone da plataforma */}
      <div className="flex items-center gap-2 min-w-0">
        <span className="text-lg">{getPlatformEmoji(project.formData.platform)}</span>
        {project.isFavorite && (
          <Star className="h-4 w-4 text-yellow-500 fill-current" />
        )}
      </div>

      {/* Conte√∫do principal */}
      <div className="flex-1 min-w-0 px-3">
        <div className="flex items-center gap-2 mb-1">
          <h3 className="font-semibold text-foreground truncate">
            {project.title || project.formData.subject}
          </h3>
          {project.isShared && (
            <ExternalLink className="h-3 w-3 text-blue-500 flex-shrink-0" />
          )}
        </div>

        {project.content && (
          <p className="text-sm text-muted-foreground line-clamp-1">
            {truncateContent(project.content, 100)}
          </p>
        )}
      </div>

      {/* Tags (vers√£o compacta) */}
      <div className="hidden md:flex items-center gap-1 min-w-0">
        {project.tags?.slice(0, 2).map(tag => (
          <Badge key={tag} variant="outline" className="text-xs">
            {tag}
          </Badge>
        ))}
        {project.tags && project.tags.length > 2 && (
          <span className="text-xs text-muted-foreground">+{project.tags.length - 2}</span>
        )}
      </div>

      {/* M√©tricas compactas */}
      <div className="flex items-center gap-3 text-xs text-muted-foreground min-w-0">
        <span className="flex items-center gap-1">
          <Clock className="h-3 w-3" />
          {project.formData.duration}s
        </span>
        {project.wordCount && (
          <span className="flex items-center gap-1">
            <FileText className="h-3 w-3" />
            {project.wordCount}
          </span>
        )}
        <Badge 
          className={cn("text-xs border", getStatusColor(project.status || 'draft'))}
        >
          {getStatusLabel(project.status || 'draft')}
        </Badge>
      </div>

      {/* Data */}
      <div className="hidden lg:flex items-center gap-1 text-xs text-muted-foreground min-w-0">
        <Calendar className="h-3 w-3" />
        <span>{formatDate(project.updatedAt || project.createdAt)}</span>
      </div>

      {/* Menu de a√ß√µes */}
      {showActions && (
        <div className="relative">
          <Button
            variant="ghost"
            size="icon"
            className={cn(
              "h-8 w-8 opacity-0 group-hover:opacity-100 transition-opacity",
              showMenu && "opacity-100"
            )}
            onClick={(e) => {
              e.stopPropagation();
              setShowMenu(!showMenu);
            }}
          >
            <MoreVertical className="h-4 w-4" />
          </Button>

          {showMenu && (
            <div className="absolute right-0 top-8 bg-background rounded-md shadow-lg border border-border z-20 min-w-[180px]">
              <div className="py-1">
                <ActionButton 
                  icon={Edit} 
                  onClick={(e) => handleActionClick('edit', e)}
                >
                  Editar
                </ActionButton>
                <ActionButton 
                  icon={Copy} 
                  onClick={(e) => handleActionClick('duplicate', e)}
                >
                  Duplicar
                </ActionButton>
                <ActionButton 
                  icon={Share} 
                  onClick={(e) => handleActionClick('share', e)}
                >
                  Compartilhar
                </ActionButton>
                <ActionButton 
                  icon={project.isFavorite ? StarOff : Star} 
                  onClick={(e) => handleActionClick('toggleFavorite', e)}
                >
                  {project.isFavorite ? 'Desfavoritar' : 'Favoritar'}
                </ActionButton>
                <Separator className="my-1" />
                <ActionButton 
                  icon={Trash2} 
                  onClick={(e) => handleActionClick('delete', e)}
                  className="text-destructive hover:text-destructive hover:bg-destructive/10"
                >
                  Excluir
                </ActionButton>
              </div>
            </div>
          )}
        </div>
      )}
    </Card>
  );
};

// Componente auxiliar para itens do menu
interface ActionButtonProps {
  icon: React.ComponentType<{ className?: string }>;
  onClick: (e: React.MouseEvent) => void;
  className?: string;
  children: React.ReactNode;
}

const ActionButton: React.FC<ActionButtonProps> = ({ 
  icon: Icon, 
  onClick, 
  className = "", 
  children 
}) => (
  <button
    className={cn(
      "w-full flex items-center gap-2 px-3 py-2 text-sm text-left",
      "hover:bg-muted/50 transition-colors",
      className
    )}
    onClick={onClick}
  >
    <Icon className="h-4 w-4" />
    {children}
  </button>
);

export default ProjectCard;

