import React, { useState, useEffect, useMemo } from 'react';
import { 
  Tags, 
  Plus, 
  Search, 
  Edit3, 
  Trash2, 
  Check, 
  X, 
  MoreVertical,
  Palette,
  TrendingUp,
  Archive,
  Copy,
  Star,
  Hash,
  Filter,
  Download,
  Upload
} from 'lucide-react';
import { Button } from '../ui/Button';
import { Input } from '../ui/Input';
import { Badge } from '../ui/Badge';
import { 
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from '../ui/Dialog';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from '../ui/DropdownMenu';
import { Textarea } from '../ui/Textarea';
import { Switch } from '../ui/Switch';
import { Checkbox } from '../ui/Checkbox';

import type { Tag, CreateTagData, TagUsageStats } from '../../types/enhanced';
import { tagService } from '../../services/tagService';
import { createLogger } from '../../utils/logger';

const logger = createLogger('TagManager');

interface TagManagerProps {
  userId: string;
  onTagsChange?: (tags: Tag[]) => void;
  showAnalytics?: boolean;
  allowBulkOperations?: boolean;
}

export const TagManager: React.FC<TagManagerProps> = ({
  userId,
  onTagsChange,
  showAnalytics = true,
  allowBulkOperations = true
}) => {
  // Estados principais
  const [tags, setTags] = useState<Tag[]>([]);
  const [usageStats, setUsageStats] = useState<TagUsageStats[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Estados de interface
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedTags, setSelectedTags] = useState<Set<string>>(new Set());
  const [viewMode, setViewMode] = useState<'grid' | 'list'>('grid');
  const [sortBy, setSortBy] = useState<'name' | 'usage' | 'date'>('usage');
  const [filterBy, setFilterBy] = useState<'all' | 'system' | 'user'>('all');

  // Estados de modais
  const [showCreateDialog, setShowCreateDialog] = useState(false);
  const [showAnalyticsDialog, setShowAnalyticsDialog] = useState(false);
  const [editingTag, setEditingTag] = useState<Tag | null>(null);

  // Estados de formul√°rio
  const [newTagName, setNewTagName] = useState('');
  const [newTagColor, setNewTagColor] = useState('#3B82F6');
  const [newTagDescription, setNewTagDescription] = useState('');

  // Cores predefinidas para tags
  const predefinedColors = [
    '#3B82F6', '#10B981', '#F59E0B', '#EF4444', 
    '#8B5CF6', '#06B6D4', '#F97316', '#84CC16',
    '#EC4899', '#6B7280', '#14B8A6', '#A855F7'
  ];

  // Carregar dados iniciais
  useEffect(() => {
    loadTags();
    if (showAnalytics) {
      loadUsageStats();
    }
  }, [userId, showAnalytics]);

  // Filtrar e ordenar tags
  const filteredAndSortedTags = useMemo(() => {
    let filtered = tags.filter(tag => {
      // Filtro de busca
      const matchesSearch = tag.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                          (tag.description?.toLowerCase().includes(searchQuery.toLowerCase()) || false);

      // Filtro por tipo
      const matchesFilter = filterBy === 'all' || 
                           (filterBy === 'system' && tag.isSystem) ||
                           (filterBy === 'user' && !tag.isSystem);

      return matchesSearch && matchesFilter;
    });

    // Ordena√ß√£o
    filtered.sort((a, b) => {
      switch (sortBy) {
        case 'name':
          return a.name.localeCompare(b.name);
        case 'usage':
          return b.usageCount - a.usageCount;
        case 'date':
          return b.updatedAt.toDate().getTime() - a.updatedAt.toDate().getTime();
        default:
          return 0;
      }
    });

    return filtered;
  }, [tags, searchQuery, filterBy, sortBy]);

  const loadTags = async () => {
    try {
      setIsLoading(true);
      setError(null);
      const userTags = await tagService.getUserTags(userId);
      setTags(userTags);
      onTagsChange?.(userTags);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao carregar tags';
      setError(errorMessage);
      logger.error('Erro ao carregar tags', err);
    } finally {
      setIsLoading(false);
    }
  };

  const loadUsageStats = async () => {
    try {
      const stats = await tagService.getTagUsageStats(userId);
      setUsageStats(stats);
    } catch (err) {
      logger.error('Erro ao carregar estat√≠sticas de tags', err);
    }
  };

  const handleCreateTag = async () => {
    if (!newTagName.trim()) return;

    try {
      setIsLoading(true);

      const tagData: CreateTagData = {
        name: newTagName.trim(),
        color: newTagColor,
        description: newTagDescription.trim() || undefined,
        isSystem: false
      };

      await tagService.createTag(userId, tagData);
      await loadTags();

      // Reset form
      setNewTagName('');
      setNewTagColor('#3B82F6');
      setNewTagDescription('');
      setShowCreateDialog(false);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao criar tag';
      setError(errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  const handleUpdateTag = async (tagId: string, updates: Partial<Tag>) => {
    try {
      setIsLoading(true);
      await tagService.updateTag(tagId, updates);
      await loadTags();
      setEditingTag(null);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao atualizar tag';
      setError(errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  const handleDeleteTag = async (tagId: string) => {
    if (!confirm('Tem certeza que deseja excluir esta tag?')) return;

    try {
      setIsLoading(true);
      await tagService.deleteTag(tagId);
      await loadTags();
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao excluir tag';
      setError(errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  const handleBulkDelete = async () => {
    if (selectedTags.size === 0) return;

    const count = selectedTags.size;
    if (!confirm(`Tem certeza que deseja excluir ${count} tag${count > 1 ? 's' : ''}?`)) return;

    try {
      setIsLoading(true);

      for (const tagId of selectedTags) {
        await tagService.deleteTag(tagId);
      }

      await loadTags();
      setSelectedTags(new Set());
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao excluir tags';
      setError(errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  const handleTagSelect = (tagId: string) => {
    const newSelected = new Set(selectedTags);
    if (newSelected.has(tagId)) {
      newSelected.delete(tagId);
    } else {
      newSelected.add(tagId);
    }
    setSelectedTags(newSelected);
  };

  const handleSelectAll = () => {
    if (selectedTags.size === filteredAndSortedTags.length) {
      setSelectedTags(new Set());
    } else {
      setSelectedTags(new Set(filteredAndSortedTags.map(tag => tag.id)));
    }
  };

  const handleCreateDefaultTags = async () => {
    try {
      setIsLoading(true);
      await tagService.createDefaultTags(userId);
      await loadTags();
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao criar tags padr√£o';
      setError(errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  const formatDate = (timestamp: any) => {
    if (!timestamp) return 'Nunca usado';
    const date = timestamp.toDate ? timestamp.toDate() : new Date(timestamp);
    return date.toLocaleDateString('pt-BR');
  };

  return (
    <div className="bg-white border border-gray-200 rounded-lg shadow-sm">
      {/* Header */}
      <div className="p-6 border-b border-gray-200">
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center gap-3">
            <Tags size={24} className="text-blue-600" />
            <div>
              <h2 className="text-xl font-semibold text-gray-900">Gerenciar Tags</h2>
              <p className="text-sm text-gray-600">
                {filteredAndSortedTags.length} de {tags.length} tags
              </p>
            </div>
          </div>

          <div className="flex items-center gap-2">
            {showAnalytics && (
              <Button
                variant="outline"
                size="sm"
                onClick={() => setShowAnalyticsDialog(true)}
              >
                <TrendingUp size={16} className="mr-1" />
                Analytics
              </Button>
            )}

            <Dialog open={showCreateDialog} onOpenChange={setShowCreateDialog}>
              <DialogTrigger asChild>
                <Button size="sm">
                  <Plus size={16} className="mr-1" />
                  Nova Tag
                </Button>
              </DialogTrigger>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>Criar Nova Tag</DialogTitle>
                </DialogHeader>

                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium mb-1">Nome da Tag</label>
                    <Input
                      value={newTagName}
                      onChange={(e) => setNewTagName(e.target.value)}
                      placeholder="Ex: Marketing Digital"
                      maxLength={30}
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium mb-1">Cor</label>
                    <div className="flex gap-2 mb-2">
                      {predefinedColors.map((color) => (
                        <button
                          key={color}
                          className={`w-8 h-8 rounded-full border-2 ${
                            newTagColor === color ? 'border-gray-900' : 'border-gray-300'
                          }`}
                          style={{ backgroundColor: color }}
                          onClick={() => setNewTagColor(color)}
                        />
                      ))}
                    </div>
                    <Input
                      type="color"
                      value={newTagColor}
                      onChange={(e) => setNewTagColor(e.target.value)}
                      className="w-20 h-10"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium mb-1">Descri√ß√£o (opcional)</label>
                    <Textarea
                      value={newTagDescription}
                      onChange={(e) => setNewTagDescription(e.target.value)}
                      placeholder="Descreva quando usar esta tag..."
                      rows={3}
                      maxLength={200}
                    />
                  </div>

                  <div className="flex justify-end gap-2">
                    <Button 
                      variant="outline" 
                      onClick={() => setShowCreateDialog(false)}
                    >
                      Cancelar
                    </Button>
                    <Button 
                      onClick={handleCreateTag}
                      disabled={!newTagName.trim() || isLoading}
                    >
                      <Check size={16} className="mr-1" />
                      Criar Tag
                    </Button>
                  </div>
                </div>
              </DialogContent>
            </Dialog>
          </div>
        </div>

        {/* Search and Filters */}
        <div className="flex flex-col sm:flex-row gap-3">
          <div className="flex-1 relative">
            <Search size={16} className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
            <Input
              placeholder="Buscar tags..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10"
            />
          </div>

          <div className="flex gap-2">
            <select
              value={filterBy}
              onChange={(e) => setFilterBy(e.target.value as any)}
              className="px-3 py-2 border border-gray-300 rounded-md text-sm"
            >
              <option value="all">Todas</option>
              <option value="user">Minhas Tags</option>
              <option value="system">Tags do Sistema</option>
            </select>

            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value as any)}
              className="px-3 py-2 border border-gray-300 rounded-md text-sm"
            >
              <option value="usage">Mais Usadas</option>
              <option value="name">Nome</option>
              <option value="date">Recentes</option>
            </select>

            <Button
              variant="outline"
              size="sm"
              onClick={() => setViewMode(viewMode === 'grid' ? 'list' : 'grid')}
            >
              {viewMode === 'grid' ? 'Lista' : 'Grade'}
            </Button>
          </div>
        </div>

        {/* Bulk Actions */}
        {allowBulkOperations && selectedTags.size > 0 && (
          <div className="mt-3 p-3 bg-blue-50 border border-blue-200 rounded-lg">
            <div className="flex items-center justify-between">
              <span className="text-sm text-blue-800">
                {selectedTags.size} tag{selectedTags.size > 1 ? 's' : ''} selecionada{selectedTags.size > 1 ? 's' : ''}
              </span>
              <div className="flex gap-2">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setSelectedTags(new Set())}
                >
                  Limpar Sele√ß√£o
                </Button>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={handleBulkDelete}
                  className="text-red-600 hover:text-red-700"
                >
                  <Trash2 size={14} className="mr-1" />
                  Excluir
                </Button>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Tags Grid/List */}
      <div className="p-6">
        {error && (
          <div className="mb-4 p-3 bg-red-50 border border-red-200 rounded-lg text-red-700 text-sm">
            {error}
          </div>
        )}

        {isLoading ? (
          <div className="flex items-center justify-center py-12">
            <div className="animate-spin rounded-full h-8 w-8 border-2 border-gray-300 border-t-blue-600"></div>
            <span className="ml-2 text-gray-600">Carregando tags...</span>
          </div>
        ) : filteredAndSortedTags.length === 0 ? (
          <div className="text-center py-12">
            <Tags size={48} className="mx-auto text-gray-400 mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">
              {searchQuery ? 'Nenhuma tag encontrada' : 'Nenhuma tag criada'}
            </h3>
            <p className="text-gray-600 mb-4">
              {searchQuery 
                ? 'Tente ajustar sua busca ou filtros.'
                : 'Crie sua primeira tag ou importe tags padr√£o.'
              }
            </p>
            {!searchQuery && (
              <div className="flex justify-center gap-2">
                <Button
                  variant="outline"
                  onClick={handleCreateDefaultTags}
                >
                  <Star size={16} className="mr-1" />
                  Criar Tags Padr√£o
                </Button>
                <Button onClick={() => setShowCreateDialog(true)}>
                  <Plus size={16} className="mr-1" />
                  Criar Tag
                </Button>
              </div>
            )}
          </div>
        ) : (
          <>
            {/* Select All Checkbox */}
            {allowBulkOperations && (
              <div className="mb-4 flex items-center gap-2">
                <Checkbox
                  checked={selectedTags.size === filteredAndSortedTags.length}
                  indeterminate={selectedTags.size > 0 && selectedTags.size < filteredAndSortedTags.length}
                  onCheckedChange={handleSelectAll}
                />
                <span className="text-sm text-gray-600">
                  Selecionar todas ({filteredAndSortedTags.length})
                </span>
              </div>
            )}

            {/* Tags Grid/List */}
            <div className={viewMode === 'grid' 
              ? 'grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4'
              : 'space-y-2'
            }>
              {filteredAndSortedTags.map((tag) => (
                <div
                  key={tag.id}
                  className={`relative border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow ${
                    selectedTags.has(tag.id) ? 'ring-2 ring-blue-500 bg-blue-50' : 'bg-white'
                  }`}
                >
                  {/* Selection Checkbox */}
                  {allowBulkOperations && (
                    <Checkbox
                      checked={selectedTags.has(tag.id)}
                      onCheckedChange={() => handleTagSelect(tag.id)}
                      className="absolute top-3 left-3"
                    />
                  )}

                  {/* Tag Content */}
                  <div className={allowBulkOperations ? 'ml-8' : ''}>
                    <div className="flex items-center justify-between mb-2">
                      <div className="flex items-center gap-2">
                        <div
                          className="w-4 h-4 rounded-full border"
                          style={{ backgroundColor: tag.color }}
                        />
                        <span className="font-medium text-gray-900">{tag.name}</span>
                        {tag.isSystem && (
                          <Badge variant="outline" className="text-xs">
                            Sistema
                          </Badge>
                        )}
                      </div>

                      <DropdownMenu>
                        <DropdownMenuTrigger asChild>
                          <Button variant="ghost" size="sm">
                            <MoreVertical size={14} />
                          </Button>
                        </DropdownMenuTrigger>
                        <DropdownMenuContent>
                          <DropdownMenuItem 
                            onClick={() => setEditingTag(tag)}
                          >
                            <Edit3 size={14} className="mr-2" />
                            Editar
                          </DropdownMenuItem>
                          <DropdownMenuItem>
                            <Copy size={14} className="mr-2" />
                            Duplicar
                          </DropdownMenuItem>
                          <DropdownMenuSeparator />
                          <DropdownMenuItem 
                            onClick={() => handleDeleteTag(tag.id)}
                            className="text-red-600"
                            disabled={tag.isSystem}
                          >
                            <Trash2 size={14} className="mr-2" />
                            Excluir
                          </DropdownMenuItem>
                        </DropdownMenuContent>
                      </DropdownMenu>
                    </div>

                    {tag.description && (
                      <p className="text-sm text-gray-600 mb-2">
                        {tag.description}
                      </p>
                    )}

                    <div className="flex items-center justify-between text-xs text-gray-500">
                      <span>{tag.usageCount} uso{tag.usageCount !== 1 ? 's' : ''}</span>
                      <span>√öltima: {formatDate(tag.lastUsedAt)}</span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </>
        )}
      </div>

      {/* Analytics Modal */}
      {showAnalytics && (
        <Dialog open={showAnalyticsDialog} onOpenChange={setShowAnalyticsDialog}>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Analytics de Tags</DialogTitle>
            </DialogHeader>

            <div className="space-y-6">
              {/* Usage Stats */}
              <div>
                <h3 className="font-medium mb-3">Tags Mais Usadas</h3>
                <div className="space-y-2">
                  {usageStats.slice(0, 10).map((stat) => (
                    <div key={stat.tagId} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                      <div className="flex items-center gap-2">
                        <Hash size={14} className="text-gray-400" />
                        <span className="font-medium">{stat.tagName}</span>
                      </div>
                      <div className="flex items-center gap-3">
                        <div className="w-24 bg-gray-200 rounded-full h-2">
                          <div 
                            className="bg-blue-600 h-2 rounded-full"
                            style={{ width: `${stat.percentage}%` }}
                          />
                        </div>
                        <span className="text-sm text-gray-600 w-12 text-right">
                          {stat.usageCount}
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      )}

      {/* Edit Tag Modal */}
      {editingTag && (
        <Dialog open={!!editingTag} onOpenChange={() => setEditingTag(null)}>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Editar Tag</DialogTitle>
            </DialogHeader>

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium mb-1">Nome da Tag</label>
                <Input
                  value={editingTag.name}
                  onChange={(e) => setEditingTag({...editingTag, name: e.target.value})}
                  maxLength={30}
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-1">Cor</label>
                <div className="flex gap-2 mb-2">
                  {predefinedColors.map((color) => (
                    <button
                      key={color}
                      className={`w-8 h-8 rounded-full border-2 ${
                        editingTag.color === color ? 'border-gray-900' : 'border-gray-300'
                      }`}
                      style={{ backgroundColor: color }}
                      onClick={() => setEditingTag({...editingTag, color})}
                    />
                  ))}
                </div>
                <Input
                  type="color"
                  value={editingTag.color}
                  onChange={(e) => setEditingTag({...editingTag, color: e.target.value})}
                  className="w-20 h-10"
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-1">Descri√ß√£o</label>
                <Textarea
                  value={editingTag.description || ''}
                  onChange={(e) => setEditingTag({...editingTag, description: e.target.value})}
                  rows={3}
                  maxLength={200}
                />
              </div>

              <div className="flex justify-end gap-2">
                <Button 
                  variant="outline" 
                  onClick={() => setEditingTag(null)}
                >
                  Cancelar
                </Button>
                <Button 
                  onClick={() => handleUpdateTag(editingTag.id, {
                    name: editingTag.name,
                    color: editingTag.color,
                    description: editingTag.description
                  })}
                  disabled={isLoading}
                >
                  <Check size={16} className="mr-1" />
                  Salvar
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      )}
    </div>
  );
}; 
export default TagManager;

import React, { useState, useRef, useEffect, useCallback } from 'react';
import { Button } from '../ui/Button';
import { Icons } from '../ui/Icons';
import { Badge } from '../ui/Badge';
import { LoadingSpinner } from '../ui/LoadingSpinner';
import { AIRefinementModal } from './AIRefinementModal';
import { VersionHistoryModal } from './VersionHistoryModal';
import { ComparisonModal } from './ComparisonModal';
import { AIEditorService } from '../../services/aiEditorService';
import { VersioningService } from '../../services/versioningService';
import type { 
  TextSelection, 
  AISuggestion, 
  ScriptVersion,
  EditorUIState,
  EditorConfig,
  EditorCallbacks
} from '../../types';

interface AdvancedTextEditorProps {
  projectId: string;
  userId: string;
  initialContent: string;
  config?: Partial<EditorConfig>;
  callbacks?: Partial<EditorCallbacks>;
  onContentChange?: (content: string) => void;
  onSelectionChange?: (selection: TextSelection | null) => void;
}

export const AdvancedTextEditor: React.FC<AdvancedTextEditorProps> = ({
  projectId,
  userId,
  initialContent,
  config = {},
  callbacks = {},
  onContentChange,
  onSelectionChange
}) => {
  // **ESTADOS PRINCIPAIS**
  const [content, setContent] = useState(initialContent);
  const [uiState, setUIState] = useState<EditorUIState>({
    currentSelection: null,
    activeModal: null,
    showSuggestions: false,
    showVersionHistory: false,
    comparisonMode: false,
    highlightedChanges: [],
    pendingAIRequests: [],
    isProcessing: false,
    lastSaved: null,
    hasUnsavedChanges: false
  });

  // **ESTADOS DE FUNCIONALIDADES**
  const [suggestions, setSuggestions] = useState<AISuggestion[]>([]);
  const [currentVersion, setCurrentVersion] = useState<ScriptVersion | null>(null);
  const [versions, setVersions] = useState<ScriptVersion[]>([]);
  const [autoSaveEnabled, setAutoSaveEnabled] = useState(config.preferences?.autoSave ?? true);

  // **REFS**
  const textareaRef = useRef<HTMLTextAreaElement>(null);
  const selectionTimeoutRef = useRef<NodeJS.Timeout>();
  const autoSaveRef = useRef<(() => void) | null>(null);

  // **CONFIGURA√á√ïES**
  const editorConfig = {
    preferences: {
      autoSave: true,
      autoSaveInterval: 30,
      aiSuggestionsEnabled: true,
      showVersionHistory: true,
      highlightChanges: true,
      comparisonMode: 'side-by-side' as const,
      aiProvider: 'gemini' as const,
      sugestionTypes: ['improve', 'clarity', 'engagement', 'grammar'] as const
    },
    theme: {
      fontSize: 16,
      lineHeight: 1.6,
      wordWrap: true,
      showLineNumbers: false,
      highlightCurrentLine: true
    },
    ...config
  };

  // **INICIALIZA√á√ÉO**
  useEffect(() => {
    loadVersions();
    if (autoSaveEnabled) {
      enableAutoSave();
    }

    return () => {
      if (autoSaveRef.current) {
        autoSaveRef.current();
      }
    };
  }, [projectId, autoSaveEnabled]);

  // **CARREGAMENTO DE DADOS**
  const loadVersions = async () => {
    try {
      const [projectVersions, current] = await Promise.all([
        VersioningService.getProjectVersions(projectId, 20),
        VersioningService.getCurrentVersion(projectId)
      ]);

      setVersions(projectVersions);
      setCurrentVersion(current);
    } catch (error) {
      console.error('Erro ao carregar vers√µes:', error);
    }
  };

  // **AUTO-SAVE**
  const enableAutoSave = useCallback(() => {
    if (autoSaveRef.current) {
      autoSaveRef.current();
    }

    VersioningService.enableAutoSave(
      projectId,
      userId,
      () => content
    ).then(stopAutoSave => {
      autoSaveRef.current = stopAutoSave;
    });
  }, [projectId, userId, content]);

  // **SELE√á√ÉO DE TEXTO**
  const handleTextSelection = useCallback(() => {
    if (!textareaRef.current) return;

    const textarea = textareaRef.current;
    const start = textarea.selectionStart;
    const end = textarea.selectionEnd;

    if (start === end) {
      // Sem sele√ß√£o
      setUIState(prev => ({ ...prev, currentSelection: null }));
      onSelectionChange?.(null);
      return;
    }

    const selectedText = content.substring(start, end);
    const context = AIEditorService.extractContext(content, {
      id: '',
      startIndex: start,
      endIndex: end,
      selectedText,
      context: '',
      timestamp: new Date() as any,
      userId
    });

    const selection: TextSelection = {
      id: `selection_${Date.now()}`,
      startIndex: start,
      endIndex: end,
      selectedText,
      context,
      timestamp: new Date() as any,
      userId
    };

    setUIState(prev => ({ ...prev, currentSelection: selection }));
    onSelectionChange?.(selection);
    callbacks.onTextSelect?.(selection);

    // Buscar sugest√µes contextuais ap√≥s um delay
    if (selectionTimeoutRef.current) {
      clearTimeout(selectionTimeoutRef.current);
    }

    selectionTimeoutRef.current = setTimeout(() => {
      if (editorConfig.preferences.aiSuggestionsEnabled && selectedText.length > 10) {
        getContextualSuggestions(selection);
      }
    }, 1000);

  }, [content, userId, callbacks, onSelectionChange, editorConfig.preferences.aiSuggestionsEnabled]);

  // **SUGEST√ïES CONTEXTUAIS**
  const getContextualSuggestions = async (selection: TextSelection) => {
    try {
      setUIState(prev => ({ ...prev, isProcessing: true }));

      const contextualSuggestions = await AIEditorService.getContextualSuggestions(
        selection,
        content,
        {
          platform: 'YouTube', // Seria din√¢mico baseado no projeto
          audience: 'geral',
          tone: 'casual'
        }
      );

      setUIState(prev => ({ 
        ...prev, 
        isProcessing: false,
        showSuggestions: contextualSuggestions.length > 0
      }));

    } catch (error) {
      console.error('Erro ao obter sugest√µes contextuais:', error);
      setUIState(prev => ({ ...prev, isProcessing: false }));
    }
  };

  // **REFINAMENTO COM IA**
  const requestAIRefinement = async (refinementType: string, instructions = '') => {
    if (!uiState.currentSelection) return;

    try {
      setUIState(prev => ({ 
        ...prev, 
        isProcessing: true,
        pendingAIRequests: [...prev.pendingAIRequests, refinementType]
      }));

      const request = {
        id: `request_${Date.now()}`,
        projectId,
        userId,
        originalText: content,
        selectedText: uiState.currentSelection.selectedText,
        selectionStart: uiState.currentSelection.startIndex,
        selectionEnd: uiState.currentSelection.endIndex,
        refinementType: refinementType as any,
        userInstructions: instructions,
        context: {
          platform: 'YouTube',
          audience: 'geral',
          tone: 'casual',
          duration: 300
        },
        timestamp: new Date() as any,
        status: 'pending' as const
      };

      const aiSuggestions = await AIEditorService.refineText(request);
      setSuggestions(prev => [...prev, ...aiSuggestions]);

      setUIState(prev => ({ 
        ...prev, 
        isProcessing: false,
        pendingAIRequests: prev.pendingAIRequests.filter(r => r !== refinementType),
        showSuggestions: true
      }));

      callbacks.onAIRequest?.(request);

    } catch (error) {
      console.error('Erro no refinamento de IA:', error);
      setUIState(prev => ({ 
        ...prev, 
        isProcessing: false,
        pendingAIRequests: prev.pendingAIRequests.filter(r => r !== refinementType)
      }));
    }
  };

  // **APLICAR SUGEST√ÉO**
  const applySuggestion = async (suggestion: AISuggestion) => {
    if (!uiState.currentSelection) return;

    try {
      const newContent = 
        content.substring(0, uiState.currentSelection.startIndex) +
        suggestion.suggestedText +
        content.substring(uiState.currentSelection.endIndex);

      setContent(newContent);
      setUIState(prev => ({ 
        ...prev, 
        hasUnsavedChanges: true,
        currentSelection: null
      }));

      onContentChange?.(newContent);
      callbacks.onSuggestionAccept?.(suggestion);

      // Remover sugest√£o da lista
      setSuggestions(prev => prev.filter(s => s.id !== suggestion.id));

    } catch (error) {
      console.error('Erro ao aplicar sugest√£o:', error);
    }
  };

  // **SALVAR VERS√ÉO**
  const saveVersion = async (comment = '') => {
    try {
      setUIState(prev => ({ ...prev, isProcessing: true }));

      const version = await VersioningService.createVersion(
        projectId,
        userId,
        content,
        comment,
        false,
        suggestions.map(s => s.id)
      );

      setCurrentVersion(version);
      setVersions(prev => [version, ...prev]);
      setUIState(prev => ({ 
        ...prev, 
        hasUnsavedChanges: false,
        lastSaved: new Date() as any,
        isProcessing: false
      }));

      callbacks.onVersionSave?.(version);

    } catch (error) {
      console.error('Erro ao salvar vers√£o:', error);
      setUIState(prev => ({ ...prev, isProcessing: false }));
    }
  };

  // **MUDAN√áAS NO CONTE√öDO**
  const handleContentChange = (newContent: string) => {
    setContent(newContent);
    setUIState(prev => ({ ...prev, hasUnsavedChanges: true }));
    onContentChange?.(newContent);
  };

  // **ATALHOS DO TECLADO**
  const handleKeyDown = (e: React.KeyboardEvent) => {
    // Ctrl+S para salvar
    if (e.ctrlKey && e.key === 's') {
      e.preventDefault();
      saveVersion('Salvamento manual');
    }

    // Ctrl+Z para undo (seria implementado com hist√≥rico local)
    if (e.ctrlKey && e.key === 'z') {
      e.preventDefault();
      // Implementar undo
    }

    // Ctrl+Shift+I para refinamento inteligente
    if (e.ctrlKey && e.shiftKey && e.key === 'I') {
      e.preventDefault();
      if (uiState.currentSelection) {
        requestAIRefinement('improve');
      }
    }
  };

  return (
    <div className="advanced-text-editor">
      {/* **TOOLBAR** */}
      <div className="editor-toolbar flex items-center justify-between bg-white dark:bg-gray-800 border-b border-gray-200 dark:border-gray-700 p-4">
        <div className="flex items-center space-x-2">
          {/* Bot√µes de refinamento r√°pido */}
          {uiState.currentSelection && (
            <div className="flex items-center space-x-2 mr-4 p-2 bg-blue-50 dark:bg-blue-900/20 rounded-lg">
              <span className="text-sm text-blue-600 dark:text-blue-400">
                {uiState.currentSelection.selectedText.length} caracteres selecionados
              </span>

              <Button
                size="sm"
                variant="outline"
                onClick={() => requestAIRefinement('improve')}
                disabled={uiState.isProcessing}
                className="h-8"
              >
                <Icons.Sparkles className="w-4 h-4 mr-1" />
                Melhorar
              </Button>

              <Button
                size="sm"
                variant="outline"
                onClick={() => requestAIRefinement('clarity')}
                disabled={uiState.isProcessing}
                className="h-8"
              >
                <Icons.Eye className="w-4 h-4 mr-1" />
                Clareza
              </Button>

              <Button
                size="sm"
                variant="outline"
                onClick={() => requestAIRefinement('engagement')}
                disabled={uiState.isProcessing}
                className="h-8"
              >
                <Icons.Heart className="w-4 h-4 mr-1" />
                Engajamento
              </Button>

              <Button
                size="sm"
                variant="outline"
                onClick={() => setUIState(prev => ({ ...prev, activeModal: 'refinement' }))}
                className="h-8"
              >
                <Icons.Settings className="w-4 h-4 mr-1" />
                Avan√ßado
              </Button>
            </div>
          )}

          {/* Indicadores de status */}
          {uiState.isProcessing && (
            <div className="flex items-center space-x-2">
              <LoadingSpinner size="sm" />
              <span className="text-sm text-gray-600 dark:text-gray-400">
                Processando IA...
              </span>
            </div>
          )}

          {uiState.hasUnsavedChanges && (
            <Badge variant="warning" className="text-xs">
              <Icons.AlertCircle className="w-3 h-3 mr-1" />
              N√£o salvo
            </Badge>
          )}
        </div>

        <div className="flex items-center space-x-2">
          {/* Estat√≠sticas */}
          <div className="text-sm text-gray-600 dark:text-gray-400 mr-4">
            {content.split(/\s+/).filter(Boolean).length} palavras
          </div>

          {/* Controles de vers√£o */}
          <Button
            size="sm"
            variant="outline"
            onClick={() => setUIState(prev => ({ ...prev, activeModal: 'history' }))}
          >
            <Icons.History className="w-4 h-4 mr-1" />
            Hist√≥rico
          </Button>

          <Button
            size="sm"
            variant="outline"
            onClick={() => setUIState(prev => ({ ...prev, activeModal: 'comparison' }))}
            disabled={versions.length < 2}
          >
            <Icons.GitCompare className="w-4 h-4 mr-1" />
            Comparar
          </Button>

          <Button
            size="sm"
            onClick={() => saveVersion('Salvamento manual')}
            disabled={uiState.isProcessing || !uiState.hasUnsavedChanges}
          >
            <Icons.Save className="w-4 h-4 mr-1" />
            Salvar
          </Button>
        </div>
      </div>

      {/* **√ÅREA DE EDI√á√ÉO** */}
      <div className="editor-content flex-1 relative">
        <textarea
          ref={textareaRef}
          value={content}
          onChange={(e) => handleContentChange(e.target.value)}
          onSelect={handleTextSelection}
          onKeyDown={handleKeyDown}
          className={`
            w-full h-full min-h-[500px] p-6 border-none outline-none resize-none
            bg-white dark:bg-gray-900 text-gray-900 dark:text-gray-100
            font-mono leading-relaxed
          `}
          style={{
            fontSize: `${editorConfig.theme.fontSize}px`,
            lineHeight: editorConfig.theme.lineHeight
          }}
          placeholder="Comece a escrever seu roteiro aqui... Selecione texto para ver sugest√µes de IA."
        />

        {/* **PAINEL DE SUGEST√ïES** */}
        {uiState.showSuggestions && suggestions.length > 0 && (
          <div className="absolute top-4 right-4 w-80 bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 rounded-lg shadow-lg p-4">
            <div className="flex items-center justify-between mb-3">
              <h3 className="text-sm font-medium text-gray-900 dark:text-gray-100">
                Sugest√µes de IA
              </h3>
              <Button
                size="sm"
                variant="ghost"
                onClick={() => setUIState(prev => ({ ...prev, showSuggestions: false }))}
              >
                <Icons.X className="w-4 h-4" />
              </Button>
            </div>

            <div className="space-y-3">
              {suggestions.slice(0, 3).map((suggestion) => (
                <div key={suggestion.id} className="border border-gray-100 dark:border-gray-700 rounded-lg p-3">
                  <div className="text-sm text-gray-700 dark:text-gray-300 mb-2">
                    {suggestion.suggestedText}
                  </div>

                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-2">
                      <Badge variant="secondary" className="text-xs">
                        {Math.round(suggestion.confidence * 100)}% confian√ßa
                      </Badge>
                    </div>

                    <div className="flex space-x-1">
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => applySuggestion(suggestion)}
                        className="h-7 text-xs"
                      >
                        Aplicar
                      </Button>
                      <Button
                        size="sm"
                        variant="ghost"
                        onClick={() => {
                          setSuggestions(prev => prev.filter(s => s.id !== suggestion.id));
                          callbacks.onSuggestionReject?.(suggestion);
                        }}
                        className="h-7 text-xs"
                      >
                        <Icons.X className="w-3 h-3" />
                      </Button>
                    </div>
                  </div>

                  {suggestion.explanation && (
                    <p className="text-xs text-gray-500 dark:text-gray-400 mt-2">
                      {suggestion.explanation}
                    </p>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      {/* **MODAIS** */}
      {uiState.activeModal === 'refinement' && uiState.currentSelection && (
        <AIRefinementModal
          isOpen={true}
          onClose={() => setUIState(prev => ({ ...prev, activeModal: null }))}
          selection={uiState.currentSelection}
          onRefine={(type, instructions) => {
            requestAIRefinement(type, instructions);
            setUIState(prev => ({ ...prev, activeModal: null }));
          }}
        />
      )}

      {uiState.activeModal === 'history' && (
        <VersionHistoryModal
          isOpen={true}
          onClose={() => setUIState(prev => ({ ...prev, activeModal: null }))}
          projectId={projectId}
          versions={versions}
          currentVersion={currentVersion}
          onRestore={(version) => {
            setContent(version.content);
            setUIState(prev => ({ ...prev, activeModal: null, hasUnsavedChanges: true }));
            callbacks.onVersionRestore?.(version);
          }}
          onCompare={(v1, v2) => {
            setUIState(prev => ({ ...prev, activeModal: 'comparison' }));
            callbacks.onCompare?.(v1, v2);
          }}
        />
      )}

      {uiState.activeModal === 'comparison' && (
        <ComparisonModal
          isOpen={true}
          onClose={() => setUIState(prev => ({ ...prev, activeModal: null }))}
          projectId={projectId}
          versions={versions}
          onSelectVersions={(v1, v2) => {
            callbacks.onCompare?.(v1, v2);
          }}
        />
      )}
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { Dialog } from '../ui/Dialog';
import { Button } from '../ui/Button';
import { Icons } from '../ui/Icons';
import { Badge } from '../ui/Badge';
import { Select } from '../ui/Select';
import { LoadingSpinner } from '../ui/LoadingSpinner';
import { Tabs } from '../ui/Tabs';
import { VersioningService } from '../../services/versioningService';
import { format } from 'date-fns';
import { ptBR } from 'date-fns/locale';
import type { ScriptVersion, ComparisonData, DiffResult } from '../../types';

interface ComparisonModalProps {
  isOpen: boolean;
  onClose: () => void;
  projectId: string;
  versions: ScriptVersion[];
  onSelectVersions: (version1: string, version2: string) => void;
}

export const ComparisonModal: React.FC<ComparisonModalProps> = ({
  isOpen,
  onClose,
  projectId,
  versions,
  onSelectVersions
}) => {
  // **ESTADOS**
  const [selectedVersion1, setSelectedVersion1] = useState<string>('');
  const [selectedVersion2, setSelectedVersion2] = useState<string>('');
  const [comparisonData, setComparisonData] = useState<ComparisonData | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [viewMode, setViewMode] = useState<'side-by-side' | 'inline'>('side-by-side');
  const [showMetrics, setShowMetrics] = useState(true);

  // **VERS√ïES ORDENADAS**
  const sortedVersions = [...versions].sort((a, b) => b.versionNumber - a.versionNumber);

  // **COMPARAR VERS√ïES**
  const handleCompare = async () => {
    if (!selectedVersion1 || !selectedVersion2) return;

    setIsLoading(true);
    try {
      const comparison = await VersioningService.compareVersions(
        selectedVersion1,
        selectedVersion2
      );
      setComparisonData(comparison);
      onSelectVersions(selectedVersion1, selectedVersion2);
    } catch (error) {
      console.error('Erro ao comparar vers√µes:', error);
    } finally {
      setIsLoading(false);
    }
  };

  // **AUTO-SELECIONAR VERS√ïES**
  useEffect(() => {
    if (versions.length >= 2 && !selectedVersion1 && !selectedVersion2) {
      // Selecionar automaticamente as duas vers√µes mais recentes
      setSelectedVersion1(sortedVersions[0]?.id || '');
      setSelectedVersion2(sortedVersions[1]?.id || '');
    }
  }, [versions, sortedVersions]);

  // **AUTO-COMPARAR QUANDO VERS√ïES MUDAM**
  useEffect(() => {
    if (selectedVersion1 && selectedVersion2 && selectedVersion1 !== selectedVersion2) {
      handleCompare();
    }
  }, [selectedVersion1, selectedVersion2]);

  // **FORMATA√á√ÉO DE DATA**
  const formatDate = (timestamp: any) => {
    const date = timestamp?.toDate ? timestamp.toDate() : new Date(timestamp);
    return format(date, "dd/MM/yyyy HH:mm", { locale: ptBR });
  };

  // **RENDERIZAR DIFF INLINE**
  const renderInlineDiff = (diff: DiffResult[]) => {
    return (
      <div className="space-y-2">
        {diff.map((change, index) => (
          <div key={`diff-${change.type}-${change.startIndex}-${index}`} className="flex">
            <div className="w-12 text-xs text-gray-500 dark:text-gray-400 mr-4 text-right">
              {index + 1}
            </div>
            <div 
              className={`flex-1 px-3 py-1 rounded ${
                change.type === 'added' ? 'bg-green-50 dark:bg-green-900/20 text-green-800 dark:text-green-200' :
                change.type === 'removed' ? 'bg-red-50 dark:bg-red-900/20 text-red-800 dark:text-red-200' :
                change.type === 'modified' ? 'bg-yellow-50 dark:bg-yellow-900/20 text-yellow-800 dark:text-yellow-200' :
                'bg-gray-50 dark:bg-gray-800 text-gray-700 dark:text-gray-300'
              }`}
            >
              <div className="flex items-center">
                {change.type === 'added' && <span className="text-green-600 mr-2">+</span>}
                {change.type === 'removed' && <span className="text-red-600 mr-2">-</span>}
                {change.type === 'modified' && <span className="text-yellow-600 mr-2">¬±</span>}
                <span className="text-sm">{change.content}</span>
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  };

  // **RENDERIZAR SIDE BY SIDE**
  const renderSideBySide = () => {
    if (!comparisonData) return null;

    const { version1, version2, diff } = comparisonData;

    return (
      <div className="grid grid-cols-2 gap-4">
        {/* Vers√£o 1 */}
        <div className="space-y-2">
          <h3 className="text-sm font-medium text-gray-900 dark:text-gray-100 border-b border-gray-200 dark:border-gray-700 pb-2">
            Vers√£o {version1.versionNumber} ({formatDate(version1.timestamp)})
          </h3>
          <div className="bg-gray-50 dark:bg-gray-900 rounded-lg p-4 max-h-96 overflow-y-auto">
            <pre className="text-sm text-gray-700 dark:text-gray-300 whitespace-pre-wrap">
              {version1.content}
            </pre>
          </div>
        </div>

        {/* Vers√£o 2 */}
        <div className="space-y-2">
          <h3 className="text-sm font-medium text-gray-900 dark:text-gray-100 border-b border-gray-200 dark:border-gray-700 pb-2">
            Vers√£o {version2.versionNumber} ({formatDate(version2.timestamp)})
          </h3>
          <div className="bg-gray-50 dark:bg-gray-900 rounded-lg p-4 max-h-96 overflow-y-auto">
            <pre className="text-sm text-gray-700 dark:text-gray-300 whitespace-pre-wrap">
              {version2.content}
            </pre>
          </div>
        </div>
      </div>
    );
  };

  // **CALCULAR ESTAT√çSTICAS**
  const getVersionStats = (version: ScriptVersion) => ({
    palavras: version.metadata.wordCount,
    caracteres: version.metadata.characterCount,
    tempoLeitura: `${version.metadata.readingTime}min`,
    sentimento: `${Math.round((version.metadata.sentiment + 1) * 50)}%`,
    sugestoesIA: version.aiSuggestions.length
  });

  return (
    <Dialog 
      isOpen={isOpen} 
      onClose={onClose}
      className="max-w-7xl"
      title="Compara√ß√£o de Vers√µes"
    >
      <div className="space-y-6">
        {/* **SELE√á√ÉO DE VERS√ïES** */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              Primeira Vers√£o
            </label>
            <Select
              value={selectedVersion1}
              onValueChange={setSelectedVersion1}
              options={sortedVersions.map(version => ({
                value: version.id,
                label: `Vers√£o ${version.versionNumber} - ${formatDate(version.timestamp)}`,
                description: version.comment || 'Sem coment√°rio'
              }))}
              placeholder="Selecione a primeira vers√£o"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              Segunda Vers√£o
            </label>
            <Select
              value={selectedVersion2}
              onValueChange={setSelectedVersion2}
              options={sortedVersions
                .filter(v => v.id !== selectedVersion1)
                .map(version => ({
                  value: version.id,
                  label: `Vers√£o ${version.versionNumber} - ${formatDate(version.timestamp)}`,
                  description: version.comment || 'Sem coment√°rio'
                }))}
              placeholder="Selecione a segunda vers√£o"
            />
          </div>
        </div>

        {/* **CONTROLES DE VISUALIZA√á√ÉO** */}
        {comparisonData && (
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              {/* Modo de visualiza√ß√£o */}
              <div className="flex bg-gray-100 dark:bg-gray-800 rounded-lg p-1">
                <button
                  onClick={() => setViewMode('side-by-side')}
                  className={`px-3 py-1 text-sm rounded-md transition-colors ${
                    viewMode === 'side-by-side'
                      ? 'bg-white dark:bg-gray-700 text-gray-900 dark:text-gray-100 shadow-sm'
                      : 'text-gray-600 dark:text-gray-400'
                  }`}
                >
                  <Icons.Columns className="w-4 h-4 mr-1 inline" />
                  Lado a Lado
                </button>
                <button
                  onClick={() => setViewMode('inline')}
                  className={`px-3 py-1 text-sm rounded-md transition-colors ${
                    viewMode === 'inline'
                      ? 'bg-white dark:bg-gray-700 text-gray-900 dark:text-gray-100 shadow-sm'
                      : 'text-gray-600 dark:text-gray-400'
                  }`}
                >
                  <Icons.List className="w-4 h-4 mr-1 inline" />
                  Unificado
                </button>
              </div>

              {/* Toggle m√©tricas */}
              <Button
                size="sm"
                variant="outline"
                onClick={() => setShowMetrics(!showMetrics)}
              >
                <Icons.BarChart className="w-4 h-4 mr-1" />
                {showMetrics ? 'Ocultar' : 'Mostrar'} M√©tricas
              </Button>
            </div>

            {/* Estat√≠sticas r√°pidas */}
            <div className="flex items-center space-x-4 text-sm text-gray-600 dark:text-gray-400">
              <div className="flex items-center space-x-1">
                <span className="w-2 h-2 bg-green-400 rounded-full"></span>
                <span>+{comparisonData.metrics.addedWords} palavras</span>
              </div>
              <div className="flex items-center space-x-1">
                <span className="w-2 h-2 bg-red-400 rounded-full"></span>
                <span>-{comparisonData.metrics.removedWords} palavras</span>
              </div>
              <div className="flex items-center space-x-1">
                <span className="w-2 h-2 bg-yellow-400 rounded-full"></span>
                <span>¬±{comparisonData.metrics.modifiedWords} modificadas</span>
              </div>
            </div>
          </div>
        )}

        {/* **LOADING** */}
        {isLoading && (
          <div className="flex items-center justify-center py-12">
            <LoadingSpinner size="lg" />
            <span className="ml-3 text-gray-600 dark:text-gray-400">
              Comparando vers√µes...
            </span>
          </div>
        )}

        {/* **CONTE√öDO DA COMPARA√á√ÉO** */}
        {comparisonData && !isLoading && (
          <Tabs
            value={showMetrics ? 'metrics' : 'content'}
            onValueChange={(value) => setShowMetrics(value === 'metrics')}
            tabs={[
              { id: 'content', label: 'Compara√ß√£o', icon: Icons.GitCompare },
              { id: 'metrics', label: 'M√©tricas', icon: Icons.BarChart },
              { id: 'changes', label: 'Mudan√ßas', icon: Icons.List }
            ]}
          >
            {/* **TAB COMPARA√á√ÉO** */}
            <div id="content">
              {viewMode === 'side-by-side' ? (
                renderSideBySide()
              ) : (
                <div className="bg-gray-50 dark:bg-gray-900 rounded-lg p-4 max-h-96 overflow-y-auto">
                  {renderInlineDiff(comparisonData.diff)}
                </div>
              )}
            </div>

            {/* **TAB M√âTRICAS** */}
            <div id="metrics">
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                {/* Estat√≠sticas das vers√µes */}
                <div className="space-y-4">
                  <h3 className="text-lg font-medium text-gray-900 dark:text-gray-100">
                    Compara√ß√£o de Estat√≠sticas
                  </h3>

                  <div className="space-y-3">
                    {Object.entries(getVersionStats(comparisonData.version1)).map(([key, value1]) => {
                      const value2 = getVersionStats(comparisonData.version2)[key as keyof ReturnType<typeof getVersionStats>];
                      const diff = typeof value1 === 'number' && typeof value2 === 'number' ? value2 - value1 : null;

                      return (
                        <div key={key} className="flex items-center justify-between p-3 bg-white dark:bg-gray-800 rounded-lg">
                          <span className="text-sm font-medium text-gray-700 dark:text-gray-300 capitalize">
                            {key.replace(/([A-Z])/g, ' $1').trim()}
                          </span>
                          <div className="flex items-center space-x-4">
                            <span className="text-sm text-gray-600 dark:text-gray-400">
                              {value1} ‚Üí {value2}
                            </span>
                            {diff !== null && (
                              <Badge
                                variant={diff > 0 ? 'success' : diff < 0 ? 'destructive' : 'secondary'}
                                className="text-xs"
                              >
                                {diff > 0 ? '+' : ''}{diff}
                              </Badge>
                            )}
                          </div>
                        </div>
                      );
                    })}
                  </div>
                </div>

                {/* M√©tricas de mudan√ßas */}
                <div className="space-y-4">
                  <h3 className="text-lg font-medium text-gray-900 dark:text-gray-100">
                    An√°lise de Mudan√ßas
                  </h3>

                  <div className="grid grid-cols-2 gap-4">
                    <div className="text-center p-4 bg-white dark:bg-gray-800 rounded-lg">
                      <div className="text-2xl font-bold text-gray-900 dark:text-gray-100">
                        {comparisonData.metrics.totalChanges}
                      </div>
                      <div className="text-sm text-gray-600 dark:text-gray-400">
                        Total de Mudan√ßas
                      </div>
                    </div>

                    <div className="text-center p-4 bg-white dark:bg-gray-800 rounded-lg">
                      <div className="text-2xl font-bold text-green-600">
                        {comparisonData.metrics.improvementScore}%
                      </div>
                      <div className="text-sm text-gray-600 dark:text-gray-400">
                        Score de Melhoria
                      </div>
                    </div>
                  </div>

                  {/* Gr√°fico de mudan√ßas */}
                  <div className="space-y-2">
                    <div className="flex items-center justify-between text-sm">
                      <span className="text-gray-700 dark:text-gray-300">Adi√ß√µes</span>
                      <span className="text-green-600">{comparisonData.metrics.addedWords}</span>
                    </div>
                    <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2">
                      <div 
                        className="bg-green-500 h-2 rounded-full"
                        style={{
                          width: `${(comparisonData.metrics.addedWords / Math.max(comparisonData.metrics.totalChanges, 1)) * 100}%`
                        }}
                      />
                    </div>

                    <div className="flex items-center justify-between text-sm">
                      <span className="text-gray-700 dark:text-gray-300">Remo√ß√µes</span>
                      <span className="text-red-600">{comparisonData.metrics.removedWords}</span>
                    </div>
                    <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2">
                      <div 
                        className="bg-red-500 h-2 rounded-full"
                        style={{
                          width: `${(comparisonData.metrics.removedWords / Math.max(comparisonData.metrics.totalChanges, 1)) * 100}%`
                        }}
                      />
                    </div>

                    <div className="flex items-center justify-between text-sm">
                      <span className="text-gray-700 dark:text-gray-300">Modifica√ß√µes</span>
                      <span className="text-yellow-600">{comparisonData.metrics.modifiedWords}</span>
                    </div>
                    <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2">
                      <div 
                        className="bg-yellow-500 h-2 rounded-full"
                        style={{
                          width: `${(comparisonData.metrics.modifiedWords / Math.max(comparisonData.metrics.totalChanges, 1)) * 100}%`
                        }}
                      />
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {/* **TAB MUDAN√áAS** */}
            <div id="changes">
              <div className="space-y-4">
                <div className="flex items-center justify-between">
                  <h3 className="text-lg font-medium text-gray-900 dark:text-gray-100">
                    Lista Detalhada de Mudan√ßas
                  </h3>
                  <span className="text-sm text-gray-600 dark:text-gray-400">
                    {comparisonData.diff.filter(d => d.type !== 'unchanged').length} mudan√ßas encontradas
                  </span>
                </div>

                <div className="max-h-96 overflow-y-auto space-y-2">
                  {comparisonData.diff
                    .filter(change => change.type !== 'unchanged')
                    .map((change, index) => (
                      <div 
                        key={`change-${change.type}-${change.startIndex}-${change.endIndex}-${index}`}
                        className={`p-3 rounded-lg border-l-4 ${
                          change.type === 'added' ? 'bg-green-50 dark:bg-green-900/20 border-green-400' :
                          change.type === 'removed' ? 'bg-red-50 dark:bg-red-900/20 border-red-400' :
                          'bg-yellow-50 dark:bg-yellow-900/20 border-yellow-400'
                        }`}
                      >
                        <div className="flex items-center justify-between mb-2">
                          <Badge
                            variant={
                              change.type === 'added' ? 'success' :
                              change.type === 'removed' ? 'destructive' :
                              'warning'
                            }
                            className="text-xs"
                          >
                            {change.type === 'added' ? 'Adicionado' :
                             change.type === 'removed' ? 'Removido' :
                             'Modificado'}
                          </Badge>
                          <span className="text-xs text-gray-500 dark:text-gray-400">
                            Posi√ß√£o: {change.startIndex}-{change.endIndex}
                          </span>
                        </div>
                        <div className="text-sm text-gray-700 dark:text-gray-300">
                          {change.content}
                        </div>
                      </div>
                    ))}
                </div>
              </div>
            </div>
          </Tabs>
        )}

        {/* **EMPTY STATE** */}
        {!comparisonData && !isLoading && (selectedVersion1 && selectedVersion2) && (
          <div className="text-center py-12">
            <Icons.GitCompare className="w-12 h-12 text-gray-400 dark:text-gray-600 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 dark:text-gray-100 mb-2">
              Pronto para Comparar
            </h3>
            <p className="text-sm text-gray-600 dark:text-gray-400 mb-4">
              Clique em "Comparar Vers√µes" para ver as diferen√ßas.
            </p>
            <Button onClick={handleCompare}>
              <Icons.GitCompare className="w-4 h-4 mr-2" />
              Comparar Vers√µes
            </Button>
          </div>
        )}

        {/* **RODAP√â** */}
        <div className="flex items-center justify-between pt-4 border-t border-gray-200 dark:border-gray-700">
          <div className="text-sm text-gray-600 dark:text-gray-400">
            {comparisonData && (
              <span>
                Compara√ß√£o realizada em {formatDate(comparisonData.timestamp)}
              </span>
            )}
          </div>

          <div className="flex space-x-2">
            <Button variant="outline" onClick={onClose}>
              Fechar
            </Button>

            {selectedVersion1 && selectedVersion2 && selectedVersion1 !== selectedVersion2 && (
              <Button onClick={handleCompare} disabled={isLoading}>
                {isLoading ? (
                  <>
                    <LoadingSpinner size="sm" className="mr-2" />
                    Comparando...
                  </>
                ) : (
                  <>
                    <Icons.GitCompare className="w-4 h-4 mr-2" />
                    Atualizar Compara√ß√£o
                  </>
                )}
              </Button>
            )}
          </div>
        </div>
      </div>
    </Dialog>
  );
};

import React, { useState, useEffect } from 'react';
import { Dialog } from '../ui/Dialog';
import { Button } from '../ui/Button';
import { Icons } from '../ui/Icons';
import { Badge } from '../ui/Badge';
import { LoadingSpinner } from '../ui/LoadingSpinner';
import { format } from 'date-fns';
import { ptBR } from 'date-fns/locale';
import type { ScriptVersion } from '../../types';

interface VersionHistoryModalProps {
  isOpen: boolean;
  onClose: () => void;
  projectId: string;
  versions: ScriptVersion[];
  currentVersion: ScriptVersion | null;
  onRestore: (version: ScriptVersion) => void;
  onCompare: (version1: string, version2: string) => void;
}

export const VersionHistoryModal: React.FC<VersionHistoryModalProps> = ({
  isOpen,
  onClose,
  projectId,
  versions,
  currentVersion,
  onRestore,
  onCompare
}) => {
  // **ESTADOS**
  const [selectedVersions, setSelectedVersions] = useState<string[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [expandedVersion, setExpandedVersion] = useState<string | null>(null);
  const [filterType, setFilterType] = useState<'all' | 'manual' | 'auto'>('all');

  // **FILTRAR VERS√ïES**
  const filteredVersions = versions.filter(version => {
    if (filterType === 'manual') return !version.isAutoSave;
    if (filterType === 'auto') return version.isAutoSave;
    return true;
  });

  // **SELE√á√ÉO DE VERS√ïES**
  const handleVersionSelect = (versionId: string) => {
    setSelectedVersions(prev => {
      if (prev.includes(versionId)) {
        return prev.filter(id => id !== versionId);
      }
      if (prev.length >= 2) {
        return [prev[1], versionId]; // Manter apenas 2 selecionados
      }
      return [...prev, versionId];
    });
  };

  // **RESTAURAR VERS√ÉO**
  const handleRestore = (version: ScriptVersion) => {
    setIsLoading(true);
    onRestore(version);
    setTimeout(() => {
      setIsLoading(false);
      onClose();
    }, 1000);
  };

  // **COMPARAR VERS√ïES**
  const handleCompare = () => {
    if (selectedVersions.length === 2) {
      onCompare(selectedVersions[0], selectedVersions[1]);
    }
  };

  // **EXPANDIR/COLAPSAR VERS√ÉO**
  const toggleExpanded = (versionId: string) => {
    setExpandedVersion(prev => prev === versionId ? null : versionId);
  };

  // **CALCULAR DIFEREN√áAS**
  const calculateDifferences = (version: ScriptVersion): string => {
    const changes = version.changes.length;
    const additions = version.changes.filter(c => c.type === 'addition').length;
    const deletions = version.changes.filter(c => c.type === 'deletion').length;
    const modifications = version.changes.filter(c => c.type === 'modification').length;

    if (changes === 0) return 'Primeira vers√£o';

    const parts = [];
    if (additions > 0) parts.push(`+${additions}`);
    if (deletions > 0) parts.push(`-${deletions}`);
    if (modifications > 0) parts.push(`¬±${modifications}`);

    return parts.join(' ') || 'Sem mudan√ßas';
  };

  // **FORMATA√á√ÉO DE DATA**
  const formatDate = (timestamp: any) => {
    const date = timestamp?.toDate ? timestamp.toDate() : new Date(timestamp);
    return format(date, "dd 'de' MMM '√†s' HH:mm", { locale: ptBR });
  };

  // **RESET AO ABRIR**
  useEffect(() => {
    if (isOpen) {
      setSelectedVersions([]);
      setExpandedVersion(null);
      setFilterType('all');
    }
  }, [isOpen]);

  return (
    <Dialog 
      isOpen={isOpen} 
      onClose={onClose}
      className="max-w-5xl"
      title="Hist√≥rico de Vers√µes"
    >
      <div className="space-y-4">
        {/* **CONTROLES** */}
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            {/* Filtros */}
            <div className="flex items-center space-x-2">
              <span className="text-sm text-gray-600 dark:text-gray-400">Filtrar:</span>
              <div className="flex bg-gray-100 dark:bg-gray-800 rounded-lg p-1">
                {[
                  { key: 'all', label: 'Todas' },
                  { key: 'manual', label: 'Manuais' },
                  { key: 'auto', label: 'Auto-save' }
                ].map(filter => (
                  <button
                    key={filter.key}
                    onClick={() => setFilterType(filter.key as any)}
                    className={`px-3 py-1 text-sm rounded-md transition-colors ${
                      filterType === filter.key
                        ? 'bg-white dark:bg-gray-700 text-gray-900 dark:text-gray-100 shadow-sm'
                        : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-100'
                    }`}
                  >
                    {filter.label}
                  </button>
                ))}
              </div>
            </div>

            {/* Estat√≠sticas */}
            <div className="text-sm text-gray-600 dark:text-gray-400">
              {filteredVersions.length} vers√£o(√µes)
            </div>
          </div>

          {/* A√ß√µes */}
          <div className="flex items-center space-x-2">
            {selectedVersions.length === 2 && (
              <Button
                size="sm"
                variant="outline"
                onClick={handleCompare}
              >
                <Icons.GitCompare className="w-4 h-4 mr-1" />
                Comparar Selecionadas
              </Button>
            )}

            {selectedVersions.length === 1 && (
              <span className="text-sm text-gray-600 dark:text-gray-400">
                Selecione mais uma vers√£o para comparar
              </span>
            )}
          </div>
        </div>

        {/* **LISTA DE VERS√ïES** */}
        <div className="space-y-3 max-h-[500px] overflow-y-auto">
          {filteredVersions.map((version, index) => {
            const isSelected = selectedVersions.includes(version.id);
            const isCurrent = currentVersion?.id === version.id;
            const isExpanded = expandedVersion === version.id;

            return (
              <div
                key={version.id}
                className={`
                  border rounded-lg transition-all duration-200
                  ${isSelected 
                    ? 'border-blue-300 dark:border-blue-600 bg-blue-50 dark:bg-blue-900/20' 
                    : 'border-gray-200 dark:border-gray-700 bg-white dark:bg-gray-800'
                  }
                  ${isCurrent ? 'ring-2 ring-green-400 ring-opacity-50' : ''}
                `}
              >
                <div className="p-4">
                  {/* **CABE√áALHO DA VERS√ÉO** */}
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-3">
                      {/* Checkbox de sele√ß√£o */}
                      <input
                        type="checkbox"
                        checked={isSelected}
                        onChange={() => handleVersionSelect(version.id)}
                        className="w-4 h-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
                      />

                      {/* Informa√ß√µes b√°sicas */}
                      <div>
                        <div className="flex items-center space-x-2">
                          <h3 className="font-medium text-gray-900 dark:text-gray-100">
                            Vers√£o {version.versionNumber}
                          </h3>

                          {/* Badges */}
                          {isCurrent && (
                            <Badge variant="success" className="text-xs">
                              <Icons.CheckCircle className="w-3 h-3 mr-1" />
                              Atual
                            </Badge>
                          )}

                          {version.isAutoSave && (
                            <Badge variant="secondary" className="text-xs">
                              <Icons.Clock className="w-3 h-3 mr-1" />
                              Auto-save
                            </Badge>
                          )}

                          {version.aiSuggestions.length > 0 && (
                            <Badge variant="info" className="text-xs">
                              <Icons.Sparkles className="w-3 h-3 mr-1" />
                              IA ({version.aiSuggestions.length})
                            </Badge>
                          )}
                        </div>

                        <div className="text-sm text-gray-600 dark:text-gray-400 mt-1">
                          {formatDate(version.timestamp)}
                          {version.comment && ` ‚Ä¢ ${version.comment}`}
                        </div>
                      </div>
                    </div>

                    {/* A√ß√µes */}
                    <div className="flex items-center space-x-2">
                      {/* Estat√≠sticas r√°pidas */}
                      <div className="text-xs text-gray-500 dark:text-gray-400 text-right">
                        <div>{version.metadata.wordCount} palavras</div>
                        <div>{calculateDifferences(version)}</div>
                      </div>

                      {/* Bot√µes de a√ß√£o */}
                      <Button
                        size="sm"
                        variant="ghost"
                        onClick={() => toggleExpanded(version.id)}
                      >
                        <Icons.ChevronDown 
                          className={`w-4 h-4 transition-transform ${isExpanded ? 'rotate-180' : ''}`} 
                        />
                      </Button>

                      {!isCurrent && (
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => handleRestore(version)}
                          disabled={isLoading}
                        >
                          <Icons.RotateCcw className="w-4 h-4 mr-1" />
                          Restaurar
                        </Button>
                      )}
                    </div>
                  </div>

                  {/* **DETALHES EXPANDIDOS** */}
                  {isExpanded && (
                    <div className="mt-4 pt-4 border-t border-gray-200 dark:border-gray-700 space-y-4">
                      {/* Estat√≠sticas detalhadas */}
                      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                        <div className="text-center">
                          <div className="text-lg font-semibold text-gray-900 dark:text-gray-100">
                            {version.metadata.wordCount}
                          </div>
                          <div className="text-xs text-gray-600 dark:text-gray-400">Palavras</div>
                        </div>

                        <div className="text-center">
                          <div className="text-lg font-semibold text-gray-900 dark:text-gray-100">
                            {version.metadata.characterCount}
                          </div>
                          <div className="text-xs text-gray-600 dark:text-gray-400">Caracteres</div>
                        </div>

                        <div className="text-center">
                          <div className="text-lg font-semibold text-gray-900 dark:text-gray-100">
                            {version.metadata.readingTime}min
                          </div>
                          <div className="text-xs text-gray-600 dark:text-gray-400">Leitura</div>
                        </div>

                        <div className="text-center">
                          <div className="text-lg font-semibold text-gray-900 dark:text-gray-100">
                            {Math.round((version.metadata.sentiment + 1) * 50)}%
                          </div>
                          <div className="text-xs text-gray-600 dark:text-gray-400">Sentimento</div>
                        </div>
                      </div>

                      {/* Preview do conte√∫do */}
                      <div>
                        <h4 className="text-sm font-medium text-gray-900 dark:text-gray-100 mb-2">
                          Preview do Conte√∫do
                        </h4>
                        <div className="bg-gray-50 dark:bg-gray-900 rounded-lg p-3 text-sm text-gray-700 dark:text-gray-300">
                          {version.content.length > 200 
                            ? `${version.content.substring(0, 200)}...`
                            : version.content
                          }
                        </div>
                      </div>

                      {/* Mudan√ßas */}
                      {version.changes.length > 0 && (
                        <div>
                          <h4 className="text-sm font-medium text-gray-900 dark:text-gray-100 mb-2">
                            Mudan√ßas ({version.changes.length})
                          </h4>
                          <div className="space-y-1">
                            {version.changes.slice(0, 3).map((change) => (
                              <div key={change.id} className="text-xs text-gray-600 dark:text-gray-400 flex items-center space-x-2">
                                <span className={`w-2 h-2 rounded-full ${
                                  change.type === 'addition' ? 'bg-green-400' :
                                  change.type === 'deletion' ? 'bg-red-400' :
                                  change.type === 'modification' ? 'bg-yellow-400' :
                                  'bg-blue-400'
                                }`} />
                                <span className="capitalize">{change.type}</span>
                                <span>‚Ä¢</span>
                                <span>{change.newText?.substring(0, 50) || change.oldText?.substring(0, 50)}...</span>
                              </div>
                            ))}
                            {version.changes.length > 3 && (
                              <div className="text-xs text-gray-500 dark:text-gray-400">
                                +{version.changes.length - 3} mais mudan√ßas
                              </div>
                            )}
                          </div>
                        </div>
                      )}

                      {/* Sugest√µes de IA aplicadas */}
                      {version.aiSuggestions.length > 0 && (
                        <div>
                          <h4 className="text-sm font-medium text-gray-900 dark:text-gray-100 mb-2">
                            Sugest√µes de IA Aplicadas ({version.aiSuggestions.length})
                          </h4>
                          <div className="flex flex-wrap gap-2">
                            {version.aiSuggestions.map((suggestionId, index) => (
                              <Badge key={suggestionId} variant="info" className="text-xs">
                                Sugest√£o {index + 1}
                              </Badge>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  )}
                </div>
              </div>
            );
          })}
        </div>

        {/* **EMPTY STATE** */}
        {filteredVersions.length === 0 && (
          <div className="text-center py-12">
            <Icons.History className="w-12 h-12 text-gray-400 dark:text-gray-600 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 dark:text-gray-100 mb-2">
              Nenhuma vers√£o encontrada
            </h3>
            <p className="text-sm text-gray-600 dark:text-gray-400">
              {filterType === 'all' 
                ? 'Este projeto ainda n√£o possui vers√µes salvas.'
                : `Nenhuma vers√£o ${filterType === 'manual' ? 'manual' : 'autom√°tica'} encontrada.`
              }
            </p>
          </div>
        )}

        {/* **RODAP√â** */}
        <div className="flex items-center justify-between pt-4 border-t border-gray-200 dark:border-gray-700">
          <div className="flex items-center space-x-4 text-sm text-gray-600 dark:text-gray-400">
            <div className="flex items-center space-x-1">
              <span className="w-2 h-2 bg-green-400 rounded-full"></span>
              <span>Adi√ß√£o</span>
            </div>
            <div className="flex items-center space-x-1">
              <span className="w-2 h-2 bg-red-400 rounded-full"></span>
              <span>Remo√ß√£o</span>
            </div>
            <div className="flex items-center space-x-1">
              <span className="w-2 h-2 bg-yellow-400 rounded-full"></span>
              <span>Modifica√ß√£o</span>
            </div>
            <div className="flex items-center space-x-1">
              <span className="w-2 h-2 bg-blue-400 rounded-full"></span>
              <span>IA</span>
            </div>
          </div>

          <div className="flex space-x-2">
            <Button variant="outline" onClick={onClose}>
              Fechar
            </Button>

            {selectedVersions.length === 2 && (
              <Button onClick={handleCompare}>
                <Icons.GitCompare className="w-4 h-4 mr-2" />
                Comparar Vers√µes
              </Button>
            )}
          </div>
        </div>

        {/* **LOADING OVERLAY** */}
        {isLoading && (
          <div className="absolute inset-0 bg-white/50 dark:bg-gray-900/50 flex items-center justify-center">
            <div className="bg-white dark:bg-gray-800 rounded-lg p-4 shadow-lg flex items-center space-x-3">
              <LoadingSpinner size="sm" />
              <span className="text-sm text-gray-900 dark:text-gray-100">
                Restaurando vers√£o...
              </span>
            </div>
          </div>
        )}
      </div>
    </Dialog>
  );
};

import React, { useState, useEffect, useRef } from 'react';
import { Play, Pause, Square, Volume2, VolumeX, Settings, Download, Mic, Zap, Clock, Users, Star } from 'lucide-react';
import { Button } from '../ui/Button';
import { Card } from '../ui/Card';
import { Select } from '../ui/Select';
import { Slider } from '../ui/Slider';
import { Badge } from '../ui/Badge';
import { LoadingSpinner } from '../ui/LoadingSpinner';
import { VoiceSynthesisService } from '../../services/voiceSynthesisService';
import type { VoiceProfile, VoiceSynthesis } from '../../types';

interface VoiceSynthesisPanelProps {
  projectId: string;
  userId: string;
  text: string;
  isVisible: boolean;
  onClose: () => void;
}

export const VoiceSynthesisPanel: React.FC<VoiceSynthesisPanelProps> = ({
  projectId,
  userId,
  text,
  isVisible,
  onClose
}) => {
  // Estados
  const [availableVoices, setAvailableVoices] = useState<VoiceProfile[]>([]);
  const [selectedVoice, setSelectedVoice] = useState<string>('');
  const [settings, setSettings] = useState({
    rate: 1.0,
    pitch: 1.0,
    volume: 1.0,
    emphasis: 'moderate' as const,
    pause: {
      sentence: 500,
      paragraph: 1000
    }
  });
  const [isPlaying, setIsPlaying] = useState(false);
  const [isSynthesizing, setIsSynthesizing] = useState(false);
  const [currentSynthesis, setCurrentSynthesis] = useState<VoiceSynthesis | null>(null);
  const [userQuota, setUserQuota] = useState({ used: 0, limit: 100, remaining: 100 });
  const [showAdvancedSettings, setShowAdvancedSettings] = useState(false);
  const [previewText, setPreviewText] = useState('');
  const [audioProgress, setAudioProgress] = useState(0);
  const [isPreviewingVoice, setIsPreviewingVoice] = useState(false);
  const [activeTab, setActiveTab] = useState<'voices' | 'settings' | 'preview'>('voices');

  const audioRef = useRef<HTMLAudioElement>(null);

  // Inicializa√ß√£o com loading suave
  useEffect(() => {
    const initializeVoices = async () => {
      if (!isVisible) return;

      try {
        await VoiceSynthesisService.initialize();
        const voices = VoiceSynthesisService.getAvailableVoices('pt');
        setAvailableVoices(voices);

        if (voices.length > 0) {
          // Preferir vozes portuguesas primeiro
          const ptVoice = voices.find(v => v.language.startsWith('pt')) || voices[0];
          setSelectedVoice(ptVoice.id);
        }

        // Obter quota do usu√°rio
        const quota = await VoiceSynthesisService.checkUserQuota(userId);
        setUserQuota(quota);
      } catch (error) {
        console.error('Erro ao inicializar s√≠ntese de voz:', error);
      }
    };

    initializeVoices();
  }, [isVisible, userId]);

  // Limpar ao fechar
  useEffect(() => {
    if (!isVisible) {
      handleStop();
      setActiveTab('voices');
      setShowAdvancedSettings(false);
    }
  }, [isVisible]);

  // Monitorar progresso do √°udio
  useEffect(() => {
    const audio = audioRef.current;
    if (!audio) return;

    const updateProgress = () => {
      if (audio.duration) {
        setAudioProgress((audio.currentTime / audio.duration) * 100);
      }
    };

    audio.addEventListener('timeupdate', updateProgress);
    return () => audio.removeEventListener('timeupdate', updateProgress);
  }, [currentSynthesis]);

  // Verificar se est√° reproduzindo
  useEffect(() => {
    const checkPlayingStatus = () => {
      const isSpeaking = VoiceSynthesisService.isSpeaking();
      setIsPlaying(isSpeaking || (audioRef.current && !audioRef.current.paused));
    };

    const interval = setInterval(checkPlayingStatus, 500);
    return () => clearInterval(interval);
  }, []);

  // Handlers melhorados
  const handleVoiceChange = (voiceId: string) => {
    setSelectedVoice(voiceId);

    // Anima√ß√£o suave de sele√ß√£o
    const voiceElement = document.querySelector(`[data-voice-id="${voiceId}"]`);
    if (voiceElement) {
      voiceElement.scrollIntoView({ behavior: 'smooth', block: 'nearest' });
    }
  };

  const handlePreviewVoice = async (voiceId?: string) => {
    const targetVoice = voiceId || selectedVoice;
    if (!targetVoice) return;

    setIsPreviewingVoice(true);

    try {
      const voice = VoiceSynthesisService.getVoiceById(targetVoice);
      if (voice) {
        const sampleText = previewText || getPreviewText(voice.language);
        await VoiceSynthesisService.previewVoice(targetVoice, sampleText);
      }
    } catch (error) {
      console.error('Erro ao fazer preview da voz:', error);
    } finally {
      setIsPreviewingVoice(false);
    }
  };

  const handleSynthesize = async () => {
    if (!selectedVoice || !text.trim()) {
      return;
    }

    if (userQuota.remaining <= 0) {
      // Melhor feedback para limite atingido
      alert('üö´ Voc√™ atingiu o limite de s√≠nteses deste m√™s. Fa√ßa upgrade do seu plano para continuar.');
      return;
    }

    setIsSynthesizing(true);

    try {
      const synthesis = await VoiceSynthesisService.synthesizeText(
        projectId,
        userId,
        text,
        selectedVoice,
        settings
      );

      setCurrentSynthesis(synthesis);

      // Atualizar quota com anima√ß√£o
      const newQuota = await VoiceSynthesisService.checkUserQuota(userId);
      setUserQuota(newQuota);

      // Se h√° URL de √°udio, configurar reprodu√ß√£o
      if (synthesis.audioUrl && audioRef.current) {
        audioRef.current.src = synthesis.audioUrl;
      }

      // Feedback de sucesso
      setActiveTab('preview');

    } catch (error) {
      console.error('Erro na s√≠ntese:', error);
      alert('‚ùå Erro ao sintetizar voz. Tente novamente em alguns momentos.');
    } finally {
      setIsSynthesizing(false);
    }
  };

  const handlePlay = () => {
    if (currentSynthesis?.audioUrl && audioRef.current) {
      // √Åudio pr√©-gerado
      audioRef.current.play();
      setIsPlaying(true);
    } else if (selectedVoice && text.trim()) {
