      error: null,
      errorInfo: null,
      errorId: null,
      retryCount: 0,
    };
  }

  static getDerivedStateFromError(error: Error): Partial<State> {
    // Update state so the next render will show the fallback UI
    const errorId = `err_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

    return {
      hasError: true,
      error,
      errorId,
    };
  }

  componentDidCatch(error: Error, errorInfo: ErrorInfo) {
    const { onError } = this.props;
    const { errorId } = this.state;

    // Log error with structured data
    logger.error('React Error Boundary triggered', {
      errorId,
      message: error.message,
      stack: error.stack,
      componentStack: errorInfo.componentStack,
      retryCount: this.state.retryCount,
      timestamp: new Date().toISOString(),
      userAgent: navigator.userAgent,
      url: window.location.href,
      userId: this.getUserId(),
    }, 'ERROR_BOUNDARY');

    // Call custom error handler if provided
    if (onError) {
      try {
        onError(error, errorInfo);
      } catch (handlerError) {
        logger.error('Error in custom error handler', {
          originalError: error.message,
          handlerError: handlerError instanceof Error ? handlerError.message : 'Unknown',
        }, 'ERROR_BOUNDARY');
      }
    }

    // Update state with error info
    this.setState({
      errorInfo,
    });

    // Report to external error tracking (production only)
    if (config.environment === 'production') {
      this.reportToExternalService(error, errorInfo, errorId || 'unknown');
    }
  }

  private getUserId(): string | null {
    try {
      // Get user ID from auth context or localStorage
      return localStorage.getItem('userId') || 'anonymous';
    } catch {
      return 'unknown';
    }
  }

  private async reportToExternalService(error: Error, errorInfo: ErrorInfo, errorId: string) {
    try {
      // Here you would integrate with services like Sentry, LogRocket, etc.
      // For now, we'll just log it as a structured error
      logger.error('External error report', {
        service: 'external_error_tracking',
        errorId,
        error: {
          message: error.message,
          stack: error.stack,
          name: error.name,
        },
        errorInfo: {
          componentStack: errorInfo.componentStack,
        },
        metadata: {
          timestamp: new Date().toISOString(),
          url: window.location.href,
          userAgent: navigator.userAgent,
          environment: config.environment,
          version: config.version,
        },
      }, 'EXTERNAL_TRACKING');
    } catch (reportError) {
      logger.error('Failed to report to external service', {
        originalError: error.message,
        reportError: reportError instanceof Error ? reportError.message : 'Unknown',
      }, 'ERROR_BOUNDARY');
    }
  }

  private handleRetry = () => {
    const { retryCount } = this.state;

    if (retryCount >= this.maxRetries) {
      logger.warn('Max retry attempts reached', {
        retryCount,
        maxRetries: this.maxRetries,
        errorId: this.state.errorId,
      }, 'ERROR_BOUNDARY');
      return;
    }

    logger.info('Retrying after error', {
      retryCount: retryCount + 1,
      errorId: this.state.errorId,
    }, 'ERROR_BOUNDARY');

    this.setState({
      hasError: false,
      error: null,
      errorInfo: null,
      retryCount: retryCount + 1,
    });

    // Clear any existing timeout
    if (this.retryTimeout) {
      clearTimeout(this.retryTimeout);
    }

    // Reset retry count after successful render (30 seconds)
    this.retryTimeout = setTimeout(() => {
      this.setState({ retryCount: 0 });
    }, 30000);
  };

  private handleReload = () => {
    logger.info('User triggered page reload', {
      errorId: this.state.errorId,
    }, 'ERROR_BOUNDARY');

    window.location.reload();
  };

  private handleGoHome = () => {
    logger.info('User navigated to home', {
      errorId: this.state.errorId,
    }, 'ERROR_BOUNDARY');

    window.location.href = '/';
  };

  private handleReportBug = () => {
    const { error, errorInfo, errorId } = this.state;

    const bugReport = {
      errorId,
      message: error?.message || 'Unknown error',
      stack: error?.stack || 'No stack trace',
      componentStack: errorInfo?.componentStack || 'No component stack',
      url: window.location.href,
      timestamp: new Date().toISOString(),
      userAgent: navigator.userAgent,
    };

    // Copy to clipboard
    navigator.clipboard.writeText(JSON.stringify(bugReport, null, 2))
      .then(() => {
        logger.info('Bug report copied to clipboard', { errorId }, 'ERROR_BOUNDARY');
        alert('Relat√≥rio de erro copiado para a √°rea de transfer√™ncia. Cole em seu e-mail de suporte.');
      })
      .catch(() => {
        logger.warn('Failed to copy bug report', { errorId }, 'ERROR_BOUNDARY');
        console.log('Bug Report:', bugReport);
        alert('N√£o foi poss√≠vel copiar. Verifique o console para detalhes do erro.');
      });
  };

  componentWillUnmount() {
    if (this.retryTimeout) {
      clearTimeout(this.retryTimeout);
    }
  }

  render() {
    const { hasError, error, retryCount, errorId } = this.state;
    const { children, fallback, showErrorDetails = config.debugMode } = this.props;

    if (hasError) {
      // Custom fallback provided
      if (fallback) {
        return fallback;
      }

      // Default error UI
      return (
        <div className="min-h-screen flex items-center justify-center p-4 bg-background">
          <Card className="w-full max-w-lg p-6 text-center">
            <div className="flex justify-center mb-4">
              <AlertTriangle className="h-12 w-12 text-destructive" />
            </div>

            <h1 className="text-2xl font-bold mb-2 text-foreground">
              Oops! Algo deu errado
            </h1>

            <p className="text-muted-foreground mb-6">
              Encontramos um erro inesperado. Nossa equipe foi notificada automaticamente.
            </p>

            {showErrorDetails && error && (
              <div className="mb-6 p-4 bg-muted rounded-lg text-left">
                <h3 className="font-semibold mb-2 text-sm">Detalhes do Erro:</h3>
                <p className="text-xs text-muted-foreground mb-2">
                  ID: {errorId}
                </p>
                <p className="text-xs font-mono bg-background p-2 rounded border overflow-auto max-h-32">
                  {error.message}
                </p>
              </div>
            )}

            <div className="space-y-3">
              {retryCount < this.maxRetries && (
                <Button 
                  onClick={this.handleRetry} 
                  className="w-full"
                  variant="default"
                >
                  <RefreshCw className="w-4 h-4 mr-2" />
                  Tentar Novamente ({this.maxRetries - retryCount} tentativas restantes)
                </Button>
              )}

              <div className="grid grid-cols-1 sm:grid-cols-2 gap-3">
                <Button 
                  onClick={this.handleGoHome} 
                  variant="outline"
                  className="w-full"
                >
                  <Home className="w-4 h-4 mr-2" />
                  Ir para In√≠cio
                </Button>

                <Button 
                  onClick={this.handleReload} 
                  variant="outline"
                  className="w-full"
                >
                  <RefreshCw className="w-4 h-4 mr-2" />
                  Recarregar P√°gina
                </Button>
              </div>

              <Button 
                onClick={this.handleReportBug} 
                variant="ghost"
                size="sm"
                className="w-full text-muted-foreground"
              >
                <Bug className="w-4 h-4 mr-2" />
                Reportar Bug
              </Button>
            </div>

            <p className="text-xs text-muted-foreground mt-6">
              Se o problema persistir, entre em contato com o suporte.
            </p>
          </Card>
        </div>
      );
    }

    return children;
  }
}

// Higher-order component for wrapping components with error boundary
export function withErrorBoundary<P extends object>(
  Component: React.ComponentType<P>,
  errorBoundaryProps?: Omit<Props, 'children'>
) {
  const WrappedComponent = (props: P) => (
    <ErrorBoundary {...errorBoundaryProps}>
      <Component {...props} />
    </ErrorBoundary>
  );

  WrappedComponent.displayName = `withErrorBoundary(${Component.displayName || Component.name})`;

  return WrappedComponent;
}

// Hook for manually triggering error boundary (for testing)
export function useErrorHandler() {
  return (error: Error, errorInfo?: { componentStack?: string }) => {
    // This will be caught by the nearest error boundary
    throw error;
  };
}

export default ErrorBoundary;

import React, { useEffect, useRef, createContext, useContext, useState } from 'react';

// Contexto para gerenciamento de foco
interface FocusContextType {
  registerFocusable: (id: string, element: HTMLElement) => void;
  unregisterFocusable: (id: string) => void;
  focusNext: () => void;
  focusPrevious: () => void;
  focusFirst: () => void;
  focusLast: () => void;
  focusById: (id: string) => void;
  announceLive: (message: string, priority?: 'polite' | 'assertive') => void;
  isReducedMotion: boolean;
}

const FocusContext = createContext<FocusContextType | undefined>(undefined);

export const useFocusManager = () => {
  const context = useContext(FocusContext);
  if (!context) {
    throw new Error('useFocusManager deve ser usado dentro de FocusProvider');
  }
  return context;
};

// Provider de gerenciamento de foco
export const FocusProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [focusableElements, setFocusableElements] = useState<Map<string, HTMLElement>>(new Map());
  const [currentFocusIndex, setCurrentFocusIndex] = useState<number>(-1);
  const [isReducedMotion, setIsReducedMotion] = useState(false);
  const liveRegionRef = useRef<HTMLDivElement>(null);

  // Detectar prefer√™ncia de movimento reduzido
  useEffect(() => {
    const mediaQuery = window.matchMedia('(prefers-reduced-motion: reduce)');
    setIsReducedMotion(mediaQuery.matches);

    const handleChange = (e: MediaQueryListEvent) => {
      setIsReducedMotion(e.matches);
    };

    mediaQuery.addEventListener('change', handleChange);
    return () => mediaQuery.removeEventListener('change', handleChange);
  }, []);

  const registerFocusable = (id: string, element: HTMLElement) => {
    setFocusableElements(prev => new Map(prev).set(id, element));
  };

  const unregisterFocusable = (id: string) => {
    setFocusableElements(prev => {
      const newMap = new Map(prev);
      newMap.delete(id);
      return newMap;
    });
  };

  const getFocusableArray = () => {
    return Array.from(focusableElements.values()).filter(element => {
      return element.offsetParent !== null && !element.hasAttribute('disabled');
    });
  };

  const focusNext = () => {
    const elements = getFocusableArray();
    if (elements.length === 0) return;

    const nextIndex = (currentFocusIndex + 1) % elements.length;
    elements[nextIndex]?.focus();
    setCurrentFocusIndex(nextIndex);
  };

  const focusPrevious = () => {
    const elements = getFocusableArray();
    if (elements.length === 0) return;

    const prevIndex = currentFocusIndex <= 0 ? elements.length - 1 : currentFocusIndex - 1;
    elements[prevIndex]?.focus();
    setCurrentFocusIndex(prevIndex);
  };

  const focusFirst = () => {
    const elements = getFocusableArray();
    if (elements.length === 0) return;

    elements[0]?.focus();
    setCurrentFocusIndex(0);
  };

  const focusLast = () => {
    const elements = getFocusableArray();
    if (elements.length === 0) return;

    const lastIndex = elements.length - 1;
    elements[lastIndex]?.focus();
    setCurrentFocusIndex(lastIndex);
  };

  const focusById = (id: string) => {
    const element = focusableElements.get(id);
    if (element) {
      element.focus();
      const elements = getFocusableArray();
      const index = elements.indexOf(element);
      if (index !== -1) {
        setCurrentFocusIndex(index);
      }
    }
  };

  const announceLive = (message: string, priority: 'polite' | 'assertive' = 'polite') => {
    if (liveRegionRef.current) {
      liveRegionRef.current.setAttribute('aria-live', priority);
      liveRegionRef.current.textContent = message;

      // Limpar ap√≥s um tempo para permitir novos an√∫ncios
      setTimeout(() => {
        if (liveRegionRef.current) {
          liveRegionRef.current.textContent = '';
        }
      }, 1000);
    }
  };

  // Gerenciar teclas globais
  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      // Skip se estiver digitando em um input
      if (event.target instanceof HTMLInputElement || 
          event.target instanceof HTMLTextAreaElement ||
          (event.target as HTMLElement)?.isContentEditable) {
        return;
      }

      // Navega√ß√£o por setas (se Ctrl estiver pressionado)
      if (event.ctrlKey) {
        switch (event.key) {
          case 'ArrowDown':
            event.preventDefault();
            focusNext();
            break;
          case 'ArrowUp':
            event.preventDefault();
            focusPrevious();
            break;
          case 'Home':
            event.preventDefault();
            focusFirst();
            break;
          case 'End':
            event.preventDefault();
            focusLast();
            break;
        }
      }

      // Skip/Bypass links (permite pular navega√ß√£o)
      if (event.key === 'Tab' && event.shiftKey && event.ctrlKey) {
        event.preventDefault();
        const mainContent = document.querySelector('[role="main"]') as HTMLElement;
        if (mainContent) {
          mainContent.focus();
          mainContent.scrollIntoView({ behavior: 'smooth' });
        }
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [currentFocusIndex, focusableElements]);

  return (
    <FocusContext.Provider
      value={{
        registerFocusable,
        unregisterFocusable,
        focusNext,
        focusPrevious,
        focusFirst,
        focusLast,
        focusById,
        announceLive,
        isReducedMotion
      }}
    >
      {children}

      {/* Live region para an√∫ncios de tela */}
      <div
        ref={liveRegionRef}
        aria-live="polite"
        aria-atomic="true"
        className="sr-only"
      />

      {/* Indicador visual de foco para usu√°rios de teclado */}
      <style jsx global>{`
        .focus-visible {
          outline: 2px solid #3B82F6;
          outline-offset: 2px;
          border-radius: 4px;
        }

        /* Anima√ß√µes respeitando prefer√™ncias do usu√°rio */
        @media (prefers-reduced-motion: reduce) {
          * {
            animation-duration: 0.01ms !important;
            animation-iteration-count: 1 !important;
            transition-duration: 0.01ms !important;
          }
        }
      `}</style>
    </FocusContext.Provider>
  );
};

// Hook para elementos focus√°veis
export const useFocusable = (id: string, autoRegister: boolean = true) => {
  const elementRef = useRef<HTMLElement>(null);
  const { registerFocusable, unregisterFocusable } = useFocusManager();

  useEffect(() => {
    if (autoRegister && elementRef.current) {
      registerFocusable(id, elementRef.current);

      return () => {
        unregisterFocusable(id);
      };
    }
  }, [id, autoRegister, registerFocusable, unregisterFocusable]);

  return elementRef;
};

