// Hook para an√∫ncios autom√°ticos
export const useAnnouncer = () => {
  const { announceLive } = useFocusManager();

  const announceSuccess = (message: string) => {
    announceLive(`‚úÖ ${message}`, 'polite');
  };

  const announceError = (message: string) => {
    announceLive(`‚ùå ${message}`, 'assertive');
  };

  const announceInfo = (message: string) => {
    announceLive(`‚ÑπÔ∏è ${message}`, 'polite');
  };

  const announceLoading = (message: string = 'Carregando...') => {
    announceLive(`‚è≥ ${message}`, 'polite');
  };

  return {
    announceSuccess,
    announceError,
    announceInfo,
    announceLoading,
    announce: announceLive
  };
};

// Componente de landmarks sem√¢nticos
export const Landmark: React.FC<{
  role: 'main' | 'navigation' | 'banner' | 'contentinfo' | 'complementary' | 'search';
  label?: string;
  children: React.ReactNode;
  className?: string;
}> = ({ role, label, children, className = '' }) => {
  const props: any = {
    role,
    className: `focus:outline-none ${className}`,
    tabIndex: -1
  };

  if (label) {
    props['aria-label'] = label;
  }

  return React.createElement(
    role === 'main' ? 'main' :
    role === 'navigation' ? 'nav' :
    role === 'banner' ? 'header' :
    role === 'contentinfo' ? 'footer' :
    role === 'search' ? 'search' :
    'section',
    props,
    children
  );
};

// Hook para detectar se o usu√°rio est√° navegando por teclado
export const useKeyboardNavigation = () => {
  const [isKeyboardUser, setIsKeyboardUser] = useState(false);

  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      if (event.key === 'Tab') {
        setIsKeyboardUser(true);
      }
    };

    const handleMouseDown = () => {
      setIsKeyboardUser(false);
    };

    document.addEventListener('keydown', handleKeyDown);
    document.addEventListener('mousedown', handleMouseDown);

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
      document.removeEventListener('mousedown', handleMouseDown);
    };
  }, []);

  return isKeyboardUser;
};

// Componente para indicar regi√µes interativas
export const InteractiveRegion: React.FC<{
  children: React.ReactNode;
  label: string;
  description?: string;
  className?: string;
}> = ({ children, label, description, className = '' }) => {
  const isKeyboardUser = useKeyboardNavigation();

  return (
    <div
      role="region"
      aria-label={label}
      aria-describedby={description ? `${label}-desc` : undefined}
      className={`
        ${className}
        ${isKeyboardUser ? 'focus-within:ring-2 focus-within:ring-blue-500 focus-within:ring-opacity-50' : ''}
        transition-all duration-200
      `}
      tabIndex={-1}
    >
      {description && (
        <ScreenReaderOnly>
          <div id={`${label}-desc`}>{description}</div>
        </ScreenReaderOnly>
      )}
      {children}
    </div>
  );
};

