      return null;
    }
  },

  set(theme: "light" | "dark"): boolean {
    try {
      if (typeof window === 'undefined' || typeof localStorage === 'undefined') {
        return false;
      }

      localStorage.setItem(this.key, theme);
      return true;
    } catch (error) {
      console.warn('ThemeToggle: Failed to write to localStorage:', error);
      return false;
    }
  }
};

// Enhanced system preference detection with proper cleanup
const useSystemPreference = () => {
  const [systemPrefersDark, setSystemPrefersDark] = React.useState(false);
  const mediaQueryRef = React.useRef<MediaQueryList | null>(null);

  React.useEffect(() => {
    if (typeof window === 'undefined') return;

    try {
      mediaQueryRef.current = window.matchMedia("(prefers-color-scheme: dark)");
      setSystemPrefersDark(mediaQueryRef.current.matches);

      const handleChange = (e: MediaQueryListEvent) => {
        setSystemPrefersDark(e.matches);
      };

      // Modern browsers
      if (mediaQueryRef.current.addEventListener) {
        mediaQueryRef.current.addEventListener('change', handleChange);
      } else {
        // Legacy browsers
        mediaQueryRef.current.addListener(handleChange);
      }

      return () => {
        if (mediaQueryRef.current) {
          if (mediaQueryRef.current.removeEventListener) {
            mediaQueryRef.current.removeEventListener('change', handleChange);
          } else {
            mediaQueryRef.current.removeListener(handleChange);
          }
        }
      };
    } catch (error) {
      console.warn('ThemeToggle: System preference detection failed:', error);
      setSystemPrefersDark(false);
    }
  }, []);

  return systemPrefersDark;
};

export function useTheme() {
  const [theme, setTheme] = React.useState<"light" | "dark">("light");
  const [isMounted, setIsMounted] = React.useState(false);
  const systemPrefersDark = useSystemPreference();
  const isInitializedRef = React.useRef(false);
  const themeTransitionTimeoutRef = React.useRef<NodeJS.Timeout>();

  // Enhanced initialization with proper hydration safety
  React.useEffect(() => {
    if (isInitializedRef.current) return;

    setIsMounted(true);

    // Batch the theme determination and application
    requestAnimationFrame(() => {
      const storedTheme = ThemeStorage.get();

      let initialTheme: "light" | "dark";

      if (storedTheme) {
        initialTheme = storedTheme;
      } else {
        // Use system preference as fallback
        initialTheme = systemPrefersDark ? "dark" : "light";
        // Save the initial theme determination
        ThemeStorage.set(initialTheme);
      }

      setTheme(initialTheme);
      isInitializedRef.current = true;
    });
  }, [systemPrefersDark]);

  // Enhanced theme application with performance optimization
  React.useEffect(() => {
    if (!isMounted || !isInitializedRef.current) return;

    // Clear any existing timeout to prevent race conditions
    if (themeTransitionTimeoutRef.current) {
      clearTimeout(themeTransitionTimeoutRef.current);
    }

    // Apply theme changes in next frame to prevent layout thrashing
    requestAnimationFrame(() => {
      try {
        // Safely update body classes
        if (document.body) {
          document.body.classList.remove("light", "dark");
          document.body.classList.add(theme);

          // Add smooth transition only after first paint to prevent flash
          if (!document.body.style.transition) {
            document.body.style.transition = "background-color 0.3s ease, color 0.3s ease";
          }
        }

        // Save to localStorage with error handling
        const saved = ThemeStorage.set(theme);

        if (!saved) {
          console.warn('ThemeToggle: Failed to persist theme preference');
        }
      } catch (error) {
        console.error('ThemeToggle: Failed to apply theme:', error);
      }
    });

    // Cleanup function
    return () => {
      if (themeTransitionTimeoutRef.current) {
        clearTimeout(themeTransitionTimeoutRef.current);
      }
    };
  }, [theme, isMounted]);

  // Optimized toggle function with race condition prevention
  const toggleTheme = React.useCallback(() => {
    if (!isInitializedRef.current) return;

    setTheme(prevTheme => {
      const newTheme = prevTheme === "dark" ? "light" : "dark";

      // Dispatch custom event for other components that might listen
      if (typeof window !== 'undefined') {
        try {
          window.dispatchEvent(new CustomEvent('themechange', { 
            detail: { theme: newTheme, previousTheme: prevTheme } 
          }));
        } catch (error) {
          // Ignore event dispatch errors
        }
      }

      return newTheme;
    });
  }, []);

  // Effect to sync with system preference changes when no stored theme
  React.useEffect(() => {
    if (!isInitializedRef.current || ThemeStorage.get()) return;

    // If no stored theme, follow system preference
    const newTheme = systemPrefersDark ? "dark" : "light";
    if (theme !== newTheme) {
      setTheme(newTheme);
    }
  }, [systemPrefersDark, theme]);

  return { theme, toggleTheme, isMounted: isMounted && isInitializedRef.current };
}

export function ThemeToggle() {
  const { toggleTheme, theme, isMounted } = useTheme();

  // Enhanced hydration safety with consistent fallback
  if (!isMounted) {
    return (
      <Button 
        variant="outline" 
        size="icon" 
        aria-label="Loading theme toggle"
        className="transition-colors duration-300"
        disabled
      >
        <Sun className="h-[1.2rem] w-[1.2rem] opacity-50" />
      </Button>
    );
  }

  return (
    <Button 
      variant="outline" 
      size="icon" 
      onClick={toggleTheme}
      aria-label={`Switch to ${theme === 'light' ? 'dark' : 'light'} mode`}
      className="transition-colors duration-300 hover:bg-accent"
    >
      <Sun className={`h-[1.2rem] w-[1.2rem] transition-all duration-300 ${
        theme === 'dark' ? 'rotate-90 scale-0' : 'rotate-0 scale-100'
      }`} />
      <Moon className={`absolute h-[1.2rem] w-[1.2rem] transition-all duration-300 ${
        theme === 'dark' ? 'rotate-0 scale-100' : 'rotate-90 scale-0'
      }`} />
      <span className="sr-only">Toggle theme</span>
    </Button>
  );
}

import React, { Component, ErrorInfo, ReactNode } from 'react';
import { logger } from '../../utils/logger';

interface Props {
  children: ReactNode;
  fallback?: ReactNode;
  scriptName?: string;
}

interface State {
  hasError: boolean;
  errorInfo?: string;
}

/**
 * üõ°Ô∏è THIRD PARTY ERROR BOUNDARY
 * 
 * Boundary espec√≠fico para isolar erros de scripts third-party
 * (Microsoft Clarity, Google Analytics, etc.) evitando que 
 * contaminem o console ou quebrem a aplica√ß√£o principal.
 */
export class ThirdPartyErrorBoundary extends Component<Props, State> {
  constructor(props: Props) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError(error: Error): State {
    // Analisa se √© erro conhecido de third-party
    const isThirdPartyError = 
      error.message?.includes('Cannot read properties of undefined') ||
      error.message?.includes('clarity') ||
      error.message?.includes('gtag') ||
      error.stack?.includes('clarity.ms') ||
      error.stack?.includes('google-analytics') ||
      error.stack?.includes('googleapis.com');

    if (isThirdPartyError) {
      logger.debug('Third-party script error caught and isolated', { 
        error: error.message,
        stack: error.stack?.substring(0, 200) + '...',
        type: 'third-party-isolated'
      });

      return { 
        hasError: true, 
        errorInfo: `Third-party script error: ${error.message}` 
      };
    }

    // Se n√£o √© third-party, re-throw para error boundary principal
    throw error;
  }

  componentDidCatch(error: Error, errorInfo: ErrorInfo) {
    const { scriptName = 'unknown' } = this.props;

    // Log detalhado para debugging
    logger.warn('Third-party error boundary activated', {
      scriptName,
      error: error.message,
      componentStack: errorInfo.componentStack?.substring(0, 300) + '...',
      errorBoundary: 'ThirdPartyErrorBoundary',
      action: 'error-isolated'
    });
  }

  render() {
    if (this.state.hasError) {
      // Silent failure para third-party components
      const { scriptName = 'Third-party script' } = this.props;

      logger.debug('Third-party component failed silently', {
        scriptName,
        fallbackUsed: 'silent',
        errorInfo: this.state.errorInfo
      });

      return null;
    }

    return this.props.children;
  }
}

/**
 * üõ°Ô∏è SCRIPT ERROR SUPPRESSOR
 */
export const suppressThirdPartyErrors = (): (() => void) => {
  const originalError = window.onerror;

  window.onerror = (message, source, lineno, colno, error) => {
    const errorStr = String(message);
    const sourceStr = String(source || '');

    const thirdPartyPatterns = [
      'clarity.ms',
      'Cannot read properties of undefined',
      's05cslzjy5'
    ];

    const isThirdPartyError = thirdPartyPatterns.some(pattern =>
      errorStr.includes(pattern) || sourceStr.includes(pattern)
    );

    if (isThirdPartyError) {
      logger.debug('Global third-party error suppressed', {
        message: errorStr.substring(0, 100) + '...',
        source: sourceStr.substring(0, 100) + '...',
        status: 'suppressed-global'
      });

      return true; // Prevent error from showing in console
    }

    return originalError 
      ? originalError(message, source, lineno, colno, error) 
      : false;
  };

  return () => {
    window.onerror = originalError;
    logger.debug('Third-party error suppression cleaned up');
  };
};

export default ThirdPartyErrorBoundary;

import React, { useState, useEffect } from 'react';
import { CheckCircle, AlertCircle, XCircle, Info, X } from 'lucide-react';
import { cn } from '../../lib/utils';

export type ToastType = 'success' | 'error' | 'warning' | 'info';

export interface ToastProps {
  id: string;
  type: ToastType;
  title: string;
  message?: string;
  duration?: number;
  onClose: (id: string) => void;
  className?: string;
}

export const Toast: React.FC<ToastProps> = ({
  id,
  type,
  title,
  message,
  duration = 5000,
  onClose,
  className
}) => {
  const [isVisible, setIsVisible] = useState(false);
  const [isExiting, setIsExiting] = useState(false);

  useEffect(() => {
    // Anima√ß√£o de entrada
    const enterTimer = setTimeout(() => setIsVisible(true), 50);

    // Auto-dismiss
    if (duration > 0) {
      const exitTimer = setTimeout(() => {
        setIsExiting(true);
        setTimeout(() => onClose(id), 300);
      }, duration);

      return () => {
        clearTimeout(enterTimer);
        clearTimeout(exitTimer);
      };
    }

    return () => clearTimeout(enterTimer);
  }, [id, duration, onClose]);

  const handleClose = () => {
    setIsExiting(true);
    setTimeout(() => onClose(id), 300);
  };

  const getIcon = () => {
    switch (type) {
      case 'success':
        return <CheckCircle className="w-5 h-5 text-green-600" />;
      case 'error':
        return <XCircle className="w-5 h-5 text-red-600" />;
      case 'warning':
        return <AlertCircle className="w-5 h-5 text-yellow-600" />;
      case 'info':
        return <Info className="w-5 h-5 text-blue-600" />;
      default:
        return null;
    }
  };

  const getColors = () => {
    switch (type) {
      case 'success':
        return 'bg-green-50 border-green-200 dark:bg-green-900/20 dark:border-green-800';
      case 'error':
        return 'bg-red-50 border-red-200 dark:bg-red-900/20 dark:border-red-800';
      case 'warning':
        return 'bg-yellow-50 border-yellow-200 dark:bg-yellow-900/20 dark:border-yellow-800';
      case 'info':
        return 'bg-blue-50 border-blue-200 dark:bg-blue-900/20 dark:border-blue-800';
      default:
        return 'bg-gray-50 border-gray-200 dark:bg-gray-900/20 dark:border-gray-800';
    }
  };

  return (
    <div
      className={cn(
        'pointer-events-auto w-full max-w-sm overflow-hidden rounded-lg border shadow-lg transition-all duration-300 ease-in-out',
        getColors(),
        isVisible && !isExiting
          ? 'translate-x-0 opacity-100'
          : 'translate-x-full opacity-0',
        className
      )}
      role="alert"
      aria-live="polite"
    >
      <div className="p-4">
        <div className="flex items-start">
          <div className="flex-shrink-0">
            {getIcon()}
          </div>

          <div className="ml-3 w-0 flex-1">
            <p className={cn(
              'text-sm font-medium',
              type === 'success' && 'text-green-800 dark:text-green-200',
              type === 'error' && 'text-red-800 dark:text-red-200',
              type === 'warning' && 'text-yellow-800 dark:text-yellow-200',
              type === 'info' && 'text-blue-800 dark:text-blue-200'
            )}>
              {title}
            </p>

            {message && (
              <p className={cn(
                'mt-1 text-sm',
                type === 'success' && 'text-green-700 dark:text-green-300',
                type === 'error' && 'text-red-700 dark:text-red-300',
                type === 'warning' && 'text-yellow-700 dark:text-yellow-300',
                type === 'info' && 'text-blue-700 dark:text-blue-300'
              )}>
                {message}
              </p>
            )}
          </div>

          <div className="ml-4 flex flex-shrink-0">
            <button
              className={cn(
                'inline-flex rounded-md p-1.5 transition-colors hover:bg-black/10 focus:outline-none focus:ring-2 focus:ring-offset-2',
                type === 'success' && 'text-green-500 focus:ring-green-600',
                type === 'error' && 'text-red-500 focus:ring-red-600',
                type === 'warning' && 'text-yellow-500 focus:ring-yellow-600',
                type === 'info' && 'text-blue-500 focus:ring-blue-600'
              )}
              onClick={handleClose}
              aria-label="Fechar notifica√ß√£o"
            >
              <X className="h-4 w-4" />
            </button>
          </div>
        </div>
      </div>

      {/* Progress bar para auto-dismiss */}
      {duration > 0 && (
        <div className="h-1 bg-black/10">
          <div
            className={cn(
              'h-full transition-all ease-linear',
              type === 'success' && 'bg-green-600',
              type === 'error' && 'bg-red-600',
              type === 'warning' && 'bg-yellow-600',
              type === 'info' && 'bg-blue-600'
            )}
            style={{
              animation: `shrink ${duration}ms linear forwards`
            }}
          />
        </div>
      )}

      <style jsx>{`
        @keyframes shrink {
          from { width: 100%; }
          to { width: 0%; }
        }
      `}</style>
    </div>
  );
};

// Provider de contexto para toasts
interface ToastContextType {
  showToast: (type: ToastType, title: string, message?: string, duration?: number) => void;
}

const ToastContext = React.createContext<ToastContextType | undefined>(undefined);

export const useToast = () => {
  const context = React.useContext(ToastContext);
  if (!context) {
    throw new Error('useToast deve ser usado dentro de ToastProvider');
  }
  return context;
};

interface ToastState {
  id: string;
  type: ToastType;
  title: string;
  message?: string;
  duration?: number;
}

export const ToastProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [toasts, setToasts] = useState<ToastState[]>([]);

  const showToast = (type: ToastType, title: string, message?: string, duration?: number) => {
    const id = Math.random().toString(36).substr(2, 9);
    const newToast: ToastState = { id, type, title, message, duration };

    setToasts(prev => [...prev, newToast]);
  };

  const removeToast = (id: string) => {
    setToasts(prev => prev.filter(toast => toast.id !== id));
  };

  return (
    <ToastContext.Provider value={{ showToast }}>
      {children}

      {/* Container de toasts */}
      <div className="fixed top-4 right-4 z-[100] flex flex-col gap-2 pointer-events-none">
        {toasts.map(toast => (
          <Toast
            key={toast.id}
            {...toast}
            onClose={removeToast}
          />
        ))}
      </div>
    </ToastContext.Provider>
  );
};

