import React, { createContext, useContext, useEffect, useState } from "react";
import type { User as FirebaseUser } from 'firebase/auth';
import { onAuthStateChanged } from 'firebase/auth';
import { auth, isFirebaseConfigured } from "../firebaseConfig";
import { 
  AuthContextType, 
  ExtendedUser, 
  UserRole, 
  UserPermissions,
  isAdminEmail,
  getUserRole,
  getPermissionsForRole,
  DEFAULT_USER_PERMISSIONS
} from "../types/auth";
import { adminService } from '../services/adminService';
import { createLogger } from '../utils/logger';

const logger = createLogger('AuthContext');

const AuthContext = createContext<AuthContextType>({ 
  // User state
  currentUser: null,
  firebaseUser: null,
  loading: true,
  isFirebaseEnabled: false,

  // Role helpers
  isAdmin: false,
  isUser: false,
  hasRole: () => false,
  hasPermission: () => false,

  // Auth actions
  checkPermissions: () => DEFAULT_USER_PERMISSIONS,
  refreshUserData: async () => {},
  updateUserPreferences: async () => {},
});

export const useAuth = () => {
  return useContext(AuthContext);
};

export const AuthProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [firebaseUser, setFirebaseUser] = useState<FirebaseUser | null>(null);
  const [currentUser, setCurrentUser] = useState<ExtendedUser | null>(null);
  const [loading, setLoading] = useState(true);

  // === HELPER FUNCTIONS ===

  const createExtendedUser = (fbUser: FirebaseUser): ExtendedUser => {
    const role = getUserRole(fbUser.email);
    const permissions = getPermissionsForRole(role);

    logger.info('Creating extended user', {
      uid: fbUser.uid,
      email: fbUser.email,
      role,
      isAdmin: role === 'admin'
    });

    return {
      // Dados b√°sicos do Firebase
      uid: fbUser.uid,
      email: fbUser.email,
      displayName: fbUser.displayName,
      photoURL: fbUser.photoURL,
      emailVerified: fbUser.emailVerified,

      // Dados estendidos
      role,
      permissions,

      // Metadados (defaults for now, pode vir do Firestore futuramente)
      createdAt: new Date(fbUser.metadata.creationTime || Date.now()),
      lastLoginAt: new Date(fbUser.metadata.lastSignInTime || Date.now()),
      lastActiveAt: new Date(),

      // Configura√ß√µes (defaults)
      preferences: {
        theme: 'auto',
        language: 'pt-BR',
        notifications: true,
        analyticsOptIn: true,
      },

      // Status
      isActive: true,
      isBlocked: false,

      // Admin espec√≠fico
      adminMetadata: role === 'admin' ? {
        adminSince: new Date(fbUser.metadata.creationTime || Date.now()),
        lastAdminAction: new Date(),
      } : undefined,
    };
  };

  const updateExtendedUser = (fbUser: FirebaseUser | null) => {
    if (!fbUser) {
      setCurrentUser(null);
      setFirebaseUser(null);
      // Clear admin service user when logged out
      adminService.setCurrentUser(null);
      return;
    }

    const extendedUser = createExtendedUser(fbUser);
    setCurrentUser(extendedUser);
    setFirebaseUser(fbUser);

    // Update admin service with current user
    adminService.setCurrentUser(extendedUser.email);

    logger.debug('Extended user updated', {
      uid: extendedUser.uid,
      role: extendedUser.role,
      hasAdminPerms: extendedUser.permissions.canViewAdminDashboard,
      adminServiceRole: adminService.getCurrentRole()?.role
    });
  };

  // === ROLE HELPERS ===

  const hasRole = (role: UserRole): boolean => {
    if (!currentUser) return false;
    return currentUser.role === role;
  };

  const hasPermission = (permission: keyof UserPermissions): boolean => {
    if (!currentUser) return false;
    return currentUser.permissions[permission];
  };

  const checkPermissions = (): UserPermissions => {
    if (!currentUser) return DEFAULT_USER_PERMISSIONS;
    return currentUser.permissions;
  };

  // === AUTH ACTIONS ===

  const refreshUserData = async (): Promise<void> => {
    if (!firebaseUser) return;

    logger.info('Refreshing user data', { uid: firebaseUser.uid });

    try {
      // Recarregar dados do Firebase
      await firebaseUser.reload();

      // Recriar extended user com dados atualizados
      updateExtendedUser(firebaseUser);

      logger.info('User data refreshed successfully');
    } catch (error) {
      logger.error('Failed to refresh user data', { error });
    }
  };

  const updateUserPreferences = async (
    preferences: Partial<ExtendedUser['preferences']>
  ): Promise<void> => {
    if (!currentUser) return;

    logger.info('Updating user preferences', { 
      uid: currentUser.uid, 
      preferences 
    });

    try {
      const updatedUser: ExtendedUser = {
        ...currentUser,
        preferences: {
          ...currentUser.preferences,
          ...preferences,
        },
        lastActiveAt: new Date(),
      };

      setCurrentUser(updatedUser);

      // TODO: Salvar no Firestore em implementa√ß√£o futura
      logger.info('User preferences updated successfully');
    } catch (error) {
      logger.error('Failed to update user preferences', { error });
    }
  };

  // === FIREBASE AUTH LISTENER ===

  useEffect(() => {
    if (!isFirebaseConfigured || !auth) {
      // Firebase n√£o configurado - modo sem autentica√ß√£o
      logger.warn('Firebase not configured - running in demo mode');
      updateExtendedUser(null);
      setLoading(false);
      return;
    }

    logger.info('Setting up Firebase auth listener');

    const unsubscribe = onAuthStateChanged(auth, (fbUser) => {
      logger.info('Auth state changed', {
        isLoggedIn: !!fbUser,
        email: fbUser?.email,
        uid: fbUser?.uid
      });

      updateExtendedUser(fbUser);
      setLoading(false);
    });

    return () => {
      logger.debug('Cleaning up Firebase auth listener');
      unsubscribe();
    };
  }, []);

  // === COMPUTED VALUES ===

  const isAdmin = currentUser?.role === 'admin';
  const isUser = currentUser?.role === 'user';

  // === CONTEXT VALUE ===

  const value: AuthContextType = {
    // User state
    currentUser,
    firebaseUser,
    loading,
    isFirebaseEnabled: isFirebaseConfigured,

    // Role helpers
    isAdmin,
    isUser,
    hasRole,
    hasPermission,

    // Auth actions
    checkPermissions,
    refreshUserData,
    updateUserPreferences,
  };

  // === DEBUG LOGGING ===

  useEffect(() => {
    if (process.env.NODE_ENV === 'development' && currentUser) {
      logger.debug('AuthContext Debug Info', {
        user: {
          uid: currentUser.uid,
          email: currentUser.email,
          role: currentUser.role,
          isAdmin,
          isUser,
        },
        permissions: Object.entries(currentUser.permissions)
          .filter(([_, value]) => value)
          .map(([key]) => key),
        firebaseEnabled: isFirebaseConfigured
      });
    }
  }, [currentUser, isAdmin, isUser]);

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
};

import { useState, useEffect, useCallback, useRef } from 'react';
import { aiAnalyticsService, PredictiveInsight, UserSegment } from '../services/aiAnalyticsService';
import { useAuth } from '../contexts/AuthContext';
import { createLogger } from '../utils/logger';

const logger = createLogger('useAIAnalytics');

export interface AIAnalyticsState {
  insights: PredictiveInsight[];
  segments: UserSegment[];
  isLoading: boolean;
  error: string | null;
  lastUpdated: Date | null;
}

export interface AIAnalyticsActions {
  trackAction: (actionType: string, context: string, metadata?: Record<string, any>) => void;
  predictNextAction: (context: string) => Promise<{
    action: string;
    confidence: number;
    reasoning: string;
  } | null>;
  refreshInsights: () => Promise<void>;
  getSmartCachingStrategy: () => {
    preloadResources: string[];
    cacheStrategy: 'aggressive' | 'moderate' | 'minimal';
    reasoning: string;
  };
}

export const useAIAnalytics = (): AIAnalyticsState & AIAnalyticsActions => {
  const { currentUser } = useAuth();
  const [state, setState] = useState<AIAnalyticsState>({
    insights: [],
    segments: [],
    isLoading: true,
    error: null,
    lastUpdated: null
  });

  const trackingQueue = useRef<Array<{
    actionType: string;
    context: string;
    metadata?: Record<string, any>;
  }>>([]);

  const isInitialized = useRef(false);

  // Internal tracking function
  const trackActionInternal = useCallback((
    actionType: string, 
    context: string, 
    metadata?: Record<string, any>
  ) => {
    if (!currentUser?.uid) {
      logger.warn('Cannot track action: user not authenticated');
      return;
    }

    try {
      aiAnalyticsService.trackBehavior({
        userId: currentUser.uid,
        actionType: actionType as any,
        context,
        metadata: metadata || {}
      });

      logger.debug('Action tracked', { actionType, context, userId: currentUser.uid });
    } catch (error) {
      logger.error('Failed to track action', { error, actionType, context });
    }
  }, [currentUser?.uid]);

  // Initialize AI Analytics
  useEffect(() => {
    const initializeAnalytics = async () => {
      if (isInitialized.current) return;

      try {
        setState(prev => ({ ...prev, isLoading: true, error: null }));

        await aiAnalyticsService.initialize();

        // Load initial data
        const [insights, segments] = await Promise.all([
          aiAnalyticsService.getInsights(currentUser?.uid),
          Promise.resolve(aiAnalyticsService.getUserSegments())
        ]);

        setState(prev => ({
          ...prev,
          insights,
          segments,
          isLoading: false,
          lastUpdated: new Date()
        }));

        // Process any queued tracking events
        if (trackingQueue.current.length > 0) {
          trackingQueue.current.forEach(({ actionType, context, metadata }) => {
            trackActionInternal(actionType, context, metadata);
          });
          trackingQueue.current = [];
        }

        isInitialized.current = true;
        logger.info('AI Analytics initialized successfully', {
          insightsCount: insights.length,
          segmentsCount: segments.length,
          userId: currentUser?.uid
        });
      } catch (error) {
        logger.error('Failed to initialize AI Analytics', { error });
        setState(prev => ({
          ...prev,
          isLoading: false,
          error: error instanceof Error ? error.message : 'Unknown error'
        }));
      }
    };

    initializeAnalytics();
  }, [currentUser?.uid, trackActionInternal]);

  // Auto-refresh insights periodically
  useEffect(() => {
    if (!isInitialized.current) return;

    const interval = setInterval(async () => {
      try {
        const insights = await aiAnalyticsService.getInsights(currentUser?.uid);
        setState(prev => ({
          ...prev,
          insights,
          lastUpdated: new Date()
        }));
        logger.debug('Insights auto-refreshed', { count: insights.length });
      } catch (error) {
        logger.warn('Failed to auto-refresh insights', { error });
      }
    }, 5 * 60 * 1000); // Every 5 minutes

    return () => clearInterval(interval);
  }, [currentUser?.uid]);

  // Public API methods
  const trackAction = useCallback((
    actionType: string, 
    context: string, 
    metadata?: Record<string, any>
  ) => {
    if (!isInitialized.current) {
      // Queue the action for later processing
      trackingQueue.current.push({ actionType, context, metadata });
      logger.debug('Action queued for tracking', { actionType, context });
      return;
    }

    trackActionInternal(actionType, context, metadata);
  }, [trackActionInternal]);

  const predictNextAction = useCallback(async (context: string) => {
    if (!currentUser?.uid || !isInitialized.current) {
      logger.warn('Cannot predict next action: not ready');
      return null;
    }

    try {
      const prediction = await aiAnalyticsService.predictNextAction(currentUser.uid, context);
      logger.debug('Next action predicted', { 
        context, 
        prediction: prediction?.action,
        confidence: prediction?.confidence 
      });
      return prediction;
    } catch (error) {
      logger.error('Failed to predict next action', { error, context });
      return null;
    }
  }, [currentUser?.uid]);

  const refreshInsights = useCallback(async () => {
    if (!isInitialized.current) {
      logger.warn('Cannot refresh insights: not initialized');
      return;
    }

    try {
      setState(prev => ({ ...prev, isLoading: true }));

      const [insights, segments] = await Promise.all([
        aiAnalyticsService.getInsights(currentUser?.uid),
        Promise.resolve(aiAnalyticsService.getUserSegments())
      ]);

      setState(prev => ({
        ...prev,
        insights,
        segments,
        isLoading: false,
        lastUpdated: new Date(),
        error: null
      }));

      logger.info('Insights refreshed manually', { 
        insightsCount: insights.length,
        segmentsCount: segments.length 
      });
    } catch (error) {
      logger.error('Failed to refresh insights', { error });
      setState(prev => ({
        ...prev,
        isLoading: false,
        error: error instanceof Error ? error.message : 'Failed to refresh insights'
      }));
    }
  }, [currentUser?.uid]);

  const getSmartCachingStrategy = useCallback(() => {
    if (!currentUser?.uid || !isInitialized.current) {
      logger.warn('Cannot get caching strategy: not ready');
      return {
        preloadResources: [],
        cacheStrategy: 'minimal' as const,
        reasoning: 'User not authenticated or analytics not initialized'
      };
    }

    try {
      const strategy = aiAnalyticsService.getSmartCachingStrategy(currentUser.uid);
      logger.debug('Caching strategy generated', { 
        strategy: strategy.cacheStrategy,
        resourceCount: strategy.preloadResources.length 
      });
      return strategy;
    } catch (error) {
      logger.error('Failed to get caching strategy', { error });
      return {
        preloadResources: [],
        cacheStrategy: 'minimal' as const,
        reasoning: 'Error generating strategy'
      };
    }
  }, [currentUser?.uid]);

  return {
    // State
    insights: state.insights,
    segments: state.segments,
    isLoading: state.isLoading,
    error: state.error,
    lastUpdated: state.lastUpdated,

    // Actions
    trackAction,
    predictNextAction,
    refreshInsights,
    getSmartCachingStrategy
  };
};

// Custom hook for page-level tracking
export const usePageTracking = (pageName: string, metadata?: Record<string, any>) => {
  const { trackAction } = useAIAnalytics();

  useEffect(() => {
    trackAction('navigate', `page:${pageName}`, {
      ...metadata,
      timestamp: Date.now(),
      url: window.location.pathname
    });
  }, [trackAction, pageName, metadata]);
};

// Custom hook for feature usage tracking
export const useFeatureTracking = () => {
  const { trackAction } = useAIAnalytics();

  const trackFeatureUsage = useCallback((
    featureName: string, 
    action: 'start' | 'complete' | 'error' | 'cancel',
    metadata?: Record<string, any>
  ) => {
    trackAction('feature', `${featureName}:${action}`, {
      ...metadata,
      timestamp: Date.now(),
      feature: featureName,
      action
    });
  }, [trackAction]);

  return { trackFeatureUsage };
};

/**
 * üß† MEMORY LEAK DETECTION HOOK
 * React hook para detectar e prevenir vazamentos de mem√≥ria
 */

import { useEffect, useRef, useCallback } from 'react';
import { logger } from '../utils/logger';
import { performanceService } from '../services/performance';

// =============================================================================
// TYPES & INTERFACES
// =============================================================================

export interface MemoryLeakDetectionOptions {
  enabled?: boolean;
  checkInterval?: number; // milliseconds
  componentName?: string;
  warnThreshold?: number; // MB
  errorThreshold?: number; // MB
  onMemoryLeak?: (leak: MemoryLeakInfo) => void;
}

export interface MemoryLeakInfo {
  componentName: string;
  memoryUsed: number;
  memoryDelta: number;
  timestamp: number;
  severity: 'warn' | 'error';
  suggestions: string[];
}

export interface ComponentMemoryStats {
  initialMemory: number;
  currentMemory: number;
  peakMemory: number;
  allocations: number;
  leaksPrevented: number;
}

// =============================================================================
// MEMORY LEAK DETECTION HOOK
// =============================================================================

export function useMemoryLeak(options: MemoryLeakDetectionOptions = {}) {
  const {
    enabled = true,
    checkInterval = 10000, // 10 seconds
    componentName = 'UnknownComponent',
    warnThreshold = 10, // 10MB
    errorThreshold = 25, // 25MB
    onMemoryLeak,
  } = options;

  const memoryStatsRef = useRef<ComponentMemoryStats>({
    initialMemory: 0,
    currentMemory: 0,
    peakMemory: 0,
    allocations: 0,
    leaksPrevented: 0,
  });

  const intervalRef = useRef<NodeJS.Timeout | null>(null);
  const mountTimeRef = useRef<number>(Date.now());
  const cleanupFunctionsRef = useRef<(() => void)[]>([]);

  /**
   * Get current memory usage in MB
   */
  const getCurrentMemory = useCallback((): number => {
    const memory = (performance as any).memory;
    if (!memory) return 0;
    return memory.usedJSHeapSize / (1024 * 1024); // Convert to MB
  }, []);

  /**
   * Check for memory leaks
   */
  const checkMemoryLeak = useCallback(() => {
    if (!enabled) return;

    const currentMemory = getCurrentMemory();
    const stats = memoryStatsRef.current;

    // Update stats
    stats.currentMemory = currentMemory;
    stats.peakMemory = Math.max(stats.peakMemory, currentMemory);
    stats.allocations++;

    // Calculate memory delta since mount
    const memoryDelta = currentMemory - stats.initialMemory;

    // Check thresholds
    let severity: 'warn' | 'error' | null = null;

    if (memoryDelta > errorThreshold) {
      severity = 'error';
    } else if (memoryDelta > warnThreshold) {
      severity = 'warn';
    }

    if (severity) {
      const leakInfo: MemoryLeakInfo = {
        componentName,
        memoryUsed: currentMemory,
        memoryDelta,
        timestamp: Date.now(),
        severity,
        suggestions: generateSuggestions(memoryDelta, severity),
      };

      // Log the leak
      const logLevel = severity === 'error' ? 'error' : 'warn';
      logger[logLevel]('Memory leak detected', {
        component: componentName,
        memoryUsed: `${currentMemory.toFixed(2)}MB`,
        memoryDelta: `+${memoryDelta.toFixed(2)}MB`,
        mountTime: Date.now() - mountTimeRef.current,
        severity,
      }, 'MEMORY_LEAK');

      // Record performance metric
      performanceService.recordMetric(
        `memory_leak_${componentName}`,
        memoryDelta,
        'bytes',
        'memory',
        {
          component: componentName,
          severity,
          suggestions: leakInfo.suggestions,
        }
      );

      // Call custom handler
      if (onMemoryLeak) {
        onMemoryLeak(leakInfo);
      }
    }
  }, [enabled, componentName, warnThreshold, errorThreshold, onMemoryLeak, getCurrentMemory]);

  /**
   * Register cleanup function
   */
  const addCleanup = useCallback((cleanupFn: () => void) => {
    cleanupFunctionsRef.current.push(cleanupFn);

    // Return unregister function
    return () => {
      const index = cleanupFunctionsRef.current.indexOf(cleanupFn);
      if (index > -1) {
        cleanupFunctionsRef.current.splice(index, 1);
      }
    };
  }, []);

  /**
   * Force garbage collection (development only)
   */
  const forceGC = useCallback(() => {
    if (typeof window !== 'undefined' && (window as any).gc) {
      (window as any).gc();
      logger.debug('Forced garbage collection', { component: componentName }, 'MEMORY_LEAK');
    } else {
      logger.warn('Garbage collection not available', { component: componentName }, 'MEMORY_LEAK');
    }
  }, [componentName]);

  /**
   * Get current memory stats
   */
  const getMemoryStats = useCallback((): ComponentMemoryStats => {
    return { ...memoryStatsRef.current };
  }, []);

  /**
   * Reset memory tracking
   */
  const resetMemoryTracking = useCallback(() => {
    const initialMemory = getCurrentMemory();
    memoryStatsRef.current = {
      initialMemory,
      currentMemory: initialMemory,
      peakMemory: initialMemory,
      allocations: 0,
      leaksPrevented: 0,
    };
    mountTimeRef.current = Date.now();
  }, [getCurrentMemory]);

  // Initialize memory tracking on mount
  useEffect(() => {
    if (!enabled) return;

    const initialMemory = getCurrentMemory();
    memoryStatsRef.current.initialMemory = initialMemory;
    memoryStatsRef.current.currentMemory = initialMemory;
    memoryStatsRef.current.peakMemory = initialMemory;

    logger.debug('Memory leak detection initialized', {
      component: componentName,
      initialMemory: `${initialMemory.toFixed(2)}MB`,
      checkInterval: `${checkInterval}ms`,
    }, 'MEMORY_LEAK');

    // Start periodic checks
    intervalRef.current = setInterval(checkMemoryLeak, checkInterval);

    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [enabled, componentName, checkInterval, checkMemoryLeak, getCurrentMemory]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      // Run all registered cleanup functions
      cleanupFunctionsRef.current.forEach(cleanup => {
        try {
          cleanup();
          memoryStatsRef.current.leaksPrevented++;
        } catch (error) {
          logger.error('Error in cleanup function', {
            component: componentName,
            error: error instanceof Error ? error.message : 'Unknown',
          }, 'MEMORY_LEAK');
        }
      });

      // Clear cleanup array
      cleanupFunctionsRef.current = [];

      // Log final stats
      const stats = memoryStatsRef.current;
      const finalMemory = getCurrentMemory();
      const totalTime = Date.now() - mountTimeRef.current;

      logger.info('Component unmounted - Memory leak detection summary', {
        component: componentName,
        initialMemory: `${stats.initialMemory.toFixed(2)}MB`,
        finalMemory: `${finalMemory.toFixed(2)}MB`,
        peakMemory: `${stats.peakMemory.toFixed(2)}MB`,
        memoryDelta: `${(finalMemory - stats.initialMemory).toFixed(2)}MB`,
        totalTime: `${totalTime}ms`,
        allocations: stats.allocations,
        leaksPrevented: stats.leaksPrevented,
      }, 'MEMORY_LEAK');
    };
  }, [componentName, getCurrentMemory]);

  return {
    // Stats
    getMemoryStats,
    getCurrentMemory,

    // Actions
    checkMemoryLeak,
    forceGC,
    resetMemoryTracking,
    addCleanup,

    // Utilities
    isEnabled: enabled,
    componentName,
  };
}

// =============================================================================
// UTILITY FUNCTIONS
// =============================================================================

function generateSuggestions(memoryDelta: number, severity: 'warn' | 'error'): string[] {
  const suggestions: string[] = [];

  if (severity === 'error') {
    suggestions.push('Critical memory leak detected - immediate action required');
    suggestions.push('Check for unremoved event listeners');
    suggestions.push('Verify all subscriptions are cancelled in useEffect cleanup');
    suggestions.push('Look for circular references in object structures');
    suggestions.push('Consider using WeakMap/WeakSet for temporary references');
  } else {
    suggestions.push('Potential memory leak - monitor component');
    suggestions.push('Review useEffect dependencies and cleanup functions');
    suggestions.push('Check if large objects are being retained unnecessarily');
    suggestions.push('Consider memoization to prevent unnecessary re-renders');
  }

  if (memoryDelta > 50) {
    suggestions.push('Memory usage is extremely high - investigate large data structures');
  }

  return suggestions;
}

// =============================================================================
// HIGHER-ORDER COMPONENT
// =============================================================================

export function withMemoryLeakDetection<P extends object>(
  Component: React.ComponentType<P>,
  options?: MemoryLeakDetectionOptions
) {
  const WrappedComponent = (props: P) => {
    const memoryLeak = useMemoryLeak({
      componentName: Component.displayName || Component.name || 'WrappedComponent',
      ...options,
    });

    return <Component {...props} />;
  };

  WrappedComponent.displayName = `withMemoryLeakDetection(${Component.displayName || Component.name})`;

  return WrappedComponent;
}

// =============================================================================
// MEMORY MANAGEMENT UTILITIES
// =============================================================================

/**
 * Create a memory-safe event listener
 */
export function createSafeEventListener(
  target: EventTarget,
  event: string,
  handler: EventListener,
  options?: boolean | AddEventListenerOptions
): () => void {
  target.addEventListener(event, handler, options);

  return () => {
    target.removeEventListener(event, handler, options);
  };
}

/**
 * Create a memory-safe interval
 */
export function createSafeInterval(
  callback: () => void,
  delay: number
): () => void {
  const intervalId = setInterval(callback, delay);

  return () => {
    clearInterval(intervalId);
  };
}

/**
 * Create a memory-safe timeout
 */
export function createSafeTimeout(
  callback: () => void,
  delay: number
): () => void {
  const timeoutId = setTimeout(callback, delay);

  return () => {
    clearTimeout(timeoutId);
  };
}

/**
 * Create a memory-safe subscription
 */
export function createSafeSubscription<T>(
  subscribe: (callback: (value: T) => void) => () => void,
  callback: (value: T) => void
): () => void {
  return subscribe(callback);
}

