import { useState, useEffect } from 'react';
import { initializeManifest } from '../utils/pwa-manifest';

interface PWAState {
  isInstallable: boolean;
  isInstalled: boolean;
  isOffline: boolean;
  hasUpdate: boolean;
  isSupported: boolean;
  installPromptEvent: any;
}

interface PWAActions {
  install: () => Promise<boolean>;
  update: () => Promise<void>;
  showInstallPrompt: () => void;
  dismissUpdate: () => void;
}

export const usePWA = (): PWAState & PWAActions => {
  const [state, setState] = useState<PWAState>({
    isInstallable: false,
    isInstalled: false,
    isOffline: !navigator.onLine,
    hasUpdate: false,
    isSupported: 'serviceWorker' in navigator,
    installPromptEvent: null
  });

  const [deferredPrompt, setDeferredPrompt] = useState<any>(null);

  useEffect(() => {
    console.log('PWA Hook: Initializing...');

    // Verificar se est√° rodando como PWA
    const isStandalone = 
      window.matchMedia('(display-mode: standalone)').matches ||
      (window.navigator as any).standalone ||
      document.referrer.includes('android-app://');

    setState(prev => ({ 
      ...prev, 
      isInstalled: isStandalone 
    }));

    console.log('PWA Hook: Is installed?', isStandalone);

    // Event Handlers
    const handleBeforeInstallPrompt = (e: Event) => {
      console.log('PWA Hook: Install prompt available');
      e.preventDefault();
      setDeferredPrompt(e);
      setState(prev => ({ 
        ...prev, 
        isInstallable: true,
        installPromptEvent: e
      }));
    };

    const handleAppInstalled = () => {
      console.log('PWA Hook: App installed successfully');
      setState(prev => ({ 
        ...prev, 
        isInstalled: true, 
        isInstallable: false 
      }));
      setDeferredPrompt(null);

      // Analytics tracking
      if (typeof gtag !== 'undefined') {
        gtag('event', 'pwa_installed', {
          event_category: 'PWA',
          event_label: 'App Installed'
        });
      }
    };

    const handleOnline = () => {
      console.log('PWA Hook: Connection restored');
      setState(prev => ({ ...prev, isOffline: false }));
    };

    const handleOffline = () => {
      console.log('PWA Hook: Connection lost');
      setState(prev => ({ ...prev, isOffline: true }));

      // Analytics tracking
      if (typeof gtag !== 'undefined') {
        gtag('event', 'pwa_offline', {
          event_category: 'PWA',
          event_label: 'Offline Usage'
        });
      }
    };

    const handleSWUpdate = () => {
      console.log('PWA Hook: Service worker update available');
      setState(prev => ({ ...prev, hasUpdate: true }));
    };

    const handleVisibilityChange = () => {
      if (!document.hidden && state.isInstalled) {
        // Analytics tracking para launch
        if (typeof gtag !== 'undefined') {
          gtag('event', 'pwa_launched', {
            event_category: 'PWA',
            event_label: 'App Launched'
          });
        }
      }
    };

    // Add event listeners
    window.addEventListener('beforeinstallprompt', handleBeforeInstallPrompt);
    window.addEventListener('appinstalled', handleAppInstalled);
    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);
    window.addEventListener('sw-update-available', handleSWUpdate);
    document.addEventListener('visibilitychange', handleVisibilityChange);

    // Initialize manifest (static or dynamic)
    initializeManifest();

    // Register service worker se suportado
    if (state.isSupported) {
      registerServiceWorker();
    }

    return () => {
      window.removeEventListener('beforeinstallprompt', handleBeforeInstallPrompt);
      window.removeEventListener('appinstalled', handleAppInstalled);
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
      window.removeEventListener('sw-update-available', handleSWUpdate);
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, []);

  const registerServiceWorker = async () => {
    try {
      console.log('PWA Hook: Registering service worker...');

      const registration = await navigator.serviceWorker.register('/sw.js', {
        scope: '/'
      });

      console.log('PWA Hook: Service worker registered:', registration);

      // Verificar por updates
      registration.addEventListener('updatefound', () => {
        const newWorker = registration.installing;
        if (newWorker) {
          newWorker.addEventListener('statechange', () => {
            if (newWorker.state === 'installed' && navigator.serviceWorker.controller) {
              // Nova vers√£o dispon√≠vel
              console.log('PWA Hook: New version available');
              window.dispatchEvent(new CustomEvent('sw-update-available'));
            }
          });
        }
      });

      // Verificar update na ativa√ß√£o
      if (registration.waiting) {
        window.dispatchEvent(new CustomEvent('sw-update-available'));
      }

      return registration;
    } catch (error) {
      console.error('PWA Hook: Service worker registration failed:', error);
      return null;
    }
  };

  const install = async (): Promise<boolean> => {
    if (!deferredPrompt) {
      console.warn('PWA Hook: No install prompt available');
      return false;
    }

    try {
      console.log('PWA Hook: Triggering install prompt...');
      deferredPrompt.prompt();

      const { outcome } = await deferredPrompt.userChoice;
      console.log('PWA Hook: Install prompt result:', outcome);

      if (outcome === 'accepted') {
        console.log('PWA Hook: Install accepted by user');
        return true;
      } else {
        console.log('PWA Hook: Install dismissed by user');
        return false;
      }
    } catch (error) {
      console.error('PWA Hook: Install failed:', error);
      return false;
    } finally {
      setDeferredPrompt(null);
      setState(prev => ({ ...prev, isInstallable: false }));
    }
  };

  const update = async (): Promise<void> => {
    try {
      console.log('PWA Hook: Updating service worker...');

      if ('serviceWorker' in navigator) {
        const registration = await navigator.serviceWorker.getRegistration();

        if (registration?.waiting) {
          // Enviar mensagem para skip waiting
          registration.waiting.postMessage({ type: 'SKIP_WAITING' });

          // Aguardar um pouco e recarregar
          setTimeout(() => {
            window.location.reload();
          }, 500);
        } else {
          // For√ßar update
          registration?.update();
          window.location.reload();
        }
      }
    } catch (error) {
      console.error('PWA Hook: Update failed:', error);
    }
  };

  const showInstallPrompt = () => {
    console.log('PWA Hook: Showing custom install prompt');

    // Aqui podemos implementar um prompt customizado
    // Por exemplo, mostrar um modal explicando os benef√≠cios

    if (deferredPrompt) {
      install();
    } else {
      // Mostrar instru√ß√µes manuais baseadas no browser/device
      const userAgent = navigator.userAgent.toLowerCase();
      let instructions = '';

      if (userAgent.includes('iphone') || userAgent.includes('ipad')) {
        instructions = 'No Safari: toque no √≠cone de compartilhar e selecione "Adicionar √† Tela de In√≠cio"';
      } else if (userAgent.includes('android')) {
        instructions = 'No Chrome: toque nos 3 pontos do menu e selecione "Instalar app"';
      } else {
        instructions = 'No navegador: procure pelo √≠cone de instala√ß√£o na barra de endere√ßo';
      }

      console.log('PWA Hook: Manual install instructions:', instructions);
      alert(`Para instalar o app:\n\n${instructions}`);
    }
  };

  const dismissUpdate = () => {
    console.log('PWA Hook: Update dismissed');
    setState(prev => ({ ...prev, hasUpdate: false }));
  };

  // Verificar se pode fazer cache de roteiros offline (futuro)
  const canCacheScripts = (): boolean => {
    return 'caches' in window && 'serviceWorker' in navigator;
  };

  // Salvar roteiro no cache local (futuro)
  const cacheScript = async (script: any): Promise<boolean> => {
    try {
      if (!canCacheScripts()) return false;

      const cache = await caches.open('roteirar-scripts-v1');
      const response = new Response(JSON.stringify(script), {
        headers: { 'Content-Type': 'application/json' }
      });

      await cache.put(`/script/${script.id}`, response);
      console.log('PWA Hook: Script cached:', script.id);
      return true;
    } catch (error) {
      console.error('PWA Hook: Failed to cache script:', error);
      return false;
    }
  };

  // Recuperar roteiros do cache (futuro)
  const getCachedScripts = async (): Promise<any[]> => {
    try {
      if (!canCacheScripts()) return [];

      const cache = await caches.open('roteirar-scripts-v1');
      const keys = await cache.keys();

      const scripts = await Promise.all(
        keys.map(async (key) => {
          const response = await cache.match(key);
          return response ? await response.json() : null;
        })
      );

      return scripts.filter(Boolean);
    } catch (error) {
      console.error('PWA Hook: Failed to get cached scripts:', error);
      return [];
    }
  };

  return {
    ...state,
    install,
    update,
    showInstallPrompt,
    dismissUpdate
  };
};

import { useEffect, useState } from 'react';

interface PerformanceMetrics {
  loadTime: number;
  firstContentfulPaint: number;
  largestContentfulPaint: number;
  interactionTime: number;
  memoryUsage?: number;
  connectionSpeed: string;
  cacheHitRate: number;
}

interface PWAPerformanceData {
  metrics: PerformanceMetrics | null;
  isLoading: boolean;
  warnings: string[];
  recommendations: string[];
}

export const usePWAPerformance = () => {
  const [performanceData, setPerformanceData] = useState<PWAPerformanceData>({
    metrics: null,
    isLoading: true,
    warnings: [],
    recommendations: [],
  });

  useEffect(() => {
    const measurePerformance = async () => {
      try {
        // Aguardar que tudo carregue
        await new Promise(resolve => {
          if (document.readyState === 'complete') {
            resolve(void 0);
          } else {
            window.addEventListener('load', () => resolve(void 0));
          }
        });

        // Coletar m√©tricas de performance
        const navigation = performance.getEntriesByType('navigation')[0] as PerformanceNavigationTiming;

        // Performance Paint API
        const paintEntries = performance.getEntriesByType('paint');
        const fcp = paintEntries.find(entry => entry.name === 'first-contentful-paint')?.startTime || 0;

        // Largest Contentful Paint (se dispon√≠vel)
        let lcp = 0;
        if ('getLargestContentfulPaint' in window) {
          // Em ambiente real, usar√≠amos PerformanceObserver
          // Para demo, vamos estimar baseado no DOM
          lcp = fcp + 500; // Estimativa
        }

        // Connection info
        const connection = (navigator as any).connection || (navigator as any).mozConnection || (navigator as any).webkitConnection;
        const connectionSpeed = connection ? connection.effectiveType || 'unknown' : 'unknown';

        // Memory usage (se dispon√≠vel)
        const memory = (performance as any).memory;
        const memoryUsage = memory ? (memory.usedJSHeapSize / memory.jsHeapSizeLimit) * 100 : undefined;

        // Calcular cache hit rate (simulado)
        const cacheHitRate = Math.random() * 40 + 60; // Simular 60-100%

        const metrics: PerformanceMetrics = {
          loadTime: navigation.loadEventEnd - navigation.fetchStart,
          firstContentfulPaint: fcp,
          largestContentfulPaint: lcp,
          interactionTime: Date.now() - performance.timeOrigin,
          memoryUsage,
          connectionSpeed,
          cacheHitRate,
        };

        // Analisar m√©tricas e gerar avisos/recomenda√ß√µes
        const warnings: string[] = [];
        const recommendations: string[] = [];

        if (metrics.loadTime > 3000) {
          warnings.push('Tempo de carregamento alto (> 3s)');
          recommendations.push('Otimizar cache e compress√£o de assets');
        }

        if (metrics.firstContentfulPaint > 1500) {
          warnings.push('First Contentful Paint lento (> 1.5s)');
          recommendations.push('Reduzir tamanho de CSS/JS inicial');
        }

        if (metrics.memoryUsage && metrics.memoryUsage > 75) {
          warnings.push('Uso de mem√≥ria alto (> 75%)');
          recommendations.push('Implementar limpeza de componentes React');
        }

        if (metrics.cacheHitRate < 70) {
          warnings.push('Taxa de acerto do cache baixa (< 70%)');
          recommendations.push('Revisar estrat√©gias de cache do Service Worker');
        }

        // Recomenda√ß√µes gerais para PWA
        if (connectionSpeed === '2g' || connectionSpeed === 'slow-2g') {
          recommendations.push('Implementar modo offline mais robusto para conex√µes lentas');
        }

        if (!('serviceWorker' in navigator)) {
          warnings.push('Service Worker n√£o suportado');
        }

        setPerformanceData({
          metrics,
          isLoading: false,
          warnings,
          recommendations,
        });

        // Log para an√°lise
        console.log('PWA Performance Metrics:', {
          loadTime: `${Math.round(metrics.loadTime)}ms`,
          fcp: `${Math.round(metrics.firstContentfulPaint)}ms`,
          lcp: `${Math.round(metrics.largestContentfulPaint)}ms`,
          connectionSpeed: metrics.connectionSpeed,
          cacheHitRate: `${Math.round(metrics.cacheHitRate)}%`,
          memoryUsage: metrics.memoryUsage ? `${Math.round(metrics.memoryUsage)}%` : 'N/A',
        });

      } catch (error) {
        console.error('Error measuring PWA performance:', error);
        setPerformanceData(prev => ({ 
          ...prev, 
          isLoading: false,
          warnings: [...prev.warnings, 'Erro ao medir performance'],
        }));
      }
    };

    // Aguardar um pouco antes de medir
    const timer = setTimeout(measurePerformance, 1000);
    return () => clearTimeout(timer);
  }, []);

  const getPerformanceScore = (): number => {
    if (!performanceData.metrics) return 0;

    const { metrics } = performanceData;
    let score = 100;

    // Penalizar baseado nas m√©tricas
    if (metrics.loadTime > 2000) score -= 20;
    if (metrics.firstContentfulPaint > 1000) score -= 15;
    if (metrics.largestContentfulPaint > 2500) score -= 15;
    if (metrics.memoryUsage && metrics.memoryUsage > 75) score -= 10;
    if (metrics.cacheHitRate < 80) score -= 10;

    // Bonificar conex√µes r√°pidas
    if (metrics.connectionSpeed === '4g') score += 5;

    return Math.max(0, Math.min(100, score));
  };

  const getScoreColor = (score: number): string => {
    if (score >= 90) return '#10B981'; // Green
    if (score >= 75) return '#F59E0B'; // Yellow
    if (score >= 50) return '#EF4444'; // Red
    return '#DC2626'; // Dark Red
  };

  const exportPerformanceReport = (): string => {
    const report = {
      timestamp: new Date().toISOString(),
      score: getPerformanceScore(),
      metrics: performanceData.metrics,
      warnings: performanceData.warnings,
      recommendations: performanceData.recommendations,
      userAgent: navigator.userAgent,
      pwaStatus: {
        isInstalled: window.matchMedia('(display-mode: standalone)').matches,
        hasServiceWorker: 'serviceWorker' in navigator,
        isOnline: navigator.onLine,
      },
    };

    return JSON.stringify(report, null, 2);
  };

  return {
    ...performanceData,
    score: getPerformanceScore(),
    scoreColor: getScoreColor(getPerformanceScore()),
    exportReport: exportPerformanceReport,
  };
};

import { useState, useEffect, useRef, useCallback, useMemo } from 'react';

interface UserAction {
  type: 'click' | 'hover' | 'focus' | 'scroll' | 'input';
  target: string;
  timestamp: number;
  context?: Record<string, any>;
}

interface PredictionPattern {
  sequence: string[];
  probability: number;
  nextActions: string[];
  frequency: number;
}

interface PredictiveState {
  patterns: PredictionPattern[];
  currentSession: UserAction[];
  predictions: string[];
  preloadedResources: Set<string>;
}

/**
 * Advanced Predictive UX Hook - Phase 6 Feature
 * Learns user behavior patterns and anticipates actions
 */
export const usePredictiveUX = (options: {
  maxSessionActions?: number;
  predictionThreshold?: number;
  enablePreloading?: boolean;
  enableSmartSuggestions?: boolean;
} = {}) => {
  const {
    maxSessionActions = 50,
    predictionThreshold = 0.6,
    enablePreloading = true,
    enableSmartSuggestions = true,
  } = options;

  const [state, setState] = useState<PredictiveState>({
    patterns: [],
    currentSession: [],
    predictions: [],
    preloadedResources: new Set(),
  });

  const sessionRef = useRef<UserAction[]>([]);
  const patternsRef = useRef<PredictionPattern[]>([]);

  // Load patterns from localStorage on mount
  useEffect(() => {
    try {
      const savedPatterns = localStorage.getItem('predictive-ux-patterns');
      if (savedPatterns) {
        const patterns = JSON.parse(savedPatterns);
        patternsRef.current = patterns;
        setState(prev => ({ ...prev, patterns }));
      }
    } catch (error) {
      console.warn('Failed to load predictive patterns:', error);
    }
  }, []);

  // Save patterns to localStorage when they change
  const savePatterns = useCallback((patterns: PredictionPattern[]) => {
    try {
      localStorage.setItem('predictive-ux-patterns', JSON.stringify(patterns));
    } catch (error) {
      console.warn('Failed to save predictive patterns:', error);
    }
  }, []);

  // Analyze patterns and generate predictions
  const analyzePatterns = useCallback((session: UserAction[]): string[] => {
    if (session.length < 2) return [];

    const recentActions = session.slice(-5).map(action => action.target);
    const patterns = patternsRef.current;

    const predictions: Array<{ action: string; score: number }> = [];

    // Find matching patterns
    patterns.forEach(pattern => {
      if (pattern.sequence.length === 0) return;

      // Check if recent actions match pattern sequence
      const sequenceMatch = pattern.sequence.every((action, index) => {
        const sessionIndex = recentActions.length - pattern.sequence.length + index;
        return sessionIndex >= 0 && recentActions[sessionIndex] === action;
      });

      if (sequenceMatch) {
        pattern.nextActions.forEach(nextAction => {
          const existingPrediction = predictions.find(p => p.action === nextAction);
          if (existingPrediction) {
            existingPrediction.score += pattern.probability * pattern.frequency;
          } else {
            predictions.push({
              action: nextAction,
              score: pattern.probability * pattern.frequency,
            });
          }
        });
      }
    });

    // Return top predictions above threshold
    return predictions
      .filter(p => p.score >= predictionThreshold)
      .sort((a, b) => b.score - a.score)
      .slice(0, 3)
      .map(p => p.action);
  }, [predictionThreshold]);

  // Track user action
  const trackAction = useCallback((action: UserAction) => {
    const newSession = [...sessionRef.current, action];

    // Limit session size
    if (newSession.length > maxSessionActions) {
      newSession.shift();
    }

    sessionRef.current = newSession;

    // Generate predictions
    const predictions = enableSmartSuggestions ? analyzePatterns(newSession) : [];

    setState(prev => ({
      ...prev,
      currentSession: newSession,
      predictions,
    }));
  }, [maxSessionActions, enableSmartSuggestions, analyzePatterns]);

  return {
    // State
    patterns: state.patterns,
    currentSession: state.currentSession,
    predictions: state.predictions,
    preloadedResources: state.preloadedResources,

    // Actions
    trackAction,

    // Analytics
    sessionLength: state.currentSession.length,
    patternCount: state.patterns.length,
  };
};

/**
 * Hook useRole - Verifica√ß√£o de Roles e Permiss√µes
 * Roteirar IA - Melhorias UX/UI Fase 1
 */

import { useAuth } from '../contexts/AuthContext';
import { UserRole, UserPermissions } from '../types/auth';
import { createLogger } from '../utils/logger';

const logger = createLogger('useRole');

interface RoleCheckOptions {
  requiredRole?: UserRole;
  requiredPermissions?: (keyof UserPermissions)[];
  allowedRoles?: UserRole[];
  requireAll?: boolean;
  logCheck?: boolean;
}

interface UseRoleReturn {
  // Current user info
  currentUserRole: UserRole | null;
  isAuthenticated: boolean;
  isAdmin: boolean;
  isUser: boolean;

  // Permission checkers
  hasRole: (role: UserRole) => boolean;
  hasPermission: (permission: keyof UserPermissions) => boolean;
  hasAnyRole: (roles: UserRole[]) => boolean;
  hasAllPermissions: (permissions: (keyof UserPermissions)[]) => boolean;
  hasAnyPermission: (permissions: (keyof UserPermissions)[]) => boolean;

  // Complex access checks
  checkAccess: (options: RoleCheckOptions) => boolean;
  checkMultipleAccess: (optionsList: RoleCheckOptions[]) => boolean[];

  // Convenience permission checkers
  canCreateProjects: boolean;
  canEditOwnProjects: boolean;
  canDeleteOwnProjects: boolean;
  canShareProjects: boolean;
  canViewAdminDashboard: boolean;
  canManageUsers: boolean;
  canViewSystemLogs: boolean;
  canModifySystemSettings: boolean;
  canViewAdvancedAnalytics: boolean;
  canAccessBetaFeatures: boolean;
  canExportProjects: boolean;
  canUseAIFeatures: boolean;

  // Grouped permission checkers
  hasProjectPermissions: boolean;
  hasAdminPermissions: boolean;
  hasAnyAdminPermission: boolean;

  // Advanced utilities
  getPermissionsList: () => (keyof UserPermissions)[];
  getMissingPermissions: (required: (keyof UserPermissions)[]) => (keyof UserPermissions)[];
  getPermissionLevel: () => 'none' | 'basic' | 'admin' | 'full';
}

/**
 * Hook principal para verifica√ß√£o de roles e permiss√µes
 */
export const useRole = (): UseRoleReturn => {
  const { 
    currentUser, 
    hasRole: authHasRole, 
    hasPermission: authHasPermission,
    isAdmin: authIsAdmin,
    isUser: authIsUser
  } = useAuth();

  // === BASIC GETTERS ===

  const currentUserRole = currentUser?.role || null;
  const isAuthenticated = !!currentUser;
  const isAdmin = authIsAdmin;
  const isUser = authIsUser;

  // === ROLE CHECKERS ===

  const hasRole = (role: UserRole): boolean => {
    return authHasRole(role);
  };

  const hasPermission = (permission: keyof UserPermissions): boolean => {
    return authHasPermission(permission);
  };

  const hasAnyRole = (roles: UserRole[]): boolean => {
    return roles.some(role => hasRole(role));
  };

  const hasAllPermissions = (permissions: (keyof UserPermissions)[]): boolean => {
    return permissions.every(permission => hasPermission(permission));
  };

  const hasAnyPermission = (permissions: (keyof UserPermissions)[]): boolean => {
    return permissions.some(permission => hasPermission(permission));
  };

  // === COMPLEX ACCESS CHECKS ===

  const checkAccess = ({
    requiredRole,
    requiredPermissions = [],
    allowedRoles = [],
    requireAll = true,
    logCheck = false
  }: RoleCheckOptions): boolean => {
    if (!currentUser) {
      if (logCheck) {
        logger.debug('Access check failed: user not authenticated');
      }
      return false;
    }

    // Verificar role
    let hasRequiredRole = true;
    if (requiredRole) {
      hasRequiredRole = hasRole(requiredRole);
    } else if (allowedRoles.length > 0) {
      hasRequiredRole = hasAnyRole(allowedRoles);
    }

    // Verificar permiss√µes
    let hasRequiredPermissions = true;
    if (requiredPermissions.length > 0) {
      if (requireAll) {
        hasRequiredPermissions = hasAllPermissions(requiredPermissions);
      } else {
        hasRequiredPermissions = hasAnyPermission(requiredPermissions);
      }
    }

    const accessGranted = hasRequiredRole && hasRequiredPermissions;

    if (logCheck) {
      logger.debug('Access check result', {
        userRole: currentUserRole,
        accessGranted,
        hasRequiredRole,
        hasRequiredPermissions,
        requiredRole,
        requiredPermissions,
        allowedRoles
      });
    }

    return accessGranted;
  };

  const checkMultipleAccess = (optionsList: RoleCheckOptions[]): boolean[] => {
    return optionsList.map(options => checkAccess(options));
  };

  // === CONVENIENCE PERMISSION CHECKERS ===

  const canCreateProjects = hasPermission('canCreateProjects');
  const canEditOwnProjects = hasPermission('canEditOwnProjects');
  const canDeleteOwnProjects = hasPermission('canDeleteOwnProjects');
  const canShareProjects = hasPermission('canShareProjects');
  const canViewAdminDashboard = hasPermission('canViewAdminDashboard');
  const canManageUsers = hasPermission('canManageUsers');
  const canViewSystemLogs = hasPermission('canViewSystemLogs');
  const canModifySystemSettings = hasPermission('canModifySystemSettings');
  const canViewAdvancedAnalytics = hasPermission('canViewAdvancedAnalytics');
  const canAccessBetaFeatures = hasPermission('canAccessBetaFeatures');
  const canExportProjects = hasPermission('canExportProjects');
  const canUseAIFeatures = hasPermission('canUseAIFeatures');

  // === GROUPED PERMISSION CHECKERS ===

  const projectPermissions: (keyof UserPermissions)[] = [
    'canCreateProjects',
    'canEditOwnProjects',
    'canDeleteOwnProjects',
    'canShareProjects'
  ];

  const adminPermissions: (keyof UserPermissions)[] = [
    'canViewAdminDashboard',
    'canManageUsers',
    'canViewSystemLogs',
    'canModifySystemSettings',
    'canViewAdvancedAnalytics'
  ];

  const hasProjectPermissions = hasAllPermissions(projectPermissions);
  const hasAdminPermissions = hasAllPermissions(adminPermissions);
  const hasAnyAdminPermission = hasAnyPermission(adminPermissions);

  // === ADVANCED UTILITIES ===

  const getPermissionsList = (): (keyof UserPermissions)[] => {
    if (!currentUser) return [];

    return Object.entries(currentUser.permissions)
      .filter(([_, hasPermission]) => hasPermission)
      .map(([permission]) => permission as keyof UserPermissions);
  };

  const getMissingPermissions = (
    required: (keyof UserPermissions)[]
  ): (keyof UserPermissions)[] => {
    return required.filter(permission => !hasPermission(permission));
  };

  const getPermissionLevel = (): 'none' | 'basic' | 'admin' | 'full' => {
    if (!currentUser) return 'none';

    if (hasAdminPermissions) return 'full';
    if (hasAnyAdminPermission) return 'admin';
    if (hasProjectPermissions) return 'basic';
    return 'none';
  };

  return {
    // Current user info
    currentUserRole,
    isAuthenticated,
    isAdmin,
    isUser,

    // Permission checkers
    hasRole,
    hasPermission,
    hasAnyRole,
    hasAllPermissions,
    hasAnyPermission,

    // Complex access checks
    checkAccess,
    checkMultipleAccess,

    // Convenience permission checkers
    canCreateProjects,
    canEditOwnProjects,
    canDeleteOwnProjects,
    canShareProjects,
    canViewAdminDashboard,
    canManageUsers,
    canViewSystemLogs,
    canModifySystemSettings,
    canViewAdvancedAnalytics,
    canAccessBetaFeatures,
    canExportProjects,
    canUseAIFeatures,

    // Grouped permission checkers
    hasProjectPermissions,
    hasAdminPermissions,
    hasAnyAdminPermission,

    // Advanced utilities
    getPermissionsList,
    getMissingPermissions,
    getPermissionLevel,
  };
};

// === SPECIALIZED HOOKS ===

/**
 * Hook espec√≠fico para funcionalidades administrativas
 */
export const useAdminRole = () => {
  const {
    isAdmin,
    canViewAdminDashboard,
    canManageUsers,
    canViewSystemLogs,
    canModifySystemSettings,
    canViewAdvancedAnalytics,
    hasAdminPermissions,
    hasAnyAdminPermission
  } = useRole();

  return {
    isAdmin,
    canViewAdminDashboard,
    canManageUsers,
    canViewSystemLogs,
    canModifySystemSettings,
    canViewAdvancedAnalytics,
    hasFullAdminAccess: hasAdminPermissions,
    hasPartialAdminAccess: hasAnyAdminPermission,
    hasNoAdminAccess: !hasAnyAdminPermission
  };
};

/**
 * Hook espec√≠fico para funcionalidades de projeto
 */
export const useProjectRole = () => {
  const {
    canCreateProjects,
    canEditOwnProjects,
    canDeleteOwnProjects,
    canShareProjects,
    canExportProjects,
    hasProjectPermissions
  } = useRole();

  return {
    canCreateProjects,
    canEditOwnProjects,
    canDeleteOwnProjects,
    canShareProjects,
    canExportProjects,
    hasFullProjectAccess: hasProjectPermissions,
    canManageOwnProjects: canEditOwnProjects && canDeleteOwnProjects,
    canWorkWithProjects: canCreateProjects || canEditOwnProjects
  };
};

/**
 * Hook para verificar se pode acessar uma funcionalidade beta
 */
export const useBetaFeatures = () => {
  const { canAccessBetaFeatures, isAdmin } = useRole();

  return {
    canAccessBetaFeatures: canAccessBetaFeatures || isAdmin,
    isBetaTester: canAccessBetaFeatures,
    isAdminWithBetaAccess: isAdmin
  };
};

export default useRole;

import React, { useState, useCallback, useEffect, useRef } from 'react';
import Navbar from '../components/Navbar';
