      return {
        sentiment: analysis.sentiment || 0,
        readabilityScore: analysis.readabilityScore || 50,
        engagementScore: analysis.engagementScore || 50,
        keywordDensity: analysis.keywordDensity || {},
        suggestions: analysis.suggestions || []
      };

    } catch (error) {
