  // **SUGEST√ïES CONTEXTUAIS**

  static async getContextualSuggestions(
    selection: TextSelection,
    fullText: string,
    context: any
  ): Promise<string[]> {
    try {
      const prompt = `
        Texto completo do roteiro: "${fullText}"

        Texto selecionado: "${selection.selectedText}"
        Posi√ß√£o: caracteres ${selection.startIndex} a ${selection.endIndex}

        Contexto:
        - Plataforma: ${context.platform}
        - P√∫blico: ${context.audience}
        - Tom: ${context.tone}

        Forne√ßa 5 sugest√µes espec√≠ficas para melhorar APENAS o texto selecionado,
        considerando o contexto do roteiro completo.

        Responda com um array JSON de strings.
      `;

      const response = await this.callGeminiAPI(prompt, context);
      const suggestionsText = response.candidates[0]?.content.parts[0]?.text;

      if (!suggestionsText) {
        return ['Sem sugest√µes dispon√≠veis'];
      }

      const suggestions = JSON.parse(suggestionsText.trim());
      return Array.isArray(suggestions) ? suggestions : ['Erro no formato das sugest√µes'];

