  name?: string;
  category: ErrorCategory;
  severity: ErrorSeverity;
  context: ErrorContext;
  fingerprint: string;
  count: number;
  firstOccurrence: string;
  lastOccurrence: string;
  resolved: boolean;
  tags: string[];
}

export interface ErrorStats {
  totalErrors: number;
  byCategory: Record<ErrorCategory, number>;
  bySeverity: Record<ErrorSeverity, number>;
  topErrors: Array<{ fingerprint: string; count: number; message: string }>;
  errorRate: number;
  lastUpdated: string;
}

// =============================================================================
// ERROR TRACKING SERVICE
// =============================================================================

class ErrorTrackingService {
  private errors: Map<string, TrackedError> = new Map();
  private sessionId: string;
  private maxErrors = 1000; // Maximum stored errors
  private reportingQueue: TrackedError[] = [];
  private isOnline = navigator.onLine;

  constructor() {
    this.sessionId = this.generateSessionId();
    this.setupEventListeners();
    this.setupPeriodicReporting();

    logger.info('Error tracking service initialized', {
      sessionId: this.sessionId,
      environment: config.environment,
    }, 'ERROR_TRACKING');
  }

  /**
   * Track a new error
   */
  trackError(
    error: Error | string,
    category: ErrorCategory = 'unknown',
    severity: ErrorSeverity = 'medium',
    context: ErrorContext = {}
  ): string {
    const errorObj = typeof error === 'string' 
      ? new Error(error) 
      : error;

    const enhancedContext = this.enhanceContext(context);
    const fingerprint = this.generateFingerprint(errorObj, category, enhancedContext);

    const existingError = this.errors.get(fingerprint);

    if (existingError) {
      // Update existing error
      existingError.count++;
      existingError.lastOccurrence = new Date().toISOString();
      existingError.context = { ...existingError.context, ...enhancedContext };
    } else {
      // Create new error entry
      const trackedError: TrackedError = {
        id: this.generateErrorId(),
        message: errorObj.message || 'Unknown error',
        stack: errorObj.stack,
        name: errorObj.name || 'Error',
        category,
        severity,
        context: enhancedContext,
        fingerprint,
        count: 1,
        firstOccurrence: new Date().toISOString(),
        lastOccurrence: new Date().toISOString(),
        resolved: false,
        tags: this.generateTags(errorObj, category, enhancedContext),
      };

      this.errors.set(fingerprint, trackedError);

      // Add to reporting queue
      this.queueForReporting(trackedError);
    }

    // Log the error
    this.logError(this.errors.get(fingerprint)!);

    // Cleanup if necessary
    this.cleanupOldErrors();

    return fingerprint;
  }

  /**
   * Track API errors
   */
  trackApiError(
    method: string,
    url: string,
    status: number,
    response?: any,
    context: ErrorContext = {}
  ): string {
    const errorMessage = `API Error: ${method} ${url} - Status ${status}`;
    const severity = this.getApiErrorSeverity(status);

    return this.trackError(
      new Error(errorMessage),
      'api',
      severity,
      {
        ...context,
        api: {
          method,
          url,
          status,
          response: typeof response === 'string' ? response : JSON.stringify(response),
        },
      }
    );
  }

  /**
   * Track network errors
   */
  trackNetworkError(
    error: Error,
    context: ErrorContext = {}
  ): string {
    return this.trackError(
      error,
      'network',
      'high',
      {
        ...context,
        network: {
          online: navigator.onLine,
          connectionType: this.getConnectionType(),
        },
      }
    );
  }

  /**
   * Track validation errors
   */
  trackValidationError(
    field: string,
    value: any,
    errors: string[],
    context: ErrorContext = {}
  ): string {
    const errorMessage = `Validation Error: ${field} - ${errors.join(', ')}`;

    return this.trackError(
      new Error(errorMessage),
      'validation',
      'low',
      {
        ...context,
        validation: {
          field,
          value: typeof value === 'string' ? value : JSON.stringify(value),
          errors,
        },
      }
    );
  }

  /**
   * Track performance issues
   */
  trackPerformanceIssue(
    metric: string,
    value: number,
    threshold: number,
    context: ErrorContext = {}
  ): string {
    const errorMessage = `Performance Issue: ${metric} (${value}) exceeded threshold (${threshold})`;

    return this.trackError(
      new Error(errorMessage),
      'performance',
      value > threshold * 2 ? 'high' : 'medium',
      {
        ...context,
        performance: {
          metric,
          value,
          threshold,
          ratio: value / threshold,
        },
      }
    );
  }

  /**
   * Mark an error as resolved
   */
  resolveError(fingerprint: string): boolean {
    const error = this.errors.get(fingerprint);
    if (error) {
      error.resolved = true;
      logger.info('Error marked as resolved', {
        fingerprint,
        message: error.message,
      }, 'ERROR_TRACKING');
      return true;
    }
    return false;
  }

  /**
   * Get error statistics
   */
  getErrorStats(): ErrorStats {
    const errors = Array.from(this.errors.values());
    const totalErrors = errors.reduce((sum, error) => sum + error.count, 0);

    const byCategory: Record<ErrorCategory, number> = {
      ui: 0, api: 0, network: 0, validation: 0, auth: 0,
      performance: 0, security: 0, integration: 0, unknown: 0,
    };

    const bySeverity: Record<ErrorSeverity, number> = {
      low: 0, medium: 0, high: 0, critical: 0,
    };

    errors.forEach(error => {
      byCategory[error.category] += error.count;
      bySeverity[error.severity] += error.count;
    });

    const topErrors = errors
      .sort((a, b) => b.count - a.count)
      .slice(0, 10)
      .map(error => ({
        fingerprint: error.fingerprint,
        count: error.count,
        message: error.message,
      }));

    return {
      totalErrors,
      byCategory,
      bySeverity,
      topErrors,
      errorRate: this.calculateErrorRate(),
      lastUpdated: new Date().toISOString(),
    };
  }

  /**
   * Get all tracked errors
   */
  getAllErrors(): TrackedError[] {
    return Array.from(this.errors.values())
      .sort((a, b) => new Date(b.lastOccurrence).getTime() - new Date(a.lastOccurrence).getTime());
  }

  /**
   * Get errors by category
   */
  getErrorsByCategory(category: ErrorCategory): TrackedError[] {
    return this.getAllErrors().filter(error => error.category === category);
  }

  /**
   * Clear all errors
   */
  clearErrors(): void {
    this.errors.clear();
    this.reportingQueue = [];
    logger.info('All errors cleared', {}, 'ERROR_TRACKING');
  }

  /**
   * Export errors for analysis
   */
  exportErrors(format: 'json' | 'csv' = 'json'): string {
    const errors = this.getAllErrors();

    if (format === 'csv') {
      const headers = [
        'ID', 'Message', 'Category', 'Severity', 'Count',
        'First Occurrence', 'Last Occurrence', 'Resolved'
      ];

      const rows = errors.map(error => [
        error.id,
        error.message.replace(/"/g, '""'),
        error.category,
        error.severity,
        error.count,
        error.firstOccurrence,
        error.lastOccurrence,
        error.resolved
      ]);

      return [headers, ...rows].map(row => row.join(',')).join('\n');
    }

    return JSON.stringify(errors, null, 2);
  }

  // =============================================================================
  // PRIVATE METHODS
  // =============================================================================

  private generateSessionId(): string {
    return `sess_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  private generateErrorId(): string {
    return `err_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  private generateFingerprint(error: Error, category: ErrorCategory, context: ErrorContext): string {
    const key = `${error.name}:${error.message}:${category}:${context.component || ''}:${context.route || ''}`;
    return btoa(key).replace(/[^a-zA-Z0-9]/g, '').substr(0, 16);
  }

  private enhanceContext(context: ErrorContext): ErrorContext {
    return {
      ...context,
      userId: context.userId || this.getUserId(),
      sessionId: this.sessionId,
      userAgent: navigator.userAgent,
      route: context.route || window.location.pathname,
      timestamp: new Date().toISOString(),
      environment: config.environment,
      version: config.version,
      online: navigator.onLine,
      connectionType: this.getConnectionType(),
    };
  }

  private generateTags(error: Error, category: ErrorCategory, context: ErrorContext): string[] {
    const tags = [category];

    if (error.name !== 'Error') {
      tags.push(error.name.toLowerCase());
    }

    if (context.component) {
      tags.push(`component:${context.component}`);
    }

    if (context.route) {
      tags.push(`route:${context.route.replace('/', '')}`);
    }

    if (!navigator.onLine) {
      tags.push('offline');
    }

    return tags;
  }

  private logError(error: TrackedError): void {
    const logLevel = this.getLogLevel(error.severity);

    logger[logLevel](`${error.category.toUpperCase()} Error: ${error.message}`, {
      errorId: error.id,
      fingerprint: error.fingerprint,
      category: error.category,
      severity: error.severity,
      count: error.count,
      context: error.context,
      stack: error.stack,
    }, 'ERROR_TRACKING');
  }

  private getLogLevel(severity: ErrorSeverity): 'warn' | 'error' {
    return severity === 'low' ? 'warn' : 'error';
  }

  private getApiErrorSeverity(status: number): ErrorSeverity {
    if (status >= 500) return 'high';
    if (status >= 400) return 'medium';
    return 'low';
  }

  private getUserId(): string {
    try {
      return localStorage.getItem('userId') || 'anonymous';
    } catch {
      return 'unknown';
    }
  }

  private getConnectionType(): string {
    const connection = (navigator as any).connection;
    return connection ? connection.effectiveType || 'unknown' : 'unknown';
  }

  private calculateErrorRate(): number {
    // Simple error rate calculation (errors per minute)
    const now = new Date();
    const oneMinuteAgo = new Date(now.getTime() - 60000);

    const recentErrors = Array.from(this.errors.values())
      .filter(error => new Date(error.lastOccurrence) > oneMinuteAgo)
      .reduce((sum, error) => sum + error.count, 0);

    return recentErrors;
  }

  private cleanupOldErrors(): void {
    if (this.errors.size <= this.maxErrors) return;

    const errors = Array.from(this.errors.entries())
      .sort((a, b) => new Date(a[1].lastOccurrence).getTime() - new Date(b[1].lastOccurrence).getTime());

    const toRemove = errors.slice(0, errors.length - this.maxErrors);
    toRemove.forEach(([fingerprint]) => this.errors.delete(fingerprint));

    logger.info('Old errors cleaned up', {
      removed: toRemove.length,
      remaining: this.errors.size,
    }, 'ERROR_TRACKING');
  }

  private queueForReporting(error: TrackedError): void {
    if (isProduction()) {
      this.reportingQueue.push(error);
    }
  }

  private setupEventListeners(): void {
    // Online/offline detection
    window.addEventListener('online', () => {
      this.isOnline = true;
      logger.info('Connection restored', {}, 'ERROR_TRACKING');
    });

    window.addEventListener('offline', () => {
      this.isOnline = false;
      logger.warn('Connection lost', {}, 'ERROR_TRACKING');
    });

    // Global error handler
    window.addEventListener('error', (event) => {
      this.trackError(
        new Error(event.message),
        'ui',
        'medium',
        {
          component: 'global',
          filename: event.filename,
          line: event.lineno,
          column: event.colno,
        }
      );
    });

    // Unhandled promise rejections
    window.addEventListener('unhandledrejection', (event) => {
      this.trackError(
        new Error(`Unhandled Promise Rejection: ${event.reason}`),
        'unknown',
        'high',
        {
          component: 'promise',
          reason: event.reason,
        }
      );
    });
  }

  private setupPeriodicReporting(): void {
    if (!isProduction()) return;

    setInterval(() => {
      if (this.reportingQueue.length > 0 && this.isOnline) {
        this.sendErrorsToExternalService();
      }
    }, 30000); // Report every 30 seconds
  }

  private async sendErrorsToExternalService(): Promise<void> {
    const errorsToSend = [...this.reportingQueue];
    this.reportingQueue = [];

    try {
      // Here you would integrate with external services like Sentry, LogRocket, etc.
      logger.info('Sending errors to external service', {
        count: errorsToSend.length,
        service: 'external_error_tracking',
      }, 'ERROR_TRACKING');

      // Simulated external service call
      // await fetch('/api/errors', {
      //   method: 'POST',
      //   headers: { 'Content-Type': 'application/json' },
      //   body: JSON.stringify(errorsToSend),
      // });

    } catch (error) {
      logger.error('Failed to send errors to external service', {
        error: error instanceof Error ? error.message : 'Unknown',
        queuedBack: errorsToSend.length,
      }, 'ERROR_TRACKING');

      // Re-queue errors for next attempt
      this.reportingQueue.unshift(...errorsToSend);
    }
  }
}

// =============================================================================
// SINGLETON INSTANCE & EXPORTS
// =============================================================================

export const errorTrackingService = new ErrorTrackingService();

// Helper functions for common error tracking patterns
export const trackError = (error: Error | string, category?: ErrorCategory, severity?: ErrorSeverity, context?: ErrorContext) =>
  errorTrackingService.trackError(error, category, severity, context);

export const trackApiError = (method: string, url: string, status: number, response?: any, context?: ErrorContext) =>
  errorTrackingService.trackApiError(method, url, status, response, context);

export const trackValidationError = (field: string, value: any, errors: string[], context?: ErrorContext) =>
  errorTrackingService.trackValidationError(field, value, errors, context);

export const trackNetworkError = (error: Error, context?: ErrorContext) =>
  errorTrackingService.trackNetworkError(error, context);

export const trackPerformanceIssue = (metric: string, value: number, threshold: number, context?: ErrorContext) =>
  errorTrackingService.trackPerformanceIssue(metric, value, threshold, context);

export default errorTrackingService;

import { collection, doc, getDocs, setDoc, deleteDoc, query, where, orderBy } from 'firebase/firestore';
import { db } from '../firebaseConfig';
import type { FilterOptions } from '../types/enhanced';
import { createLogger } from '../utils/logger';

const logger = createLogger('FilterPersistenceService');

// Interface para filtros salvos
export interface SavedFilter {
  id: string;
  name: string;
  description?: string;
  filters: FilterOptions;
  userId: string;
  isDefault?: boolean;
  usageCount: number;
  createdAt: Date;
  updatedAt: Date;
}

// Chaves para localStorage
const STORAGE_KEYS = {
  CURRENT_FILTERS: 'dashboard_current_filters',
  FILTER_HISTORY: 'dashboard_filter_history',
  PREFERENCES: 'dashboard_filter_preferences'
};

export class FilterPersistenceService {
  /**
   * Salva os filtros atuais no localStorage automaticamente
   */
  static saveCurrentFilters(filters: FilterOptions): void {
    try {
      const filtersToSave = {
        ...filters,
        timestamp: new Date().toISOString()
      };

      localStorage.setItem(STORAGE_KEYS.CURRENT_FILTERS, JSON.stringify(filtersToSave));

      // Adicionar ao hist√≥rico
      this.addToFilterHistory(filters);

      logger.debug('Filtros salvos automaticamente', { filters });
    } catch (error) {
      logger.error('Erro ao salvar filtros atuais', error);
    }
  }

  /**
   * Carrega os filtros salvos do localStorage
   */
  static loadCurrentFilters(): FilterOptions | null {
    try {
      const saved = localStorage.getItem(STORAGE_KEYS.CURRENT_FILTERS);
      if (!saved) return null;

      const parsed = JSON.parse(saved);

      // Verificar se n√£o est√° muito antigo (mais de 7 dias)
      const timestamp = new Date(parsed.timestamp);
      const sevenDaysAgo = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000);

      if (timestamp < sevenDaysAgo) {
        localStorage.removeItem(STORAGE_KEYS.CURRENT_FILTERS);
        return null;
      }

      // Remover campos de controle antes de retornar
      delete parsed.timestamp;

      return parsed as FilterOptions;
    } catch (error) {
      logger.error('Erro ao carregar filtros salvos', error);
      return null;
    }
  }

  /**
   * Adiciona filtros ao hist√≥rico de uso
   */
  private static addToFilterHistory(filters: FilterOptions): void {
    try {
      const historyRaw = localStorage.getItem(STORAGE_KEYS.FILTER_HISTORY);
      let history: Array<{ filters: FilterOptions; timestamp: string; hash: string }> = 
        historyRaw ? JSON.parse(historyRaw) : [];

      // Criar hash simples dos filtros para evitar duplicatas
      const hash = this.createFilterHash(filters);

      // Verificar se j√° existe no hist√≥rico recente
      const existingIndex = history.findIndex(item => item.hash === hash);

      if (existingIndex >= 0) {
        // Atualizar timestamp do existente
        history[existingIndex].timestamp = new Date().toISOString();
      } else {
        // Adicionar novo item
        history.unshift({
          filters,
          timestamp: new Date().toISOString(),
          hash
        });
      }

      // Manter apenas os √∫ltimos 20 filtros
      history = history.slice(0, 20);

      localStorage.setItem(STORAGE_KEYS.FILTER_HISTORY, JSON.stringify(history));
    } catch (error) {
      logger.error('Erro ao adicionar ao hist√≥rico de filtros', error);
    }
  }

  /**
   * Obt√©m o hist√≥rico de filtros usados
   */
  static getFilterHistory(): Array<{ filters: FilterOptions; timestamp: string; description: string }> {
    try {
      const historyRaw = localStorage.getItem(STORAGE_KEYS.FILTER_HISTORY);
      if (!historyRaw) return [];

      const history = JSON.parse(historyRaw);

      return history.map((item: any) => ({
        filters: item.filters,
        timestamp: item.timestamp,
        description: this.generateFilterDescription(item.filters)
      }));
    } catch (error) {
      logger.error('Erro ao obter hist√≥rico de filtros', error);
      return [];
    }
  }

  /**
   * Salva um preset de filtro nomeado no Firebase
   */
  static async saveFilterPreset(
    userId: string,
    name: string,
    filters: FilterOptions,
    description?: string
  ): Promise<string> {
    try {
      const presetId = `preset_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

      const preset: SavedFilter = {
        id: presetId,
        name: name.trim(),
        description: description?.trim(),
        filters,
        userId,
        isDefault: false,
        usageCount: 0,
        createdAt: new Date(),
        updatedAt: new Date()
      };

      await setDoc(doc(db, 'filter_presets', presetId), preset);

      logger.info('Preset de filtro salvo', { presetId, name });
      return presetId;
    } catch (error) {
      logger.error('Erro ao salvar preset de filtro', error);
      throw new Error('Falha ao salvar preset de filtro');
    }
  }

  /**
   * Carrega os presets de filtro do usu√°rio
   */
  static async loadFilterPresets(userId: string): Promise<SavedFilter[]> {
    try {
      const q = query(
        collection(db, 'filter_presets'),
        where('userId', '==', userId),
        orderBy('updatedAt', 'desc')
      );

      const snapshot = await getDocs(q);

      return snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
        createdAt: doc.data().createdAt?.toDate() || new Date(),
        updatedAt: doc.data().updatedAt?.toDate() || new Date()
      })) as SavedFilter[];
    } catch (error) {
      logger.error('Erro ao carregar presets de filtro', error);
      return [];
    }
  }

  /**
   * Atualiza um preset existente
   */
  static async updateFilterPreset(
    presetId: string,
    updates: Partial<Pick<SavedFilter, 'name' | 'description' | 'filters'>>
  ): Promise<void> {
    try {
      const updateData = {
        ...updates,
        updatedAt: new Date()
      };

      await setDoc(doc(db, 'filter_presets', presetId), updateData, { merge: true });

      logger.info('Preset de filtro atualizado', { presetId });
    } catch (error) {
      logger.error('Erro ao atualizar preset de filtro', error);
      throw new Error('Falha ao atualizar preset de filtro');
    }
  }

  /**
   * Remove um preset de filtro
   */
  static async deleteFilterPreset(presetId: string): Promise<void> {
    try {
      await deleteDoc(doc(db, 'filter_presets', presetId));

      logger.info('Preset de filtro removido', { presetId });
    } catch (error) {
      logger.error('Erro ao remover preset de filtro', error);
      throw new Error('Falha ao remover preset de filtro');
    }
  }

  /**
   * Incrementa o contador de uso de um preset
   */
  static async incrementPresetUsage(presetId: string): Promise<void> {
    try {
      // Para incremento, usamos uma transa√ß√£o simples
      const presetRef = doc(db, 'filter_presets', presetId);

      // Como n√£o temos transa√ß√µes, vamos fazer um update simples
      await setDoc(presetRef, {
        usageCount: (await this.getPresetUsageCount(presetId)) + 1,
        updatedAt: new Date()
      }, { merge: true });

      logger.debug('Uso do preset incrementado', { presetId });
    } catch (error) {
      logger.error('Erro ao incrementar uso do preset', error);
    }
  }

  /**
   * Obt√©m o contador de uso atual de um preset
   */
  private static async getPresetUsageCount(presetId: string): Promise<number> {
    try {
      const q = query(collection(db, 'filter_presets'), where('id', '==', presetId));
      const snapshot = await getDocs(q);

      if (!snapshot.empty) {
        return snapshot.docs[0].data().usageCount || 0;
      }

      return 0;
    } catch (error) {
      return 0;
    }
  }

  /**
   * Cria presets padr√£o para novos usu√°rios
   */
  static async createDefaultPresets(userId: string): Promise<void> {
    try {
      const defaultPresets: Array<Pick<SavedFilter, 'name' | 'description' | 'filters'>> = [
        {
          name: 'Conte√∫do Recente',
          description: 'Projetos criados nos √∫ltimos 7 dias',
          filters: {
            dateRange: {
              start: new Date(Date.now() - 7 * 24 * 60 * 60 * 1000),
              end: new Date()
            },
            sortBy: 'date',
            sortOrder: 'desc'
          }
        },
        {
          name: 'Favoritos',
          description: 'Apenas projetos marcados como favoritos',
          filters: {
            isFavorite: true,
            sortBy: 'date',
            sortOrder: 'desc'
          }
        },
        {
          name: 'Rascunhos Pendentes',
          description: 'Projetos ainda n√£o finalizados',
          filters: {
            status: ['draft'],
            sortBy: 'date',
            sortOrder: 'desc'
          }
        },
        {
          name: 'Conte√∫do Instagram',
          description: 'Todos os projetos para Instagram',
          filters: {
            platforms: ['instagram'],
            sortBy: 'date',
            sortOrder: 'desc'
          }
        }
      ];

      for (const preset of defaultPresets) {
        await this.saveFilterPreset(
          userId,
          preset.name,
          preset.filters,
          preset.description
        );
      }

      logger.info('Presets padr√£o criados', { userId, count: defaultPresets.length });
    } catch (error) {
      logger.error('Erro ao criar presets padr√£o', error);
    }
  }

  /**
   * Salva prefer√™ncias do usu√°rio para filtros
   */
  static saveFilterPreferences(preferences: {
    autoSave: boolean;
    defaultSort: { sortBy: string; sortOrder: 'asc' | 'desc' };
    rememberExpanded: boolean;
    showFilterHistory: boolean;
  }): void {
    try {
      localStorage.setItem(STORAGE_KEYS.PREFERENCES, JSON.stringify(preferences));
      logger.debug('Prefer√™ncias de filtro salvas', preferences);
    } catch (error) {
      logger.error('Erro ao salvar prefer√™ncias de filtro', error);
    }
  }

  /**
   * Carrega prefer√™ncias do usu√°rio para filtros
   */
  static loadFilterPreferences(): {
    autoSave: boolean;
    defaultSort: { sortBy: string; sortOrder: 'asc' | 'desc' };
    rememberExpanded: boolean;
    showFilterHistory: boolean;
  } {
    try {
      const saved = localStorage.getItem(STORAGE_KEYS.PREFERENCES);
      if (saved) {
        return JSON.parse(saved);
      }
    } catch (error) {
      logger.error('Erro ao carregar prefer√™ncias de filtro', error);
    }

    // Retornar padr√µes
    return {
      autoSave: true,
      defaultSort: { sortBy: 'date', sortOrder: 'desc' },
      rememberExpanded: false,
      showFilterHistory: true
    };
  }

  /**
   * Limpa todos os dados de filtros salvos
   */
  static clearAllSavedFilters(): void {
    try {
      localStorage.removeItem(STORAGE_KEYS.CURRENT_FILTERS);
      localStorage.removeItem(STORAGE_KEYS.FILTER_HISTORY);

      logger.info('Todos os filtros salvos foram limpos');
    } catch (error) {
      logger.error('Erro ao limpar filtros salvos', error);
    }
  }

  /**
   * Gera hash simples para filtros (para compara√ß√£o)
   */
  private static createFilterHash(filters: FilterOptions): string {
    const key = JSON.stringify({
      platforms: filters.platforms?.sort(),
      formats: filters.formats?.sort(),
      status: filters.status?.sort(),
      tags: filters.tags?.sort(),
      folderId: filters.folderId,
      isFavorite: filters.isFavorite,
      dateRange: filters.dateRange ? {
        start: filters.dateRange.start.toISOString().split('T')[0],
        end: filters.dateRange.end.toISOString().split('T')[0]
      } : undefined
    });

    // Hash simples
    let hash = 0;
    for (let i = 0; i < key.length; i++) {
      const char = key.charCodeAt(i);
      hash = ((hash << 5) - hash) + char;
      hash = hash & hash; // Convert to 32-bit integer
    }

    return Math.abs(hash).toString(36);
  }

  /**
   * Gera descri√ß√£o leg√≠vel dos filtros
   */
  private static generateFilterDescription(filters: FilterOptions): string {
    const parts: string[] = [];

    if (filters.platforms?.length) {
      parts.push(`${filters.platforms.length} plataforma${filters.platforms.length > 1 ? 's' : ''}`);
    }

    if (filters.status?.length) {
      parts.push(`Status: ${filters.status.join(', ')}`);
    }

    if (filters.tags?.length) {
      parts.push(`${filters.tags.length} tag${filters.tags.length > 1 ? 's' : ''}`);
    }

    if (filters.isFavorite) {
      parts.push('Favoritos');
    }

    if (filters.dateRange) {
      parts.push('Com per√≠odo definido');
    }

    return parts.length > 0 ? parts.join(' ‚Ä¢ ') : 'Filtros b√°sicos';
  }
}

import { GoogleGenerativeAI, GenerativeModel } from "@google/generative-ai";
import type { FormData } from '../types';
import { analyticsService } from './analyticsService';

export class GeminiService {
  private genAI: GoogleGenerativeAI | null = null;
  private model: GenerativeModel | null = null;

  constructor() {
    console.log('üîß GeminiService: Inicializando...');
    this.initializeModel();
  }

  private getApiKey(): string | null {
    // Prioridade: localStorage -> environment variable
    const localStorageKey = localStorage.getItem('GEMINI_API_KEY');
    if (localStorageKey && localStorageKey.trim()) {
      return localStorageKey.trim();
    }

    const envKey = import.meta.env.VITE_GOOGLE_GEMINI_API_KEY;
    if (envKey && envKey.trim()) {
      return envKey.trim();
    }

    return null;
  }

  private initializeModel() {
    try {
      const apiKey = this.getApiKey();

      if (!apiKey) {
        console.warn('‚ö†Ô∏è API key do Gemini n√£o configurada. Configure atrav√©s do localStorage ou vari√°vel de ambiente VITE_GOOGLE_GEMINI_API_KEY');
        this.genAI = null;
        this.model = null;
        return;
      }

      this.genAI = new GoogleGenerativeAI(apiKey);
      this.model = this.genAI.getGenerativeModel({ model: "gemini-1.5-flash-latest" });
      console.log('‚úÖ Gemini AI inicializado com sucesso');
    } catch (error) {
      console.error('‚ùå Erro ao inicializar Gemini:', error);
      analyticsService.trackError('Gemini Initialization Failed', { 
        error: error instanceof Error ? error.message : 'Unknown error' 
      });
      this.genAI = null;
      this.model = null;
    }
  }

  isConfigured(): boolean {
    return this.model !== null;
  }

  async generateScript(params: {
    subject: string;
    platform: string;
    duration: string;
    tone: string;
    audience: string;
    objective?: string;
  }): Promise<string> {
    const startTime = Date.now();

    try {
