  /**
   * Gerar link de compartilhamento
   */
  static async generateShareLink(
    projectId: string,
    expirationHours: number = 24
  ): Promise<string> {
    try {
      const shareLink = `${window.location.origin}/shared/${projectId}/${this.generateShareToken()}`;
