import { 
  doc, 
  setDoc, 
  getDoc, 
  collection, 
  query, 
  where, 
  getDocs, 
  orderBy, 
  limit,
  updateDoc,
  deleteDoc,
  Timestamp 
} from 'firebase/firestore';
import { db } from '../firebaseConfig';
import type { 
  ScriptVersion,
  VersionChange,
  ComparisonData,
  DiffResult,
  AIImprovement
} from '../types';

export class VersioningService {
  private static readonly MAX_VERSIONS_PER_PROJECT = 50;
  private static readonly AUTO_SAVE_INTERVAL = 30000; // 30 segundos

  // **GEST√ÉO DE VERS√ïES**

  static async createVersion(
    projectId: string,
    userId: string,
    content: string,
    comment = '',
    isAutoSave = false,
    appliedSuggestions: string[] = []
  ): Promise<ScriptVersion> {
    try {
      // Obter n√∫mero da pr√≥xima vers√£o
      const versionNumber = await this.getNextVersionNumber(projectId);

      // Calcular metadata do conte√∫do
      const metadata = this.calculateContentMetadata(content);

      // Detectar mudan√ßas em rela√ß√£o √† vers√£o anterior
      const changes = await this.detectChanges(projectId, content);

      const version: ScriptVersion = {
        id: `version_${projectId}_${versionNumber}_${Date.now()}`,
        projectId,
        userId,
        versionNumber,
        content,
        changes,
        metadata,
        aiSuggestions: appliedSuggestions,
        comment,
        isAutoSave,
        timestamp: Timestamp.now(),
        stats: {
          improvementsApplied: appliedSuggestions.length,
          aiSuggestionsUsed: appliedSuggestions.length,
          manualEdits: changes.filter(c => c.type !== 'ai_suggestion').length
        }
      };

      // Salvar vers√£o
      await setDoc(doc(db, 'script_versions', version.id), version);

      // Limpar vers√µes antigas se necess√°rio
      await this.cleanupOldVersions(projectId);

      // Atualizar projeto com a vers√£o atual
      await this.updateProjectCurrentVersion(projectId, version.id);

      return version;

    } catch (error) {
      console.error('Erro ao criar vers√£o:', error);
      throw new Error(`Falha ao criar vers√£o: ${error instanceof Error ? error.message : 'Erro desconhecido'}`);
    }
  }

  static async getProjectVersions(
    projectId: string, 
    limit = 20
  ): Promise<ScriptVersion[]> {
    try {
      const versionsQuery = query(
        collection(db, 'script_versions'),
        where('projectId', '==', projectId),
        orderBy('versionNumber', 'desc'),
        limit(limit)
      );

      const versionsSnapshot = await getDocs(versionsQuery);
      return versionsSnapshot.docs.map(doc => doc.data() as ScriptVersion);

    } catch (error) {
      console.error('Erro ao obter vers√µes:', error);
      return [];
    }
  }

  static async getVersion(versionId: string): Promise<ScriptVersion | null> {
    try {
      const versionDoc = await getDoc(doc(db, 'script_versions', versionId));
      return versionDoc.exists() ? versionDoc.data() as ScriptVersion : null;
    } catch (error) {
      console.error('Erro ao obter vers√£o:', error);
      return null;
    }
  }

  static async getCurrentVersion(projectId: string): Promise<ScriptVersion | null> {
    try {
      const versionsQuery = query(
        collection(db, 'script_versions'),
        where('projectId', '==', projectId),
        orderBy('versionNumber', 'desc'),
        limit(1)
      );

      const snapshot = await getDocs(versionsQuery);
      if (snapshot.empty) return null;

      return snapshot.docs[0].data() as ScriptVersion;
    } catch (error) {
      console.error('Erro ao obter vers√£o atual:', error);
      return null;
    }
  }

  static async restoreVersion(versionId: string, userId: string): Promise<ScriptVersion> {
    try {
      const version = await this.getVersion(versionId);
      if (!version) {
        throw new Error('Vers√£o n√£o encontrada');
      }

      // Criar nova vers√£o baseada na vers√£o restaurada
      const restoredVersion = await this.createVersion(
        version.projectId,
        userId,
        version.content,
        `Restaurado da vers√£o ${version.versionNumber}`,
        false
      );

      return restoredVersion;
    } catch (error) {
      console.error('Erro ao restaurar vers√£o:', error);
      throw error;
    }
  }

  // **COMPARA√á√ÉO DE VERS√ïES**

  static async compareVersions(
    version1Id: string,
    version2Id: string
  ): Promise<ComparisonData> {
    try {
      const [version1, version2] = await Promise.all([
        this.getVersion(version1Id),
        this.getVersion(version2Id)
      ]);

      if (!version1 || !version2) {
        throw new Error('Uma ou ambas as vers√µes n√£o foram encontradas');
      }

      if (version1.projectId !== version2.projectId) {
        throw new Error('As vers√µes pertencem a projetos diferentes');
      }

      // Calcular diferen√ßas
      const diff = this.calculateDiff(version1.content, version2.content);

      // Calcular m√©tricas
      const metrics = this.calculateComparisonMetrics(diff, version1, version2);

      const comparison: ComparisonData = {
        id: `comparison_${version1Id}_${version2Id}_${Date.now()}`,
        projectId: version1.projectId,
        version1,
        version2,
        diff,
        metrics,
        timestamp: Timestamp.now()
      };

      // Salvar compara√ß√£o para hist√≥rico
      await setDoc(doc(db, 'version_comparisons', comparison.id), comparison);

      return comparison;

    } catch (error) {
      console.error('Erro ao comparar vers√µes:', error);
      throw error;
    }
  }

  static async getVersionComparisons(projectId: string): Promise<ComparisonData[]> {
    try {
      const comparisonsQuery = query(
        collection(db, 'version_comparisons'),
        where('projectId', '==', projectId),
        orderBy('timestamp', 'desc'),
        limit(10)
      );

      const snapshot = await getDocs(comparisonsQuery);
      return snapshot.docs.map(doc => doc.data() as ComparisonData);
    } catch (error) {
      console.error('Erro ao obter compara√ß√µes:', error);
      return [];
    }
  }

  // **DIFF E AN√ÅLISE**

  private static calculateDiff(text1: string, text2: string): DiffResult[] {
    // Implementa√ß√£o simplificada de diff - em produ√ß√£o usaria biblioteca como diff-match-patch
    const lines1 = text1.split('\n');
    const lines2 = text2.split('\n');
    const diff: DiffResult[] = [];

    let i = 0;
    let j = 0;
    let position = 0;

    while (i < lines1.length || j < lines2.length) {
      const line1 = lines1[i];
      const line2 = lines2[j];

      if (i >= lines1.length) {
        // Linha adicionada
        diff.push({
          type: 'added',
          content: line2,
          startIndex: position,
          endIndex: position + line2.length
        });
        position += line2.length + 1;
        j++;
      } else if (j >= lines2.length) {
        // Linha removida
        diff.push({
          type: 'removed',
          content: line1,
          startIndex: position,
          endIndex: position + line1.length
        });
        i++;
      } else if (line1 === line2) {
        // Linha inalterada
        diff.push({
          type: 'unchanged',
          content: line1,
          startIndex: position,
          endIndex: position + line1.length
        });
        position += line1.length + 1;
        i++;
        j++;
      } else {
        // Linha modificada
        diff.push({
          type: 'modified',
          content: line2,
          startIndex: position,
          endIndex: position + line2.length
        });
        position += line2.length + 1;
        i++;
        j++;
      }
    }

    return diff;
  }

  private static calculateComparisonMetrics(
    diff: DiffResult[],
    version1: ScriptVersion,
    version2: ScriptVersion
  ): ComparisonData['metrics'] {
    const totalChanges = diff.filter(d => d.type !== 'unchanged').length;
    const addedWords = diff
      .filter(d => d.type === 'added')
      .reduce((sum, d) => sum + d.content.split(/\s+/).length, 0);
    const removedWords = diff
      .filter(d => d.type === 'removed')
      .reduce((sum, d) => sum + d.content.split(/\s+/).length, 0);
    const modifiedWords = diff
      .filter(d => d.type === 'modified')
      .reduce((sum, d) => sum + d.content.split(/\s+/).length, 0);

    // Calcular score de melhoria baseado nas m√©tricas das vers√µes
    const improvementScore = this.calculateImprovementScore(version1, version2);

    return {
      totalChanges,
      addedWords,
      removedWords,
      modifiedWords,
      improvementScore
    };
  }

  private static calculateImprovementScore(
    version1: ScriptVersion,
    version2: ScriptVersion
  ): number {
    // Score baseado em mudan√ßas nos metadados
    let score = 50; // Base neutra

    // Melhorias na contagem de palavras (mais conciso = melhor para alguns casos)
    const wordDiff = version2.metadata.wordCount - version1.metadata.wordCount;
    if (Math.abs(wordDiff) < version1.metadata.wordCount * 0.1) {
      score += 10; // Mudan√ßa apropriada
    }

    // Melhorias no sentimento
    if (version2.metadata.sentiment > version1.metadata.sentiment) {
      score += 15;
    }

    // Uso de sugest√µes de IA
    if (version2.aiSuggestions.length > 0) {
      score += 20;
    }

    return Math.min(Math.max(score, 0), 100);
  }

  // **METADATA E AN√ÅLISE**

  private static calculateContentMetadata(content: string): ScriptVersion['metadata'] {
    const words = content.split(/\s+/).filter(Boolean);
    const characters = content.length;
    const readingTime = Math.ceil(words.length / 150); // 150 palavras por minuto

    // An√°lise b√°sica de sentimento (seria melhorada com IA)
    const sentiment = this.basicSentimentAnalysis(content);

    // Densidade de palavras-chave
    const keywordDensity = this.calculateKeywordDensity(content);

    return {
      wordCount: words.length,
      characterCount: characters,
      readingTime,
      sentiment,
      keywordDensity
    };
  }

  private static basicSentimentAnalysis(text: string): number {
    // Implementa√ß√£o b√°sica - em produ√ß√£o usaria servi√ßo de IA
    const positiveWords = ['bom', '√≥timo', 'excelente', 'incr√≠vel', 'fant√°stico', 'amor', 'feliz'];
    const negativeWords = ['ruim', 'terr√≠vel', 'p√©ssimo', '√≥dio', 'triste', 'problema'];

    const words = text.toLowerCase().split(/\s+/);
    let positiveCount = 0;
    let negativeCount = 0;

    words.forEach(word => {
      if (positiveWords.some(pw => word.includes(pw))) positiveCount++;
      if (negativeWords.some(nw => word.includes(nw))) negativeCount++;
    });

    if (positiveCount + negativeCount === 0) return 0;
    return (positiveCount - negativeCount) / (positiveCount + negativeCount);
  }

  private static calculateKeywordDensity(text: string): Record<string, number> {
    const words = text.toLowerCase()
      .replace(/[^\w\s]/g, '')
      .split(/\s+/)
      .filter(word => word.length > 3);

    const frequency: Record<string, number> = {};
    const totalWords = words.length;

    words.forEach(word => {
      frequency[word] = (frequency[word] || 0) + 1;
    });

    // Converter para densidade percentual e manter apenas as mais relevantes
    const density: Record<string, number> = {};
    Object.entries(frequency)
      .sort(([,a], [,b]) => b - a)
      .slice(0, 10)
      .forEach(([word, count]) => {
        density[word] = Math.round((count / totalWords) * 100);
      });

    return density;
  }

  // **DETEC√á√ÉO DE MUDAN√áAS**

  private static async detectChanges(
    projectId: string,
    newContent: string
  ): Promise<VersionChange[]> {
    try {
      const currentVersion = await this.getCurrentVersion(projectId);
      if (!currentVersion) {
        return []; // Primeira vers√£o, sem mudan√ßas
      }

      const changes: VersionChange[] = [];
      const oldContent = currentVersion.content;

      // Detectar mudan√ßas usando diff simples
      const diff = this.calculateDiff(oldContent, newContent);

      diff.forEach((diffItem, index) => {
        if (diffItem.type !== 'unchanged') {
          changes.push({
            id: `change_${Date.now()}_${index}`,
            type: diffItem.type === 'added' ? 'addition' : 
                  diffItem.type === 'removed' ? 'deletion' : 'modification',
            startIndex: diffItem.startIndex,
            endIndex: diffItem.endIndex,
            oldText: diffItem.type === 'added' ? '' : diffItem.content,
            newText: diffItem.type === 'removed' ? '' : diffItem.content,
            timestamp: Timestamp.now()
          });
        }
      });

      return changes;
    } catch (error) {
      console.error('Erro ao detectar mudan√ßas:', error);
      return [];
    }
  }

  // **UTILIT√ÅRIOS**

  private static async getNextVersionNumber(projectId: string): Promise<number> {
    try {
      const versions = await this.getProjectVersions(projectId, 1);
      return versions.length > 0 ? versions[0].versionNumber + 1 : 1;
    } catch (error) {
      console.error('Erro ao obter pr√≥ximo n√∫mero de vers√£o:', error);
      return 1;
    }
  }

  private static async cleanupOldVersions(projectId: string): Promise<void> {
    try {
      const versions = await this.getProjectVersions(projectId, 100);

      if (versions.length <= this.MAX_VERSIONS_PER_PROJECT) {
        return;
      }

      // Manter as vers√µes mais recentes e algumas importantes
      const versionsToKeep = versions
        .slice(0, this.MAX_VERSIONS_PER_PROJECT - 10) // Manter as 40 mais recentes
        .concat(
          versions.filter(v => !v.isAutoSave) // Manter todas as vers√µes manuais
        );

      const versionsToDelete = versions.filter(v => 
        !versionsToKeep.some(keep => keep.id === v.id)
      );

      // Deletar vers√µes antigas
      await Promise.all(
        versionsToDelete.map(version => 
          deleteDoc(doc(db, 'script_versions', version.id))
        )
      );

    } catch (error) {
      console.error('Erro na limpeza de vers√µes antigas:', error);
    }
  }

  private static async updateProjectCurrentVersion(
    projectId: string, 
    versionId: string
  ): Promise<void> {
    try {
      const projectRef = doc(db, 'scripts', projectId);
      await updateDoc(projectRef, { 
        currentVersionId: versionId,
        updatedAt: Timestamp.now()
      });
    } catch (error) {
      console.error('Erro ao atualizar vers√£o atual do projeto:', error);
    }
  }

  // **AUTO-SAVE**

  static async enableAutoSave(
    projectId: string,
    userId: string,
    getContent: () => string
  ): Promise<() => void> {
    let lastContent = getContent();
    let lastSave = Date.now();

    const interval = setInterval(async () => {
      try {
        const currentContent = getContent();
        const now = Date.now();

        // Verificar se houve mudan√ßas e se passou tempo suficiente
        if (currentContent !== lastContent && 
            now - lastSave >= this.AUTO_SAVE_INTERVAL) {

          await this.createVersion(
            projectId,
            userId,
            currentContent,
            'Auto-save',
            true
          );

          lastContent = currentContent;
          lastSave = now;
        }
      } catch (error) {
        console.error('Erro no auto-save:', error);
      }
    }, this.AUTO_SAVE_INTERVAL);

    // Retornar fun√ß√£o para parar o auto-save
    return () => clearInterval(interval);
  }

  // **MELHORIAS E SUGEST√ïES**

  static async applyAIImprovement(
    versionId: string,
    improvement: AIImprovement,
    userId: string
  ): Promise<ScriptVersion> {
    try {
      const version = await this.getVersion(versionId);
      if (!version) {
        throw new Error('Vers√£o n√£o encontrada');
      }

      // Aplicar melhoria no conte√∫do
      const newContent = version.content.substring(0, improvement.startIndex) +
                        improvement.improvedSegment +
                        version.content.substring(improvement.endIndex);

      // Criar nova vers√£o com a melhoria aplicada
      const newVersion = await this.createVersion(
        version.projectId,
        userId,
        newContent,
        `Aplicada melhoria de IA: ${improvement.type}`,
        false,
        [improvement.id]
      );

      // Marcar melhoria como aceita
      const improvementRef = doc(db, 'ai_improvements', improvement.id);
      await updateDoc(improvementRef, { 
        accepted: true,
        acceptedAt: Timestamp.now(),
        versionId: newVersion.id
      });

      return newVersion;
    } catch (error) {
      console.error('Erro ao aplicar melhoria de IA:', error);
      throw error;
    }
  }

  // **EXPORTA√á√ÉO E BACKUP**

  static async exportVersionHistory(projectId: string): Promise<{
    project: any;
    versions: ScriptVersion[];
    comparisons: ComparisonData[];
  }> {
    try {
      const [versions, comparisons] = await Promise.all([
        this.getProjectVersions(projectId, 100),
        this.getVersionComparisons(projectId)
      ]);

      // Obter dados do projeto
      const projectDoc = await getDoc(doc(db, 'scripts', projectId));
      const project = projectDoc.exists() ? projectDoc.data() : null;

      return {
        project,
        versions,
        comparisons
      };
    } catch (error) {
      console.error('Erro ao exportar hist√≥rico:', error);
      throw error;
    }
  }

  static async createBackup(projectId: string): Promise<string> {
    try {
      const history = await this.exportVersionHistory(projectId);

      const backup = {
        timestamp: new Date().toISOString(),
        projectId,
        data: history
      };

      const backupRef = doc(collection(db, 'version_backups'));
      await setDoc(backupRef, backup);

      return backupRef.id;
    } catch (error) {
      console.error('Erro ao criar backup:', error);
      throw error;
    }
  }
}

import { 
  doc, 
  setDoc, 
  getDoc, 
  collection, 
  query, 
  where, 
  getDocs, 
  orderBy, 
  limit,
  updateDoc,
  Timestamp 
} from 'firebase/firestore';
import { db } from '../firebaseConfig';
import type { 
  VoiceSynthesis,
  VoiceProfile
} from '../types';

export class VoiceSynthesisService {
  private static synthesis: SpeechSynthesis | null = null;
  private static availableVoices: VoiceProfile[] = [];
  private static currentUtterance: SpeechSynthesisUtterance | null = null;

  // **INICIALIZA√á√ÉO DO SERVI√áO**

  static async initialize(): Promise<void> {
    if (!this.isSupported()) {
      console.warn('Speech Synthesis n√£o √© suportada neste navegador');
      return;
    }

    this.synthesis = window.speechSynthesis;
    await this.loadAvailableVoices();

    // Event listeners para mudan√ßas de vozes
    this.synthesis.onvoiceschanged = () => {
      this.loadAvailableVoices();
    };
  }

  static isSupported(): boolean {
    return 'speechSynthesis' in window && 'SpeechSynthesisUtterance' in window;
  }

  // **GEST√ÉO DE VOZES**

  private static async loadAvailableVoices(): Promise<void> {
    if (!this.synthesis) return;

    const voices = this.synthesis.getVoices();
    this.availableVoices = voices.map(voice => ({
      id: `browser_${voice.name.replace(/\s+/g, '_').toLowerCase()}`,
      name: voice.name,
      displayName: voice.name,
      language: voice.lang,
      gender: this.detectGender(voice.name),
      accent: this.detectAccent(voice.lang),
      description: `${voice.name} - ${voice.lang}`,
      isAvailable: true,
      isPremium: false,
      provider: 'browser'
    }));

    // Adicionar vozes premium se dispon√≠veis
    await this.loadPremiumVoices();
  }

  private static async loadPremiumVoices(): Promise<void> {
    // Aqui seria a integra√ß√£o com servi√ßos premium como ElevenLabs, Azure, etc.
    const premiumVoices: VoiceProfile[] = [
      {
        id: 'elevenlabs_rachel',
        name: 'Rachel',
        displayName: 'Rachel (Premium)',
        language: 'en-US',
        gender: 'female',
        accent: 'American',
        description: 'Voz feminina profissional com qualidade superior',
        sampleUrl: '/samples/rachel.mp3',
        isAvailable: true,
        isPremium: true,
        provider: 'elevenlabs'
      },
      {
        id: 'azure_pt_br_female',
        name: 'Francisca',
        displayName: 'Francisca (Premium)',
        language: 'pt-BR',
        gender: 'female',
        accent: 'Brazilian',
        description: 'Voz feminina brasileira natural e expressiva',
        sampleUrl: '/samples/francisca.mp3',
        isAvailable: true,
        isPremium: true,
        provider: 'azure'
      }
    ];

    this.availableVoices.push(...premiumVoices);
  }

  static getAvailableVoices(language?: string): VoiceProfile[] {
    let voices = this.availableVoices;

    if (language) {
      voices = voices.filter(voice => 
        voice.language.startsWith(language) || 
        voice.language.includes(language)
      );
    }

    return voices.sort((a, b) => {
      // Priorizar vozes do idioma portugu√™s
      const aIsPt = a.language.startsWith('pt');
      const bIsPt = b.language.startsWith('pt');

      if (aIsPt && !bIsPt) return -1;
      if (!aIsPt && bIsPt) return 1;

      // Priorizar vozes premium
      if (a.isPremium && !b.isPremium) return -1;
      if (!a.isPremium && b.isPremium) return 1;

      return a.displayName.localeCompare(b.displayName);
    });
  }

  static getVoiceById(voiceId: string): VoiceProfile | null {
    return this.availableVoices.find(voice => voice.id === voiceId) || null;
  }

  // **S√çNTESE DE VOZ**

  static async synthesizeText(
    projectId: string,
    userId: string,
    text: string,
    voiceId: string,
    settings?: Partial<VoiceSynthesis['settings']>
  ): Promise<VoiceSynthesis> {
    try {
      const voice = this.getVoiceById(voiceId);
      if (!voice) {
        throw new Error('Voz n√£o encontrada');
      }

      // Criar objeto de s√≠ntese
      const synthesis: VoiceSynthesis = {
        id: `voice_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        projectId,
        userId,
        text: this.preprocessText(text),
        voice: {
          name: voice.name,
          lang: voice.language,
          gender: voice.gender,
          accent: voice.accent
        },
        settings: {
          rate: 1.0,
          pitch: 1.0,
          volume: 1.0,
          emphasis: 'moderate',
          pause: {
            sentence: 500,
            paragraph: 1000
          },
          ...settings
        },
        status: 'pending',
        createdAt: Timestamp.now()
      };

      // Salvar no Firebase
      await this.saveSynthesis(synthesis);

      // Executar s√≠ntese
      if (voice.provider === 'browser') {
        return await this.synthesizeWithBrowser(synthesis);
      } else {
        return await this.synthesizeWithPremiumProvider(synthesis);
      }

    } catch (error) {
      console.error('Erro na s√≠ntese de voz:', error);
      throw new Error(`Falha na s√≠ntese: ${error instanceof Error ? error.message : 'Erro desconhecido'}`);
    }
  }

  private static async synthesizeWithBrowser(synthesis: VoiceSynthesis): Promise<VoiceSynthesis> {
    return new Promise(async (resolve, reject) => {
      if (!this.synthesis) {
        reject(new Error('Synthesis n√£o inicializada'));
        return;
      }

      // Atualizar status
      synthesis.status = 'processing';
      await this.updateSynthesis(synthesis.id, { status: 'processing' });

      // Criar utterance
      const utterance = new SpeechSynthesisUtterance(synthesis.text);

      // Encontrar voz do navegador
      const browserVoices = this.synthesis.getVoices();
      const voice = browserVoices.find(v => v.name === synthesis.voice.name);

      if (voice) {
        utterance.voice = voice;
      }

      // Configurar par√¢metros
      utterance.rate = synthesis.settings.rate;
      utterance.pitch = synthesis.settings.pitch;
      utterance.volume = synthesis.settings.volume;
      utterance.lang = synthesis.voice.lang;

      // Calcular dura√ß√£o estimada
      const estimatedDuration = this.estimateDuration(synthesis.text, synthesis.settings.rate);
      synthesis.duration = estimatedDuration;

      // Event listeners
      utterance.onstart = async () => {
        console.log('S√≠ntese iniciada');
      };

      utterance.onend = async () => {
        synthesis.status = 'completed';
        synthesis.processedAt = Timestamp.now();

        await this.updateSynthesis(synthesis.id, {
          status: 'completed',
          processedAt: synthesis.processedAt,
          duration: synthesis.duration
        });

        resolve(synthesis);
      };

      utterance.onerror = async (event) => {
        synthesis.status = 'failed';

        await this.updateSynthesis(synthesis.id, {
          status: 'failed',
          processedAt: Timestamp.now()
        });

        reject(new Error(`Erro na s√≠ntese: ${event.error}`));
      };

      // Iniciar s√≠ntese
      this.currentUtterance = utterance;
      this.synthesis.speak(utterance);
    });
  }

  private static async synthesizeWithPremiumProvider(synthesis: VoiceSynthesis): Promise<VoiceSynthesis> {
    // Implementa√ß√£o para provedores premium (ElevenLabs, Azure, etc.)
    // Por enquanto, simular com delay

    synthesis.status = 'processing';
    await this.updateSynthesis(synthesis.id, { status: 'processing' });

    // Simular processamento
    await new Promise(resolve => setTimeout(resolve, 2000));

    // Simular URL de √°udio gerada
    synthesis.audioUrl = `/generated/audio/${synthesis.id}.mp3`;
    synthesis.duration = this.estimateDuration(synthesis.text, synthesis.settings.rate);
    synthesis.status = 'completed';
    synthesis.processedAt = Timestamp.now();

    await this.updateSynthesis(synthesis.id, {
      status: 'completed',
      audioUrl: synthesis.audioUrl,
      duration: synthesis.duration,
      processedAt: synthesis.processedAt
    });

    return synthesis;
  }

  // **CONTROLE DE REPRODU√á√ÉO**

  static pause(): void {
    if (this.synthesis) {
      this.synthesis.pause();
    }
  }

  static resume(): void {
    if (this.synthesis) {
      this.synthesis.resume();
    }
  }

  static stop(): void {
    if (this.synthesis) {
      this.synthesis.cancel();
      this.currentUtterance = null;
    }
  }

  static isSpeaking(): boolean {
    return this.synthesis ? this.synthesis.speaking : false;
  }

  static isPaused(): boolean {
    return this.synthesis ? this.synthesis.paused : false;
  }

  // **PREVIEW E TESTE**

  static async previewVoice(voiceId: string, sampleText?: string): Promise<void> {
    const voice = this.getVoiceById(voiceId);
    if (!voice) {
      throw new Error('Voz n√£o encontrada');
    }

    const text = sampleText || this.getSampleText(voice.language);

    if (voice.provider === 'browser') {
      return this.previewBrowserVoice(voice, text);
    } else if (voice.sampleUrl) {
      return this.playAudioSample(voice.sampleUrl);
    }
  }

  private static async previewBrowserVoice(voice: VoiceProfile, text: string): Promise<void> {
    if (!this.synthesis) {
      throw new Error('Synthesis n√£o inicializada');
    }

    const utterance = new SpeechSynthesisUtterance(text);
    const browserVoices = this.synthesis.getVoices();
    const browserVoice = browserVoices.find(v => v.name === voice.name);

    if (browserVoice) {
      utterance.voice = browserVoice;
    }

    utterance.rate = 1.0;
    utterance.pitch = 1.0;
    utterance.volume = 1.0;

    this.synthesis.speak(utterance);
  }

  private static async playAudioSample(sampleUrl: string): Promise<void> {
    const audio = new Audio(sampleUrl);
    await audio.play();
  }

  // **HIST√ìRICO E ANALYTICS**

  static async getUserSyntheses(userId: string, limit = 20): Promise<VoiceSynthesis[]> {
    try {
      const synthesesQuery = query(
        collection(db, 'voice_syntheses'),
        where('userId', '==', userId),
        orderBy('createdAt', 'desc'),
        limit(limit)
      );

      const snapshot = await getDocs(synthesesQuery);
      return snapshot.docs.map(doc => doc.data() as VoiceSynthesis);
    } catch (error) {
      console.error('Erro ao obter s√≠nteses do usu√°rio:', error);
      return [];
    }
  }

  static async getProjectSyntheses(projectId: string): Promise<VoiceSynthesis[]> {
    try {
      const synthesesQuery = query(
        collection(db, 'voice_syntheses'),
        where('projectId', '==', projectId),
        orderBy('createdAt', 'desc')
      );

      const snapshot = await getDocs(synthesesQuery);
      return snapshot.docs.map(doc => doc.data() as VoiceSynthesis);
    } catch (error) {
      console.error('Erro ao obter s√≠nteses do projeto:', error);
      return [];
    }
  }

  // **UTILIT√ÅRIOS E HELPERS**

  private static preprocessText(text: string): string {
    // Limpar e otimizar texto para s√≠ntese
    return text
      .replace(/\n+/g, '. ') // Quebras de linha viram pausas
      .replace(/\s+/g, ' ')  // M√∫ltiplos espa√ßos viram um
      .replace(/([.!?])\s*([.!?])/g, '$1 ') // M√∫ltipla pontua√ß√£o
      .trim();
  }

  private static estimateDuration(text: string, rate: number): number {
    // Estimar dura√ß√£o baseada na velocidade m√©dia de fala
    const wordsPerMinute = 150 * rate; // 150 WPM √© a m√©dia
    const words = text.split(/\s+/).length;
    return Math.ceil((words / wordsPerMinute) * 60); // segundos
  }

  private static detectGender(voiceName: string): 'male' | 'female' | 'neutral' {
    const femaleNames = ['female', 'woman', 'lady', 'maria', 'ana', 'lucia', 'rachel', 'sarah', 'emma'];
    const maleNames = ['male', 'man', 'john', 'michael', 'david', 'daniel', 'carlos', 'jo√£o'];

    const name = voiceName.toLowerCase();

    if (femaleNames.some(fn => name.includes(fn))) return 'female';
    if (maleNames.some(mn => name.includes(mn))) return 'male';

    return 'neutral';
  }

  private static detectAccent(language: string): string {
    const accents: Record<string, string> = {
      'en-US': 'American',
      'en-GB': 'British',
      'en-AU': 'Australian',
      'pt-BR': 'Brazilian',
      'pt-PT': 'Portuguese',
      'es-ES': 'Spanish',
      'es-MX': 'Mexican',
      'fr-FR': 'French',
      'de-DE': 'German',
      'it-IT': 'Italian'
    };

    return accents[language] || language.split('-')[1] || 'Unknown';
  }

  private static getSampleText(language: string): string {
    const samples: Record<string, string> = {
      'pt-BR': 'Ol√°! Esta √© uma amostra da voz em portugu√™s brasileiro. Como voc√™ est√° hoje?',
      'pt-PT': 'Ol√°! Esta √© uma amostra da voz em portugu√™s europeu. Como est√°?',
      'en-US': 'Hello! This is a sample of the American English voice. How are you today?',
      'en-GB': 'Hello! This is a sample of the British English voice. How are you today?',
      'es-ES': '¬°Hola! Esta es una muestra de la voz en espa√±ol. ¬øC√≥mo est√°s hoy?',
      'fr-FR': 'Bonjour! Ceci est un √©chantillon de la voix fran√ßaise. Comment allez-vous?',
      'de-DE': 'Hallo! Dies ist eine Probe der deutschen Stimme. Wie geht es Ihnen?',
      'it-IT': 'Ciao! Questo √® un campione della voce italiana. Come stai oggi?'
    };

    // Tentar idioma espec√≠fico primeiro, depois gen√©rico
    return samples[language] || 
           samples[language.split('-')[0]] || 
           samples['en-US'];
  }

  // **PERSIST√äNCIA FIREBASE**

  private static async saveSynthesis(synthesis: VoiceSynthesis): Promise<void> {
    try {
      const synthesisRef = doc(db, 'voice_syntheses', synthesis.id);
      await setDoc(synthesisRef, synthesis);
    } catch (error) {
      console.error('Erro ao salvar s√≠ntese:', error);
      throw error;
    }
  }

  private static async updateSynthesis(synthesisId: string, updates: Partial<VoiceSynthesis>): Promise<void> {
    try {
      const synthesisRef = doc(db, 'voice_syntheses', synthesisId);
      await updateDoc(synthesisRef, updates);
    } catch (error) {
      console.error('Erro ao atualizar s√≠ntese:', error);
    }
  }

  static async getSynthesis(synthesisId: string): Promise<VoiceSynthesis | null> {
    try {
      const synthesisDoc = await getDoc(doc(db, 'voice_syntheses', synthesisId));
      return synthesisDoc.exists() ? synthesisDoc.data() as VoiceSynthesis : null;
    } catch (error) {
      console.error('Erro ao obter s√≠ntese:', error);
      return null;
    }
  }

  // **CONFIGURA√á√ïES E PREFER√äNCIAS**

  static async saveUserVoicePreferences(userId: string, preferences: {
    favoriteVoices: string[];
    defaultSettings: VoiceSynthesis['settings'];
    language: string;
  }): Promise<void> {
    try {
      const preferencesRef = doc(db, 'voice_preferences', userId);
      await setDoc(preferencesRef, {
        ...preferences,
        updatedAt: Timestamp.now()
      });
    } catch (error) {
      console.error('Erro ao salvar prefer√™ncias de voz:', error);
    }
  }

  static async getUserVoicePreferences(userId: string): Promise<any> {
    try {
      const preferencesDoc = await getDoc(doc(db, 'voice_preferences', userId));
      return preferencesDoc.exists() ? preferencesDoc.data() : null;
    } catch (error) {
      console.error('Erro ao obter prefer√™ncias de voz:', error);
      return null;
    }
  }

  // **ANALYTICS**

  static async trackVoiceUsage(userId: string, voiceId: string, duration: number): Promise<void> {
    try {
      const analyticsRef = doc(collection(db, 'voice_analytics'));
      await setDoc(analyticsRef, {
        userId,
        voiceId,
        duration,
        timestamp: Timestamp.now()
      });
    } catch (error) {
      console.error('Erro ao rastrear uso de voz:', error);
    }
  }

  // **QUOTA E LIMITES**

  static async checkUserQuota(userId: string): Promise<{
    used: number;
    limit: number;
    remaining: number;
    resetDate: Date;
  }> {
    try {
      // Obter uso do m√™s atual
      const now = new Date();
      const startOfMonth = new Date(now.getFullYear(), now.getMonth(), 1);

      const usageQuery = query(
        collection(db, 'voice_syntheses'),
        where('userId', '==', userId),
        where('createdAt', '>=', Timestamp.fromDate(startOfMonth))
      );

      const usageSnapshot = await getDocs(usageQuery);
      const used = usageSnapshot.size;

      // Limites por plano (seria din√¢mico baseado no plano do usu√°rio)
      const limit = 100; // 100 s√≠nteses por m√™s no plano b√°sico

      const resetDate = new Date(now.getFullYear(), now.getMonth() + 1, 1);

      return {
        used,
        limit,
        remaining: Math.max(0, limit - used),
        resetDate
      };
    } catch (error) {
      console.error('Erro ao verificar quota:', error);
      return { used: 0, limit: 100, remaining: 100, resetDate: new Date() };
    }
  }
}

import React from 'react';
import { render, screen, fireEvent, waitFor } from '@testing-library/react';
import { TestErrorBoundary, ErrorThrowingComponent, renderWithErrorBoundary } from './test-error-boundary';
import { 
  PerformanceMonitor, 
  MemoryLeakDetector, 
  loadTest, 
  generateTestData,
  getBrowserCapabilities 
} from './performance-test-helpers';

// Mock component for testing
const TestComponent = ({ shouldThrow = false, dataSize = 10 }: { shouldThrow?: boolean; dataSize?: number }) => {
  const data = generateTestData.largeDataset(dataSize);

  if (shouldThrow) {
    throw new Error('Intentional test error');
  }

  return (
    <div data-testid="test-component">
      <h1>Test Component</h1>
      <p>Data items: {data.length}</p>
      {data.slice(0, 5).map(item => (
        <div key={item.id} data-testid={`item-${item.id}`}>
          {item.name}
        </div>
      ))}
    </div>
  );
};

describe('üöÄ Advanced QA Test Suite', () => {
  describe('üõ°Ô∏è Error Boundary Testing', () => {
    let errorSpy: jest.SpyInstance;

    beforeEach(() => {
      errorSpy = jest.spyOn(console, 'error').mockImplementation();
    });

    afterEach(() => {
      errorSpy.mockRestore();
    });

    test('should catch and display component errors', () => {
      render(
        renderWithErrorBoundary(<ErrorThrowingComponent shouldThrow={true} />)
      );

      expect(screen.getByTestId('error-boundary')).toBeInTheDocument();
      expect(screen.getByText(/Component Error Caught in Tests/i)).toBeInTheDocument();
    });

    test('should allow error boundary reset', async () => {
      const { rerender } = render(
        renderWithErrorBoundary(
          <ErrorThrowingComponent shouldThrow={true} />,
          { resetKeys: [1] }
        )
      );

      expect(screen.getByTestId('error-boundary')).toBeInTheDocument();

      // Reset by changing reset keys
      rerender(
        renderWithErrorBoundary(
          <ErrorThrowingComponent shouldThrow={false} />,
          { resetKeys: [2] }
        )
      );

      await waitFor(() => {
        expect(screen.getByTestId('no-error')).toBeInTheDocument();
      });
    });

    test('should call custom error handler', () => {
      const onError = jest.fn();

      render(
        renderWithErrorBoundary(
          <ErrorThrowingComponent shouldThrow={true} />,
          { onError }
        )
      );

      expect(onError).toHaveBeenCalledWith(
        expect.any(Error),
        expect.objectContaining({
          componentStack: expect.any(String)
        })
      );
    });
  });

  describe('‚ö° Performance Testing', () => {
    test('should measure component render performance', async () => {
      const monitor = new PerformanceMonitor();
      monitor.start();

      render(<TestComponent dataSize={100} />);

      const metrics = monitor.getMetrics();

      expect(metrics.renderTime).toBeGreaterThan(0);
      expect(metrics.componentCount).toBeGreaterThan(0);
      expect(metrics.memoryUsed).toBeGreaterThanOrEqual(0);
    });

    test('should detect performance degradation with large datasets', async () => {
      const smallDataMetrics = await global.testUtils.measureRenderTime(async () => {
        render(<TestComponent dataSize={10} />);
      });

      const largeDataMetrics = await global.testUtils.measureRenderTime(async () => {
        render(<TestComponent dataSize={1000} />);
      });

      // Large dataset should take more time (but not too much more)
      expect(largeDataMetrics).toBeGreaterThan(smallDataMetrics);
      expect(largeDataMetrics).toBeLessThan(smallDataMetrics * 10); // Performance should be reasonable
    });

    test('should pass load testing with multiple renders', async () => {
      const results = await loadTest(async () => {
        const { unmount } = render(<TestComponent dataSize={50} />);
        unmount();
      }, 20);

      expect(results).toHaveLength(20);
      expect(results.every(r => r.renderTime < 1000)).toBe(true); // All renders under 1 second
    });
  });

  describe('üß† Memory Leak Detection', () => {
    test('should detect potential memory leaks', async () => {
      const detector = new MemoryLeakDetector(1); // 1MB threshold
      detector.startMonitoring();

      // Simulate multiple component mounts/unmounts
      for (let i = 0; i < 10; i++) {
        const { unmount } = render(<TestComponent dataSize={100} />);
        unmount();
      }

      const result = await detector.detectLeaks();

      expect(result).toEqual({
        initialMemory: expect.any(Number),
        finalMemory: expect.any(Number),
        leaked: expect.any(Boolean),
        leakSize: expect.any(Number)
      });
    });
  });

  describe('üåê Browser Compatibility', () => {
    test('should check browser capabilities', () => {
      const capabilities = getBrowserCapabilities();

      expect(capabilities).toEqual({
        supportsIntersectionObserver: expect.any(Boolean),
        supportsResizeObserver: expect.any(Boolean),
        supportsWebGL: expect.any(Boolean),
        supportsLocalStorage: expect.any(Boolean),
        supportsServiceWorker: expect.any(Boolean),
        userAgent: expect.any(String)
      });
    });

    test('should handle localStorage quota exceeded gracefully', () => {
      const mockStorage = global.testUtils.mockLocalStorageWithQuota(2);

      // Should work for items within quota
      expect(() => mockStorage.setItem('key1', 'value1')).not.toThrow();
      expect(() => mockStorage.setItem('key2', 'value2')).not.toThrow();

      // Should throw for items exceeding quota
      expect(() => mockStorage.setItem('key3', 'value3')).toThrow('QuotaExceededError');
    });
  });

  describe('üìä Data Generation & Testing', () => {
    test('should generate consistent test data', () => {
      const user1 = generateTestData.user();
      const user2 = generateTestData.user({ name: 'Custom Name' });
      const dataset = generateTestData.largeDataset(50);

      expect(user1).toEqual({
        id: expect.any(String),
        name: 'Test User',
        email: 'test@example.com',
        role: 'user'
      });

      expect(user2.name).toBe('Custom Name');
      expect(dataset).toHaveLength(50);
      expect(dataset[0]).toEqual({
        id: 0,
        name: 'Item 0',
        value: expect.any(Number),
        timestamp: expect.any(String)
      });
    });
  });

  describe('üîÑ Stress Testing', () => {
    test('should handle rapid re-renders without breaking', async () => {
      const { rerender } = render(<TestComponent dataSize={10} />);

      // Rapid re-renders with different props
      for (let i = 0; i < 20; i++) {
        rerender(<TestComponent dataSize={i + 1} />);
        await global.testUtils.waitForLoadingToFinish();
      }

      expect(screen.getByTestId('test-component')).toBeInTheDocument();
    });

    test('should handle event spam without performance issues', async () => {
      const handleClick = jest.fn();
      render(
        <button onClick={handleClick} data-testid="spam-button">
          Click me
        </button>
      );

      const button = screen.getByTestId('spam-button');
      const start = performance.now();

      // Spam clicks
      for (let i = 0; i < 100; i++) {
        fireEvent.click(button);
      }

      const end = performance.now();

      expect(handleClick).toHaveBeenCalledTimes(100);
      expect(end - start).toBeLessThan(1000); // Should complete quickly
    });
  });

  describe('üß™ Edge Cases & Boundaries', () => {
    test('should handle empty data gracefully', () => {
      render(<TestComponent dataSize={0} />);

      expect(screen.getByTestId('test-component')).toBeInTheDocument();
      expect(screen.getByText('Data items: 0')).toBeInTheDocument();
    });

    test('should handle extremely large datasets', () => {
      expect(() => {
        render(<TestComponent dataSize={10000} />);
      }).not.toThrow();
    });

    test('should cleanup properly on unmount', () => {
      const cleanupSpy = jest.fn();
      global.testCleanup = [cleanupSpy];

      const { unmount } = render(<TestComponent />);
      unmount();

      // Cleanup should be called automatically by our enhanced setup
      // (This would be called in the next test's beforeEach)
    });
  });
});

// CI/CD Automation Scripts for Production Deployment
export interface TestResults {
  passed: number;
  failed: number;
  coverage: number;
  performance: PerformanceReport;
  errors: TestError[];
}

export interface PerformanceReport {
  averageRenderTime: number;
  memoryUsage: number;
  bundleSize: number;
  lighthouseScore: number;
}

export interface TestError {
  test: string;
  error: string;
  stack?: string;
}

export interface DeploymentConfig {
  environment: 'development' | 'staging' | 'production';
  enableAnalytics: boolean;
  enablePerformanceMonitoring: boolean;
  enableErrorReporting: boolean;
}

// Pre-deployment validation
export class PreDeploymentValidator {
  private minCoverage: number = 80;
  private maxBundleSize: number = 500; // KB
  private minLighthouseScore: number = 90;

  async validateForDeployment(): Promise<{ 
    passed: boolean; 
    issues: string[]; 
    report: TestResults 
  }> {
    const issues: string[] = [];

    // Run comprehensive test suite
    const testResults = await this.runTestSuite();

    // Check test coverage
    if (testResults.coverage < this.minCoverage) {
      issues.push(`Test coverage ${testResults.coverage}% below minimum ${this.minCoverage}%`);
    }

    // Check bundle size
    if (testResults.performance.bundleSize > this.maxBundleSize) {
      issues.push(`Bundle size ${testResults.performance.bundleSize}KB exceeds limit ${this.maxBundleSize}KB`);
    }

    // Check Lighthouse score
    if (testResults.performance.lighthouseScore < this.minLighthouseScore) {
      issues.push(`Lighthouse score ${testResults.performance.lighthouseScore} below minimum ${this.minLighthouseScore}`);
    }

    // Check for failed tests
    if (testResults.failed > 0) {
      issues.push(`${testResults.failed} tests failed`);
    }

    return {
      passed: issues.length === 0,
      issues,
      report: testResults
    };
  }

  private async runTestSuite(): Promise<TestResults> {
    // This would integrate with actual test runners
    // For now, returning mock data structure
    return {
      passed: 32,
      failed: 0,
      coverage: 85,
      performance: {
        averageRenderTime: 150,
        memoryUsage: 25,
        bundleSize: 420,
        lighthouseScore: 95
      },
      errors: []
    };
  }
}

// Deployment automation
export class DeploymentAutomator {
  async deployToEnvironment(config: DeploymentConfig): Promise<{
    success: boolean;
    deploymentUrl?: string;
    error?: string;
  }> {
    try {
      // Pre-deployment validation
      const validator = new PreDeploymentValidator();
      const validation = await validator.validateForDeployment();

      if (!validation.passed) {
        return {
          success: false,
          error: `Deployment blocked: ${validation.issues.join(', ')}`
        };
      }

      // Environment-specific setup
      await this.setupEnvironment(config);

      // Build optimization
      await this.optimizeBuild(config);

      // Deploy
      const deploymentUrl = await this.executeDeploy(config);

      // Post-deployment validation
      await this.validateDeployment(deploymentUrl, config);

      return {
        success: true,
        deploymentUrl
      };

    } catch (error) {
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Unknown deployment error'
      };
    }
  }

  private async setupEnvironment(config: DeploymentConfig): Promise<void> {
    console.log(`üîß Setting up ${config.environment} environment...`);

    // Environment variables setup
    const envVars = this.getEnvironmentVariables(config);

    // Analytics setup
    if (config.enableAnalytics) {
      await this.setupAnalytics(config.environment);
    }

    // Monitoring setup
    if (config.enablePerformanceMonitoring) {
      await this.setupMonitoring(config.environment);
    }
  }

  private getEnvironmentVariables(config: DeploymentConfig): Record<string, string> {
    const baseVars = {
      NODE_ENV: config.environment,
      VITE_APP_ENV: config.environment,
    };

    switch (config.environment) {
      case 'production':
        return {
          ...baseVars,
          VITE_API_URL: 'https://api.roteirar.com',
          VITE_ANALYTICS_ENABLED: config.enableAnalytics.toString(),
          VITE_MONITORING_ENABLED: config.enablePerformanceMonitoring.toString(),
        };
      case 'staging':
